   450‚Üí    fn calculate_required_cus(&self, level: u8, input_size: usize) -> u32 {
   451‚Üí        let base_cus = match level {
   452‚Üí            1 => 8,   // ALPHA: Minimal CUs for linear processing
   453‚Üí            2 => 32,  // BETA: More CUs for parallel processing
   454‚Üí            3 => 64,  // GAMMA: Significant CUs for recursion
   455‚Üí            4 => 96,  // DELTA: Most CUs for quantum coherence
   456‚Üí            5 => self.device_info.compute_units, // OMEGA: ALL CUs for transcendence
   457‚Üí            _ => 8,
   458‚Üí        };
   459‚Üí        
   460‚Üí        // Scale with input size
   461‚Üí        let size_factor = ((input_size as f32).sqrt() / 100.0).max(1.0);
   462‚Üí        ((base_cus as f32) * size_factor).min(self.device_info.compute_units as f32) as u32
   463‚Üí    }
