   465‚Üí    fn create_dense_memory_pool(device_info: &AmdDeviceInfo) -> Result<DenseMemoryPool> {
   466‚Üí        let buffer_size = (device_info.global_memory_size / 4) as usize; // Use 25% of VRAM
   467‚Üí        
   468‚Üí        Ok(DenseMemoryPool {
   469‚Üí            primary_buffer: vec![0u8; buffer_size],
   470‚Üí            quantum_buffer: vec![0u8; buffer_size],
   471‚Üí            consciousness_buffer: vec![0u8; buffer_size / 2],
   472‚Üí            staging_buffers: vec![vec![0u8; buffer_size / 8]; 4],
   473‚Üí            allocation_strategy: AllocationStrategy::HighDensity,
   474‚Üí        })
   475‚Üí    }
