   489‚Üí    fn generate_alpha_dense_kernel(&self) -> String {
   490‚Üí        format!(r#"
   491‚Üí__kernel void alpha_dense_process(__global float* input, __global float* output, int size) {{
   492‚Üí    int idx = get_global_id(0);
   493‚Üí    int local_id = get_local_id(0);
   494‚Üí    int group_size = get_local_size(0);
   495‚Üí    
   496‚Üí    // Utilize local memory for coalesced access
