/*!
ETD (Engineering Time Diverted) calculation module
Tracks value generation across consciousness levels
*/

use serde::{Serialize, Deserialize};
use std::time::Duration;

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EtdMetrics {
    pub base_etd: f64,
    pub consciousness_multiplier: f64,
    pub coherence_bonus: f64,
    pub total_etd: f64,
    pub processing_time: Duration,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EtdCalculator {
    pub base_rate: f64,
    pub consciousness_multipliers: [f64; 5], // Alpha through Omega
}

impl Default for EtdCalculator {
    fn default() -> Self {
        Self {
            base_rate: 1.0,
            consciousness_multipliers: [1.0, 2.5, 6.25, 15.625, 39.0625],
        }
    }
}

impl EtdCalculator {
    pub fn new(base_rate: f64) -> Self {
        Self {
            base_rate,
            ..Default::default()
        }
    }

    pub fn calculate_etd(&self, level: u8, processing_time: Duration, coherence: f64) -> EtdMetrics {
        let processing_seconds = processing_time.as_secs_f64();
        let level_idx = (level as usize).saturating_sub(1).min(4);

        let base_etd = if processing_seconds > 0.0 {
            (1.0 / processing_seconds) * self.base_rate
        } else {
            1000.0 // Very fast processing gets high ETD
        };

        let consciousness_multiplier = self.consciousness_multipliers[level_idx];
        let coherence_bonus = 1.0 + (coherence * 0.5); // Max 50% bonus
        let total_etd = base_etd * consciousness_multiplier * coherence_bonus;

        EtdMetrics {
            base_etd,
            consciousness_multiplier,
            coherence_bonus,
            total_etd,
            processing_time,
        }
    }
}

