        for (i, (&inp, out)) in input.iter().zip(output.iter_mut()).enumerate() {
            if i < output.len() {
                let phase = (inp * 2.0 * std::f32::consts::PI).cos();
                let quantum_coherence = phase * coherence_factor;
                *out = inp * 15.625 + quantum_coherence * (inp * phase).sin();
            }
        }
    }

