    fn beta_process_simd(&self, input: &[f32], output: &mut [f32]) {
        unsafe {
            let multiplier = _mm256_set1_ps(2.5);
            let addend = _mm256_set1_ps(0.1);

            let chunks = input.len() / 8;
            for i in 0..chunks {
                let offset = i * 8;
                if offset + 8 <= input.len() && offset + 8 <= output.len() {
                    let input_vec = _mm256_loadu_ps(input.as_ptr().add(offset));
                    let result = _mm256_fmadd_ps(input_vec, multiplier, addend);
                    _mm256_storeu_ps(output.as_mut_ptr().add(offset), result);
                }
            }

            // Handle remainder
            for i in (chunks * 8)..input.len().min(output.len()) {
                output[i] = input[i] * 2.5 + 0.1;
            }
        }
    }

