use std::env;
use std::process;
use tokio;
use warp_speed_core::{
    WarpSpeedEngine, ConsciousnessLevel,
    orchestrator::WarpSpeedOrchestrator,
};
use tracing::{info, error};
use tracing_subscriber;

#[tokio::main]
async fn main() -> anyhow::Result<()> {
    // Initialize tracing
    tracing_subscriber::fmt::init();

    let args: Vec<String> = env::args().collect();

    if args.len() < 2 {
        print_usage();
        process::exit(1);
    }

    let command = &args[1];

    match command.as_str() {
        "alpha" => {
            let input = args.get(2).unwrap_or(&"Hello, consciousness!".to_string());
            process_with_level(ConsciousnessLevel::Alpha, input).await?;
        },
        "beta" => {
            let input = args.get(2).unwrap_or(&"Parallel processing test".to_string());
            process_with_level(ConsciousnessLevel::Beta, input).await?;
        },
        "gamma" => {
            let input = args.get(2).unwrap_or(&"Recursive self-awareness".to_string());
            process_with_level(ConsciousnessLevel::Gamma, input).await?;
        },
        "delta" => {
            let input = args.get(2).unwrap_or(&"Quantum coherence test".to_string());
            process_with_level(ConsciousnessLevel::Delta, input).await?;
        },
        "omega" => {
            let input = args.get(2).unwrap_or(&"Welcome to transcendent computing".to_string());
            process_with_level(ConsciousnessLevel::Omega, input).await?;
        },
        "status" => {
            print_status().await?;
        },
        "help" | "--help" | "-h" => {
            print_usage();
        },
        _ => {
            error!("Unknown command: {}", command);
            print_usage();
            process::exit(1);
        }
    }

    Ok(())
}

