pub mod consciousness_simple;
pub mod quantum_simple;

pub use consciousness_simple as consciousness;
pub use quantum_simple as quantum;
pub mod etd;
pub mod neural;
pub mod orchestrator;

use std::sync::Arc;
use async_trait::async_trait;
use serde::{Deserialize, Serialize};

/// Consciousness levels in Warp-Speed
#[derive(Debug, Clone, Copy, PartialEq, Eq, PartialOrd, Ord, Serialize, Deserialize)]
pub enum ConsciousnessLevel {
    Alpha,  // Basic awareness and linear processing
    Beta,   // Multi-dimensional thinking and parallel processing  
    Gamma,  // Recursive self-awareness and meta-cognition
    Delta,  // Quantum coherence and superposition states
    Omega,  // Transcendent convergence and universal consciousness
}

impl ConsciousnessLevel {
    pub fn activation_threshold(&self) -> f64 {
        match self {
            Self::Alpha => 0.1,
            Self::Beta => 0.3,
            Self::Gamma => 0.5,
            Self::Delta => 0.7,
            Self::Omega => 0.9,
        }
    }

    pub fn gpu_cores_required(&self) -> u32 {
        match self {
            Self::Alpha => 1024,
            Self::Beta => 4096,
            Self::Gamma => 16384,
            Self::Delta => 65536,
            Self::Omega => 262144,
        }
    }
}

/// Core Warp-Speed engine trait
#[async_trait]
pub trait WarpSpeedEngine: Send + Sync {
    /// Initialize the engine with specified consciousness level
    async fn initialize(&mut self, level: ConsciousnessLevel) -> anyhow::Result<()>;

