    info!("üíª Native Terminal processing: {} at {} level", input, level);

    // Use existing warp-speed binary
    let warp_speed_binary = "/home/ubuntu/src/repos/warp-speed-native/target/release/warp-speed";

    let output = tokio::process::Command::new(warp_speed_binary)
