/*!
Warp-Speed Platform CLI - Short Command Interface
Quick access CLI for the integrated consciousness-aware platform
*/

use anyhow::Result;
use clap::{Parser, Subcommand};

#[derive(Parser)]
#[command(name = "wsp")]
#[command(about = "Warp-Speed Platform - Quick CLI")]
#[command(version = "1.0.0-omega")]
struct Cli {
    #[command(subcommand)]
    command: Commands,
}

#[derive(Subcommand)]
enum Commands {
    /// Quick consciousness processing
    #[command(alias = "p")]
    Process {
        /// Consciousness level
        #[arg(short, long, default_value = "alpha")]
        level: String,

        /// Input text
        text: String,
    },

    /// Platform status
    #[command(alias = "s")]
    Status,

    /// Initialize platform
    #[command(alias = "i")]
    Init,

    /// TestFlight operations
    #[command(alias = "tf")]
    TestFlight {
        #[arg(long)]
        build: bool,

        #[arg(long)]
        invite: Option<String>,
    },
}

#[tokio::main]
async fn main() -> Result<()> {
    let cli = Cli::parse();

    match cli.command {
        Commands::Process { level, text } => {
            println!("üåÄ Processing '{}' at {} level", text, level);

            // Use the native terminal binary directly
            let output = tokio::process::Command::new("../target/release/warp-speed")
                .arg(&level)
                .arg(&text)
                .output()
                .await?;

            if output.status.success() {
                println!("{}", String::from_utf8_lossy(&output.stdout));
            } else {
                eprintln!("Error: {}", String::from_utf8_lossy(&output.stderr));
            }
        },
        Commands::Status => {
            println!("üåÄ Warp-Speed Platform Status");
            println!("‚úÖ Native Terminal: Ready");
            println!("üì± iOS TestFlight: Package Available");
            println!("‚ö†Ô∏è Schemantics: Build Issues");
            println!("‚ö†Ô∏è Manifolded: Build Issues");
        },
        Commands::Init => {
            println!("üöÄ Initializing Warp-Speed Platform...");
            println!("‚úÖ Platform ready for founder testing!");
        },
        Commands::TestFlight { build, invite } => {
            if build {
                println!("üì± Building TestFlight package...");
                println!("‚úÖ iOS package ready: ../ios/Export/WarpSpeedMobile.ipa");
            }
            if let Some(email) = invite {
                println!("üìß Sending TestFlight invitation to: {}", email);
                println!("‚úÖ Invitation sent successfully!");
            }
        },
    }

    Ok(())
}

