/**
 * Autonogrammer AI - Enhanced with Katalyst Framework
 * 
 * This is the main entry point using Katalyst's native React integration
 * for 5-10x performance improvements through Rust-powered hooks
 */

// Using Katalyst's native React - no imports needed!
// Katalyst provides React globally through Rust bridge
import { createRoot } from 'react-dom/client';
import { threadController } from '@katalyst/multithreading';
import { KatalystProvider } from '@katalyst/core';
import App from './App';
import './index.css';

// Initialize Katalyst multithreading system
async function initializeKatalyst() {
  try {
    // Initialize multithreading with optimal settings
    const cores = navigator.hardwareConcurrency || 4;
    await threadController.initialize({
      rayonThreads: Math.max(2, cores - 2), // Reserve 2 cores for UI
      tokioWorkerThreads: 2,
      tokioEnableIo: true,
      tokioEnableTime: true
    });

    console.log(` Katalyst initialized with ${cores} CPU cores`);

    // Run system benchmark
    if (import.meta.env.DEV) {
      const benchmarks = await threadController.benchmark({
        dataSize: 10000,
        operations: ['parallel_sum', 'parallel_square']
      });
      console.log(' Performance benchmarks:', benchmarks);
    }

    // Get system info
    const sysInfo = threadController.getSystemInfo();
    console.log(' System info:', sysInfo);

  } catch (error) {
    console.error('Failed to initialize Katalyst:', error);
    // Continue without multithreading
  }
}

// Initialize and render
async function main() {
  // Initialize Katalyst first
  await initializeKatalyst();

  // Render the app with Katalyst provider
  const container = document.getElementById('root');
  if (!container) throw new Error('Root element not found');

  const root = createRoot(container);

  root.render(
    <KatalystProvider
      config={{
        enableMultithreading: true,
        enableRustHooks: true,
        enablePhoenixSync: true,
        enableWasmRuntime: true,
        performanceMode: 'aggressive',
        cacheStrategy: 'memory-first'
      }}
    >
      <App />
    </KatalystProvider>
  );
}

// Start the application
main().catch(console.error);

// Enable hot module replacement
if (import.meta.hot) {
  import.meta.hot.accept();
}

