/**
 * Autonogrammer AI App - Enhanced with Katalyst
 * Using native React integration for maximum performance
 */

// Katalyst provides React natively - using enhanced hooks
import { useState, useEffect, useCallback, useMemo } from '@katalyst/hooks';
import { useMultithreading } from '@katalyst/multithreading/hooks';
import { usePhoenixChannel } from '@katalyst/phoenix/hooks';
import { CommandProcessor } from './components/CommandProcessor';
import { AIModelManager } from './components/AIModelManager';
import { PerformanceMonitor } from './components/PerformanceMonitor';
import { FieldDynamicsVisualizer } from './components/FieldDynamicsVisualizer';
import './App.css';

interface AppState {
  isConnected: boolean;
  activeModels: string[];
  performanceMetrics: any;
  fieldState: any;
}

function App() {
  // Katalyst-enhanced state management (Rust-backed)
  const [state, setState] = useState<AppState>({
    isConnected: false,
    activeModels: [],
    performanceMetrics: {},
    fieldState: {}
  });

  // Multithreading hook for heavy computations
  const { 
    createThreadPool, 
    processInParallel,
    getSystemInfo,
    benchmark 
  } = useMultithreading();

  // Phoenix real-time connection
  const { 
    channel, 
    isConnected, 
    send, 
    subscribe 
  } = usePhoenixChannel('autonogrammer:main');

  // Initialize thread pool for AI processing
  const aiProcessingPool = useMemo(() => {
    return createThreadPool('ai-processing', {
      threads: 4,
      threadName: 'ai-worker'
    });
  }, [createThreadPool]);

  // Process AI models in parallel
  const processAIModels = useCallback(async (models: any[]) => {
    console.log(' Processing AI models with Katalyst multithreading...');

    const startTime = performance.now();

    // Process models in parallel using Rust threads
    const results = await processInParallel(models, async (model) => {
      // This runs in a separate Rust thread
      return {
        modelId: model.id,
        processed: true,
        timestamp: Date.now()
      };
    });

    const duration = performance.now() - startTime;
    console.log(` Processed ${models.length} models in ${duration}ms`);

    return results;
  }, [processInParallel]);

  // Subscribe to Phoenix events
  useEffect(() => {
    if (!channel) return;

    const unsubscribe = subscribe('field:update', (payload) => {
      setState(prev => ({
        ...prev,
        fieldState: payload
      }));
    });

    return unsubscribe;
  }, [channel, subscribe]);

  // Initialize Katalyst features
  useEffect(() => {
    async function initialize() {
      // Get system information
      const sysInfo = await getSystemInfo();
      console.log('System Info:', sysInfo);

      // Run performance benchmark
      if (import.meta.env.DEV) {
        const benchmarks = await benchmark({
          dataSize: 10000,
          operations: ['parallel_map', 'parallel_reduce']
        });

        setState(prev => ({
          ...prev,
          performanceMetrics: benchmarks
        }));
      }

      // Connect to Phoenix backend
      setState(prev => ({
        ...prev,
        isConnected: true
      }));
    }

    initialize();
  }, [getSystemInfo, benchmark]);

  // Handle command execution with multithreading
  const handleCommand = useCallback(async (command: string, args: any) => {
    console.log(`Executing command: ${command}`);

    switch (command) {
      case '/process-models':
        const results = await processAIModels(args.models);
        return { success: true, results };

      case '/benchmark':
        const benchmarks = await benchmark({
          dataSize: args.dataSize || 100000,
          operations: args.operations || ['all']
        });
        return { success: true, benchmarks };

      case '/field-sync':
        send('field:sync', args);
        return { success: true, message: 'Field sync initiated' };

      default:
        return { success: false, error: 'Unknown command' };
    }
  }, [processAIModels, benchmark, send]);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>Autonogrammer AI</h1>
        <span className="version-badge">Katalyst v2.0</span>
        <div className="status-indicators">
          <span className={`status ${state.isConnected ? 'connected' : 'disconnected'}`}>
            {state.isConnected ? ' Connected' : ' Disconnected'}
          </span>
          <span className="thread-count">
             Threads: {getSystemInfo()?.rayonThreads || 0}
          </span>
        </div>
      </header>

      <main className="app-main">
        <div className="grid-container">
          {/* Command Processor with Katalyst enhancements */}
          <section className="panel command-panel">
            <h2>Command Processor</h2>
            <CommandProcessor 
              onCommand={handleCommand}
              enableMultithreading={true}
              threadPool={aiProcessingPool}
            />
          </section>

          {/* AI Model Manager */}
          <section className="panel models-panel">
            <h2>AI Models</h2>
            <AIModelManager 
              models={state.activeModels}
              onProcessModels={processAIModels}
            />
          </section>

          {/* Field Dynamics Visualizer */}
          <section className="panel field-panel">
            <h2>Field Dynamics</h2>
            <FieldDynamicsVisualizer 
              fieldState={state.fieldState}
              channel={channel}
            />
          </section>

          {/* Performance Monitor */}
          <section className="panel performance-panel">
            <h2>Performance Metrics</h2>
            <PerformanceMonitor 
              metrics={state.performanceMetrics}
              realtime={true}
            />
          </section>
        </div>
      </main>

      <footer className="app-footer">
        <p>Powered by Katalyst Framework - Rust + Phoenix + React</p>
        <p>Performance: {Object.keys(state.performanceMetrics).length} metrics tracked</p>
      </footer>
    </div>
  );
}

export default App;

