/**
 * CommandProcessor - Enhanced with Katalyst Multithreading
 * Processes commands in parallel using Rust threads
 */

import { useState, useCallback, useRef } from '@katalyst/hooks';
import { ThreadPoolController } from '@katalyst/multithreading';
import { useDebounce, useThrottle } from '@katalyst/hooks';

interface CommandProcessorProps {
  onCommand: (command: string, args: any) => Promise<any>;
  enableMultithreading?: boolean;
  threadPool?: ThreadPoolController;
}

interface CommandHistory {
  command: string;
  timestamp: number;
  result: any;
  duration: number;
}

export function CommandProcessor({ 
  onCommand, 
  enableMultithreading = true,
  threadPool 
}: CommandProcessorProps) {
  const [input, setInput] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [history, setHistory] = useState<CommandHistory[]>([]);
  const [suggestions, setSuggestions] = useState<string[]>([]);

  const inputRef = useRef<HTMLInputElement>(null);

  // Debounce input for suggestions
  const debouncedInput = useDebounce(input, 300);

  // Throttle command execution
  const throttledExecute = useThrottle(async (cmd: string) => {
    await executeCommand(cmd);
  }, 1000);

  // Parse command and arguments
  const parseCommand = useCallback((input: string) => {
    const parts = input.trim().split(' ');
    const command = parts[0];
    const argsString = parts.slice(1).join(' ');

    let args = {};
    try {
      // Try to parse as JSON first
      if (argsString.startsWith('{')) {
        args = JSON.parse(argsString);
      } else {
        // Parse key=value pairs
        argsString.split(' ').forEach(pair => {
          const [key, value] = pair.split('=');
          if (key && value) {
            args[key] = isNaN(Number(value)) ? value : Number(value);
          }
        });
      }
    } catch (e) {
      args = { raw: argsString };
    }

    return { command, args };
  }, []);

  // Execute command with optional multithreading
  const executeCommand = useCallback(async (input: string) => {
    if (!input.trim()) return;

    setIsProcessing(true);
    const startTime = performance.now();

    try {
      const { command, args } = parseCommand(input);

      let result;
      if (enableMultithreading && threadPool) {
        // Process in separate thread
        result = await threadPool.execute(async () => {
          return await onCommand(command, args);
        });
      } else {
        // Process in main thread
        result = await onCommand(command, args);
      }

      const duration = performance.now() - startTime;

      // Add to history
      setHistory(prev => [{
        command: input,
        timestamp: Date.now(),
        result,
        duration
      }, ...prev].slice(0, 50)); // Keep last 50 commands

      // Clear input
      setInput('');

      console.log(` Command executed in ${duration.toFixed(2)}ms`, result);

    } catch (error) {
      console.error('Command execution failed:', error);

      setHistory(prev => [{
        command: input,
        timestamp: Date.now(),
        result: { error: error.message },
        duration: performance.now() - startTime
      }, ...prev].slice(0, 50));
    } finally {
      setIsProcessing(false);
    }
  }, [parseCommand, enableMultithreading, threadPool, onCommand]);

  // Generate command suggestions
  useCallback(() => {
    if (!debouncedInput) {
      setSuggestions([]);
      return;
    }

    const availableCommands = [
      '/process-models - Process AI models in parallel',
      '/benchmark - Run performance benchmark',
      '/field-sync - Synchronize field dynamics',
      '/thread-info - Get thread pool information',
      '/clear - Clear command history',
      '/help - Show available commands'
    ];

    const filtered = availableCommands.filter(cmd => 
      cmd.toLowerCase().includes(debouncedInput.toLowerCase())
    );

    setSuggestions(filtered);
  }, [debouncedInput]);

  // Handle special commands
  const handleSpecialCommands = useCallback((command: string) => {
    switch (command) {
      case '/clear':
        setHistory([]);
        setInput('');
        return true;

      case '/help':
        console.log('Available commands:', suggestions);
        return true;

      case '/thread-info':
        if (threadPool) {
          console.log('Thread pool info:', threadPool.getInfo());
        }
        return true;

      default:
        return false;
    }
  }, [suggestions, threadPool]);

  // Handle form submission
  const handleSubmit = useCallback((e: React.FormEvent) => {
    e.preventDefault();

    const { command } = parseCommand(input);

    // Check for special commands first
    if (handleSpecialCommands(command)) {
      return;
    }

    // Execute command with throttling
    throttledExecute(input);
  }, [input, parseCommand, handleSpecialCommands, throttledExecute]);

  // Handle keyboard shortcuts
  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    // Command history navigation
    if (e.key === 'ArrowUp' && history.length > 0) {
      e.preventDefault();
      setInput(history[0].command);
    }

    // Auto-complete with Tab
    if (e.key === 'Tab' && suggestions.length > 0) {
      e.preventDefault();
      const firstSuggestion = suggestions[0].split(' - ')[0];
      setInput(firstSuggestion);
    }
  }, [history, suggestions]);

  return (
    <div className="command-processor">
      <form onSubmit={handleSubmit} className="command-form">
        <div className="input-wrapper">
          <span className="prompt">{'>'}</span>
          <input
            ref={inputRef}
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Enter command (e.g., /benchmark)"
            disabled={isProcessing}
            className="command-input"
            autoComplete="off"
          />
          {isProcessing && (
            <span className="processing-indicator"> Processing...</span>
          )}
        </div>

        {/* Suggestions */}
        {suggestions.length > 0 && (
          <div className="suggestions">
            {suggestions.map((suggestion, idx) => (
              <div 
                key={idx} 
                className="suggestion"
                onClick={() => setInput(suggestion.split(' - ')[0])}
              >
                {suggestion}
              </div>
            ))}
          </div>
        )}
      </form>

      {/* Command History */}
      <div className="command-history">
        <h3>History</h3>
        <div className="history-list">
          {history.map((item, idx) => (
            <div key={idx} className="history-item">
              <div className="history-header">
                <code className="history-command">{item.command}</code>
                <span className="history-duration">
                  {item.duration.toFixed(2)}ms
                </span>
              </div>
              <div className="history-result">
                {item.result.error ? (
                  <span className="error"> {item.result.error}</span>
                ) : (
                  <pre>{JSON.stringify(item.result, null, 2)}</pre>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Thread Status */}
      {enableMultithreading && threadPool && (
        <div className="thread-status">
          <h4>Thread Pool Status</h4>
          <div className="status-grid">
            <span>Active: {threadPool.getActiveCount?.() || 0}</span>
            <span>Queued: {threadPool.getQueuedCount?.() || 0}</span>
            <span>Completed: {threadPool.getCompletedCount?.() || 0}</span>
          </div>
        </div>
      )}
    </div>
  );
}

