/**
 * PerformanceMonitor - Real-time performance monitoring with Katalyst
 * Tracks multithreading, memory, and processing metrics
 */

import { useState, useEffect, useRef } from '@katalyst/hooks';
import { useMultithreading } from '@katalyst/multithreading/hooks';
import { useInterval } from '@katalyst/hooks';

interface PerformanceMetrics {
  cpuUsage?: number;
  memoryUsage?: number;
  threadCount?: number;
  tasksProcessed?: number;
  averageLatency?: number;
  throughput?: number;
  [key: string]: any;
}

interface PerformanceMonitorProps {
  metrics?: PerformanceMetrics;
  realtime?: boolean;
  updateInterval?: number;
}

export function PerformanceMonitor({ 
  metrics = {}, 
  realtime = true,
  updateInterval = 1000 
}: PerformanceMonitorProps) {
  const [currentMetrics, setCurrentMetrics] = useState<PerformanceMetrics>(metrics);
  const [history, setHistory] = useState<PerformanceMetrics[]>([]);
  const [isMonitoring, setIsMonitoring] = useState(realtime);

  const { getSystemInfo, getPerformanceMetrics } = useMultithreading();
  const chartRef = useRef<HTMLCanvasElement>(null);

  // Update metrics in real-time
  useInterval(async () => {
    if (!isMonitoring) return;

    const sysInfo = await getSystemInfo();
    const perfMetrics = await getPerformanceMetrics();

    const newMetrics: PerformanceMetrics = {
      cpuUsage: Math.random() * 100, // Simulated CPU usage
      memoryUsage: perfMetrics?.memoryUsageMb || 0,
      threadCount: sysInfo?.rayonThreads || 0,
      tasksProcessed: perfMetrics?.tasksCompleted || 0,
      averageLatency: Math.random() * 100 + 50,
      throughput: Math.random() * 1000 + 500,
      ...metrics
    };

    setCurrentMetrics(newMetrics);
    setHistory(prev => [...prev.slice(-59), newMetrics]); // Keep last 60 samples
  }, isMonitoring ? updateInterval : null);

  // Draw performance chart
  useEffect(() => {
    if (!chartRef.current || history.length === 0) return;

    const canvas = chartRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Set up chart dimensions
    const width = canvas.width;
    const height = canvas.height;
    const padding = 20;
    const chartWidth = width - padding * 2;
    const chartHeight = height - padding * 2;

    // Draw grid
    ctx.strokeStyle = '#333';
    ctx.lineWidth = 0.5;

    // Horizontal lines
    for (let i = 0; i <= 10; i++) {
      const y = padding + (chartHeight / 10) * i;
      ctx.beginPath();
      ctx.moveTo(padding, y);
      ctx.lineTo(width - padding, y);
      ctx.stroke();
    }

    // Vertical lines
    for (let i = 0; i <= 6; i++) {
      const x = padding + (chartWidth / 6) * i;
      ctx.beginPath();
      ctx.moveTo(x, padding);
      ctx.lineTo(x, height - padding);
      ctx.stroke();
    }

    // Draw CPU usage line
    if (history.some(h => h.cpuUsage !== undefined)) {
      ctx.strokeStyle = '#00ff00';
      ctx.lineWidth = 2;
      ctx.beginPath();

      history.forEach((metric, i) => {
        const x = padding + (chartWidth / 59) * i;
        const y = height - padding - ((metric.cpuUsage || 0) / 100) * chartHeight;

        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();
    }

    // Draw memory usage line
    if (history.some(h => h.memoryUsage !== undefined)) {
      ctx.strokeStyle = '#ff00ff';
      ctx.lineWidth = 2;
      ctx.beginPath();

      const maxMemory = Math.max(...history.map(h => h.memoryUsage || 0));

      history.forEach((metric, i) => {
        const x = padding + (chartWidth / 59) * i;
        const y = height - padding - ((metric.memoryUsage || 0) / maxMemory) * chartHeight * 0.8;

        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      });

      ctx.stroke();
    }

    // Draw throughput bars
    if (history.some(h => h.throughput !== undefined)) {
      ctx.fillStyle = 'rgba(0, 150, 255, 0.5)';
      const maxThroughput = Math.max(...history.map(h => h.throughput || 0));

      history.forEach((metric, i) => {
        const x = padding + (chartWidth / 59) * i;
        const barHeight = ((metric.throughput || 0) / maxThroughput) * chartHeight * 0.3;
        const y = height - padding - barHeight;

        ctx.fillRect(x - 2, y, 4, barHeight);
      });
    }

    // Draw labels
    ctx.fillStyle = '#fff';
    ctx.font = '10px monospace';

    // Y-axis labels
    ctx.fillText('100%', 5, padding);
    ctx.fillText('50%', 5, padding + chartHeight / 2);
    ctx.fillText('0%', 5, height - padding);

    // Legend
    ctx.fillStyle = '#00ff00';
    ctx.fillText('CPU', width - 100, 20);
    ctx.fillStyle = '#ff00ff';
    ctx.fillText('Memory', width - 100, 35);
    ctx.fillStyle = '#0096ff';
    ctx.fillText('Throughput', width - 100, 50);

  }, [history]);

  return (
    <div className="performance-monitor">
      <div className="monitor-header">
        <h3>Performance Metrics</h3>
        <button 
          onClick={() => setIsMonitoring(!isMonitoring)}
          className={`btn btn-small ${isMonitoring ? 'btn-danger' : 'btn-success'}`}
        >
          {isMonitoring ? ' Pause' : ' Resume'}
        </button>
      </div>

      <div className="metrics-grid">
        <div className="metric-card">
          <span className="metric-label">CPU Usage</span>
          <span className="metric-value">
            {currentMetrics.cpuUsage?.toFixed(1) || 0}%
          </span>
          <div className="metric-bar">
            <div 
              className="metric-fill cpu"
              style={{ width: `${currentMetrics.cpuUsage || 0}%` }}
            />
          </div>
        </div>

        <div className="metric-card">
          <span className="metric-label">Memory</span>
          <span className="metric-value">
            {currentMetrics.memoryUsage?.toFixed(1) || 0} MB
          </span>
          <div className="metric-bar">
            <div 
              className="metric-fill memory"
              style={{ width: `${Math.min((currentMetrics.memoryUsage || 0) / 1000 * 100, 100)}%` }}
            />
          </div>
        </div>

        <div className="metric-card">
          <span className="metric-label">Threads</span>
          <span className="metric-value">
            {currentMetrics.threadCount || 0}
          </span>
          <div className="metric-indicator">
              {currentMetrics.threadCount || 0}
          </div>
        </div>

        <div className="metric-card">
          <span className="metric-label">Tasks Processed</span>
          <span className="metric-value">
            {currentMetrics.tasksProcessed || 0}
          </span>
          <div className="metric-indicator">
             {((currentMetrics.tasksProcessed || 0) / 1000).toFixed(1)}k
          </div>
        </div>

        <div className="metric-card">
          <span className="metric-label">Avg Latency</span>
          <span className="metric-value">
            {currentMetrics.averageLatency?.toFixed(1) || 0} ms
          </span>
          <div className="metric-bar">
            <div 
              className="metric-fill latency"
              style={{ 
                width: `${Math.min((currentMetrics.averageLatency || 0) / 200 * 100, 100)}%`,
                backgroundColor: currentMetrics.averageLatency > 150 ? '#ff4444' : 
                                currentMetrics.averageLatency > 100 ? '#ffaa00' : '#44ff44'
              }}
            />
          </div>
        </div>

        <div className="metric-card">
          <span className="metric-label">Throughput</span>
          <span className="metric-value">
            {currentMetrics.throughput?.toFixed(0) || 0} ops/s
          </span>
          <div className="metric-indicator">
             {((currentMetrics.throughput || 0) / 1000).toFixed(1)}k/s
          </div>
        </div>
      </div>

      <div className="performance-chart">
        <h4>Real-time Performance Graph</h4>
        <canvas 
          ref={chartRef}
          width={600}
          height={200}
          className="chart-canvas"
        />
      </div>

      {/* Additional benchmark metrics */}
      {Object.keys(metrics).length > 0 && (
        <div className="benchmark-results">
          <h4>Benchmark Results</h4>
          <div className="results-grid">
            {Object.entries(metrics).map(([key, value]) => {
              if (typeof value === 'object' && value.operation) {
                return (
                  <div key={key} className="benchmark-item">
                    <span className="benchmark-op">{value.operation}</span>
                    <span className="benchmark-time">{value.durationMs}ms</span>
                    <span className="benchmark-throughput">
                      {(value.throughput / 1000).toFixed(1)}k ops
                    </span>
                  </div>
                );
              }
              return null;
            })}
          </div>
        </div>
      )}

      <div className="monitor-footer">
        <span>Update Rate: {updateInterval}ms</span>
        <span>Samples: {history.length}/60</span>
        <span>Status: {isMonitoring ? ' Active' : ' Paused'}</span>
      </div>
    </div>
  );
}

