/**
 * FieldDynamicsVisualizer - Visualizes field dynamics with Phoenix real-time updates
 * Integrates with Katalyst's Brain-Braun-Beyond cognitive architecture
 */

import { useState, useEffect, useCallback, useRef } from '@katalyst/hooks';
import { usePhoenixChannel } from '@katalyst/phoenix/hooks';
import { useMultithreading } from '@katalyst/multithreading/hooks';

interface FieldState {
  resonance: number;
  coherence: number;
  entropy: number;
  nodes: FieldNode[];
  connections: FieldConnection[];
  timestamp: number;
}

interface FieldNode {
  id: string;
  x: number;
  y: number;
  energy: number;
  type: 'source' | 'sink' | 'neutral';
}

interface FieldConnection {
  source: string;
  target: string;
  strength: number;
}

interface FieldDynamicsVisualizerProps {
  fieldState?: Partial<FieldState>;
  channel?: any;
}

export function FieldDynamicsVisualizer({ 
  fieldState = {}, 
  channel 
}: FieldDynamicsVisualizerProps) {
  const [field, setField] = useState<FieldState>({
    resonance: 0,
    coherence: 0,
    entropy: 0,
    nodes: [],
    connections: [],
    timestamp: Date.now(),
    ...fieldState
  });

  const [isSimulating, setIsSimulating] = useState(false);
  const [selectedNode, setSelectedNode] = useState<string | null>(null);

  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationRef = useRef<number>();

  const { createThreadPool } = useMultithreading();

  // Create thread pool for field calculations
  const fieldCalculationPool = useCallback(() => {
    return createThreadPool('field-calc', {
      threads: 2,
      threadName: 'field-worker'
    });
  }, [createThreadPool]);

  // Calculate field dynamics in separate thread
  const calculateFieldDynamics = useCallback(async () => {
    const pool = fieldCalculationPool();

    const result = await pool.execute(async () => {
      // Simulate complex field calculations
      const nodes: FieldNode[] = Array.from({ length: 20 }, (_, i) => ({
        id: `node-${i}`,
        x: Math.random() * 400 + 100,
        y: Math.random() * 300 + 50,
        energy: Math.random(),
        type: Math.random() > 0.7 ? 'source' : 
              Math.random() > 0.5 ? 'sink' : 'neutral'
      }));

      const connections: FieldConnection[] = [];
      for (let i = 0; i < nodes.length; i++) {
        for (let j = i + 1; j < nodes.length; j++) {
          const distance = Math.sqrt(
            Math.pow(nodes[i].x - nodes[j].x, 2) + 
            Math.pow(nodes[i].y - nodes[j].y, 2)
          );

          if (distance < 150 && Math.random() > 0.7) {
            connections.push({
              source: nodes[i].id,
              target: nodes[j].id,
              strength: 1 - distance / 150
            });
          }
        }
      }

      return {
        nodes,
        connections,
        resonance: Math.random(),
        coherence: Math.random(),
        entropy: Math.random()
      };
    });

    pool.destroy();
    return result;
  }, [fieldCalculationPool]);

  // Start field simulation
  const startSimulation = useCallback(async () => {
    setIsSimulating(true);

    const simulate = async () => {
      const dynamics = await calculateFieldDynamics();

      setField(prev => ({
        ...prev,
        ...dynamics,
        timestamp: Date.now()
      }));

      // Send to Phoenix channel if connected
      if (channel) {
        channel.push('field:update', dynamics);
      }
    };

    // Initial calculation
    await simulate();

    // Set up interval for continuous simulation
    const interval = setInterval(simulate, 2000);

    return () => clearInterval(interval);
  }, [calculateFieldDynamics, channel]);

  // Stop simulation
  const stopSimulation = useCallback(() => {
    setIsSimulating(false);
  }, []);

  // Draw field visualization
  useEffect(() => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const draw = () => {
      // Clear canvas
      ctx.fillStyle = '#000';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Draw field gradient background
      const gradient = ctx.createRadialGradient(
        canvas.width / 2, canvas.height / 2, 0,
        canvas.width / 2, canvas.height / 2, canvas.width / 2
      );
      gradient.addColorStop(0, `rgba(0, 100, 200, ${field.coherence * 0.3})`);
      gradient.addColorStop(0.5, `rgba(0, 200, 100, ${field.resonance * 0.2})`);
      gradient.addColorStop(1, `rgba(100, 0, 200, ${field.entropy * 0.1})`);

      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Draw connections
      field.connections.forEach(conn => {
        const source = field.nodes.find(n => n.id === conn.source);
        const target = field.nodes.find(n => n.id === conn.target);

        if (source && target) {
          ctx.strokeStyle = `rgba(100, 200, 255, ${conn.strength * 0.5})`;
          ctx.lineWidth = conn.strength * 3;
          ctx.beginPath();
          ctx.moveTo(source.x, source.y);
          ctx.lineTo(target.x, target.y);
          ctx.stroke();

          // Draw energy flow animation
          const t = (Date.now() % 2000) / 2000;
          const flowX = source.x + (target.x - source.x) * t;
          const flowY = source.y + (target.y - source.y) * t;

          ctx.fillStyle = `rgba(255, 255, 255, ${conn.strength})`;
          ctx.beginPath();
          ctx.arc(flowX, flowY, 3, 0, Math.PI * 2);
          ctx.fill();
        }
      });

      // Draw nodes
      field.nodes.forEach(node => {
        const isSelected = node.id === selectedNode;

        // Node glow effect
        if (node.type === 'source') {
          const glowGradient = ctx.createRadialGradient(
            node.x, node.y, 0,
            node.x, node.y, 30
          );
          glowGradient.addColorStop(0, `rgba(255, 200, 0, ${node.energy})`);
          glowGradient.addColorStop(1, 'rgba(255, 200, 0, 0)');
          ctx.fillStyle = glowGradient;
          ctx.fillRect(node.x - 30, node.y - 30, 60, 60);
        }

        // Draw node
        ctx.fillStyle = node.type === 'source' ? '#ffcc00' :
                        node.type === 'sink' ? '#ff00cc' : '#00ccff';
        ctx.strokeStyle = isSelected ? '#fff' : 'rgba(255, 255, 255, 0.3)';
        ctx.lineWidth = isSelected ? 3 : 1;

        ctx.beginPath();
        ctx.arc(node.x, node.y, 10 + node.energy * 10, 0, Math.PI * 2);
        ctx.fill();
        ctx.stroke();

        // Draw node energy level
        ctx.fillStyle = '#fff';
        ctx.font = '10px monospace';
        ctx.textAlign = 'center';
        ctx.fillText(node.energy.toFixed(2), node.x, node.y - 15);
      });

      // Draw field metrics
      ctx.fillStyle = '#fff';
      ctx.font = '14px monospace';
      ctx.textAlign = 'left';

      ctx.fillText(`Resonance: ${(field.resonance * 100).toFixed(1)}%`, 10, 25);
      ctx.fillText(`Coherence: ${(field.coherence * 100).toFixed(1)}%`, 10, 45);
      ctx.fillText(`Entropy: ${(field.entropy * 100).toFixed(1)}%`, 10, 65);

      // Draw timestamp
      ctx.textAlign = 'right';
      ctx.fillText(new Date(field.timestamp).toLocaleTimeString(), canvas.width - 10, 25);

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [field, selectedNode]);

  // Handle canvas click
  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Find clicked node
    const clickedNode = field.nodes.find(node => {
      const distance = Math.sqrt(
        Math.pow(node.x - x, 2) + 
        Math.pow(node.y - y, 2)
      );
      return distance < 20;
    });

    setSelectedNode(clickedNode?.id || null);

    if (clickedNode && channel) {
      channel.push('field:node_selected', { nodeId: clickedNode.id });
    }
  }, [field.nodes, channel]);

  // Protocol shell execution
  const executeProtocol = useCallback(async (protocol: string) => {
    console.log(`Executing field protocol: ${protocol}`);

    if (channel) {
      channel.push('field:protocol', { 
        protocol,
        field: field 
      });
    }

    // Simulate protocol effects
    switch (protocol) {
      case 'resonance.amplify':
        setField(prev => ({
          ...prev,
          resonance: Math.min(1, prev.resonance * 1.5)
        }));
        break;

      case 'coherence.stabilize':
        setField(prev => ({
          ...prev,
          coherence: 0.8,
          entropy: prev.entropy * 0.5
        }));
        break;

      case 'entropy.reduce':
        setField(prev => ({
          ...prev,
          entropy: Math.max(0, prev.entropy - 0.2)
        }));
        break;
    }
  }, [field, channel]);

  return (
    <div className="field-dynamics-visualizer">
      <div className="visualizer-header">
        <h3>Field Dynamics</h3>
        <div className="controls">
          {!isSimulating ? (
            <button onClick={startSimulation} className="btn btn-success">
               Start Simulation
            </button>
          ) : (
            <button onClick={stopSimulation} className="btn btn-danger">
               Stop Simulation
            </button>
          )}
          <button onClick={calculateFieldDynamics} className="btn btn-secondary">
             Recalculate
          </button>
        </div>
      </div>

      <div className="field-canvas-container">
        <canvas
          ref={canvasRef}
          width={600}
          height={400}
          onClick={handleCanvasClick}
          className="field-canvas"
        />
      </div>

      <div className="field-protocols">
        <h4>Protocol Shells</h4>
        <div className="protocol-buttons">
          <button 
            onClick={() => executeProtocol('resonance.amplify')}
            className="btn btn-protocol"
          >
             Amplify Resonance
          </button>
          <button 
            onClick={() => executeProtocol('coherence.stabilize')}
            className="btn btn-protocol"
          >
             Stabilize Coherence
          </button>
          <button 
            onClick={() => executeProtocol('entropy.reduce')}
            className="btn btn-protocol"
          >
             Reduce Entropy
          </button>
        </div>
      </div>

      {selectedNode && (
        <div className="node-details">
          <h4>Selected Node: {selectedNode}</h4>
          {(() => {
            const node = field.nodes.find(n => n.id === selectedNode);
            if (!node) return null;

            return (
              <div className="details-grid">
                <div>Type: {node.type}</div>
                <div>Energy: {node.energy.toFixed(3)}</div>
                <div>Position: ({node.x.toFixed(0)}, {node.y.toFixed(0)})</div>
                <div>
                  Connections: {
                    field.connections.filter(c => 
                      c.source === selectedNode || c.target === selectedNode
                    ).length
                  }
                </div>
              </div>
            );
          })()}
        </div>
      )}

      <div className="field-status">
        <span>Nodes: {field.nodes.length}</span>
        <span>Connections: {field.connections.length}</span>
        <span>Status: {isSimulating ? ' Simulating' : ' Idle'}</span>
      </div>
    </div>
  );
}

