        ctx.fill();
        ctx.stroke();

        // Draw node energy level
        ctx.fillStyle = '#fff';
        ctx.font = '10px monospace';
        ctx.textAlign = 'center';
        ctx.fillText(node.energy.toFixed(2), node.x, node.y - 15);
      });

      // Draw field metrics
      ctx.fillStyle = '#fff';
      ctx.font = '14px monospace';
      ctx.textAlign = 'left';

      ctx.fillText(`Resonance: ${(field.resonance * 100).toFixed(1)}%`, 10, 25);
      ctx.fillText(`Coherence: ${(field.coherence * 100).toFixed(1)}%`, 10, 45);
      ctx.fillText(`Entropy: ${(field.entropy * 100).toFixed(1)}%`, 10, 65);

      // Draw timestamp
      ctx.textAlign = 'right';
      ctx.fillText(new Date(field.timestamp).toLocaleTimeString(), canvas.width - 10, 25);

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [field, selectedNode]);

  // Handle canvas click
  const handleCanvasClick = useCallback((e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Find clicked node
    const clickedNode = field.nodes.find(node => {
      const distance = Math.sqrt(
        Math.pow(node.x - x, 2) + 
        Math.pow(node.y - y, 2)
      );
      return distance < 20;
    });

    setSelectedNode(clickedNode?.id || null);

    if (clickedNode && channel) {
      channel.push('field:node_selected', { nodeId: clickedNode.id });
    }
  }, [field.nodes, channel]);

  // Protocol shell execution
  const executeProtocol = useCallback(async (protocol: string) => {
    console.log(`Executing field protocol: ${protocol}`);

    if (channel) {
      channel.push('field:protocol', { 
        protocol,
        field: field 
      });
    }

    // Simulate protocol effects
    switch (protocol) {
      case 'resonance.amplify':
        setField(prev => ({
          ...prev,
          resonance: Math.min(1, prev.resonance * 1.5)
        }));
        break;

      case 'coherence.stabilize':
        setField(prev => ({
          ...prev,
          coherence: 0.8,
          entropy: prev.entropy * 0.5
        }));
        break;

      case 'entropy.reduce':
        setField(prev => ({
          ...prev,
          entropy: Math.max(0, prev.entropy - 0.2)
        }));
        break;
    }
  }, [field, channel]);

  return (
    <div className="field-dynamics-visualizer">
      <div className="visualizer-header">
        <h3>Field Dynamics</h3>
        <div className="controls">
          {!isSimulating ? (
            <button onClick={startSimulation} className="btn btn-success">
               Start Simulation
            </button>
          ) : (
            <button onClick={stopSimulation} className="btn btn-danger">
               Stop Simulation
            </button>
          )}
          <button onClick={calculateFieldDynamics} className="btn btn-secondary">
             Recalculate
          </button>
        </div>
      </div>

      <div className="field-canvas-container">
        <canvas
          ref={canvasRef}
          width={600}
          height={400}
          onClick={handleCanvasClick}
          className="field-canvas"
        />
      </div>

      <div className="field-protocols">
        <h4>Protocol Shells</h4>
        <div className="protocol-buttons">
          <button 
            onClick={() => executeProtocol('resonance.amplify')}
            className="btn btn-protocol"
          >
             Amplify Resonance
          </button>
          <button 
            onClick={() => executeProtocol('coherence.stabilize')}
            className="btn btn-protocol"
          >
             Stabilize Coherence
          </button>
          <button 
            onClick={() => executeProtocol('entropy.reduce')}
            className="btn btn-protocol"
          >
             Reduce Entropy
          </button>
        </div>
      </div>

      {selectedNode && (
        <div className="node-details">
          <h4>Selected Node: {selectedNode}</h4>
          {(() => {
            const node = field.nodes.find(n => n.id === selectedNode);
            if (!node) return null;

            return (
              <div className="details-grid">
                <div>Type: {node.type}</div>
                <div>Energy: {node.energy.toFixed(3)}</div>
                <div>Position: ({node.x.toFixed(0)}, {node.y.toFixed(0)})</div>
                <div>
                  Connections: {
                    field.connections.filter(c => 
                      c.source === selectedNode || c.target === selectedNode
                    ).length
                  }
                </div>
              </div>
            );
          })()}
        </div>
      )}

      <div className="field-status">
        <span>Nodes: {field.nodes.length}</span>
        <span>Connections: {field.connections.length}</span>
        <span>Status: {isSimulating ? ' Simulating' : ' Idle'}</span>
      </div>
    </div>
  );
}
