     1‚Üí//! Vimmacs - Main binary entry point
     2‚Üí//! 
     3‚Üí//! Terminal-based AI coding environment with security-first architecture
     4‚Üí//! Integrates NeoVim, Doom Emacs, terminal multiplexing, and Claude Code AI
     5‚Üí
     6‚Üíuse anyhow::Result;
     7‚Üíuse clap::{Parser, Subcommand};
     8‚Üíuse std::path::PathBuf;
     9‚Üíuse tracing::{error, info};
    10‚Üíuse tracing_subscriber::{layer::SubscriberExt, util::SubscriberInitExt};
    11‚Üí
    12‚Üíuse vimmacs_core::{VimmacsCore, config::Config, utils::{SystemUtils, FileSystemUtils}};
    13‚Üí
    14‚Üí/// Vimmacs - AI-powered terminal development environment
    15‚Üí#[derive(Parser)]
    16‚Üí#[command(author, version, about, long_about = None)]
    17‚Üístruct Cli {
    18‚Üí    /// Configuration file path
    19‚Üí    #[arg(short, long)]
    20‚Üí    config: Option<PathBuf>,
    21‚Üí    
    22‚Üí    /// Working directory
    23‚Üí    #[arg(short, long)]
    24‚Üí    directory: Option<PathBuf>,
    25‚Üí    
    26‚Üí    /// Verbose logging
    27‚Üí    #[arg(short, long)]
    28‚Üí    verbose: bool,
    29‚Üí    
    30‚Üí    /// Debug mode
    31‚Üí    #[arg(short, long)]
    32‚Üí    debug: bool,
    33‚Üí    
    34‚Üí    /// Subcommands
    35‚Üí    #[command(subcommand)]
    36‚Üí    command: Option<Commands>,
    37‚Üí}
    38‚Üí
    39‚Üí#[derive(Subcommand)]
    40‚Üíenum Commands {
    41‚Üí    /// Initialize vimmacs configuration
    42‚Üí    Init {
    43‚Üí        /// Force overwrite existing configuration
    44‚Üí        #[arg(long)]
    45‚Üí        force: bool,
    46‚Üí    },
    47‚Üí    /// Start vimmacs in interactive mode
    48‚Üí    Start {
    49‚Üí        /// Session name
    50‚Üí        #[arg(short, long)]
    51‚Üí        session: Option<String>,
    52‚Üí        
    53‚Üí        /// Editor to use (neovim, emacs, auto)
    54‚Üí        #[arg(short, long)]
    55‚Üí        editor: Option<String>,
    56‚Üí        
    57‚Üí        /// Files to open initially
    58‚Üí        files: Vec<PathBuf>,
    59‚Üí    },
    60‚Üí    /// Manage terminal sessions
    61‚Üí    Session {
    62‚Üí        #[command(subcommand)]
    63‚Üí        action: SessionCommand,
    64‚Üí    },
    65‚Üí    /// Project management
    66‚Üí    Project {
    67‚Üí        #[command(subcommand)]
    68‚Üí        action: ProjectCommand,
    69‚Üí    },
    70‚Üí    /// System information and diagnostics
    71‚Üí    Info,
    72‚Üí    /// Run security checks
    73‚Üí    Security {
    74‚Üí        #[command(subcommand)]
    75‚Üí        action: SecurityCommand,
    76‚Üí    },
    77‚Üí}
    78‚Üí
    79‚Üí#[derive(Subcommand)]
    80‚Üíenum SessionCommand {
    81‚Üí    /// List active sessions
    82‚Üí    List,
    83‚Üí    /// Create new session
    84‚Üí    Create {
    85‚Üí        name: String,
    86‚Üí        #[arg(short, long)]
    87‚Üí        directory: Option<PathBuf>,
    88‚Üí    },
    89‚Üí    /// Attach to existing session
    90‚Üí    Attach { name: String },
    91‚Üí    /// Kill session
    92‚Üí    Kill { name: String },
    93‚Üí    /// Kill all sessions
    94‚Üí    KillAll,
    95‚Üí}
    96‚Üí
    97‚Üí#[derive(Subcommand)]
    98‚Üíenum ProjectCommand {
    99‚Üí    /// Detect project information
   100‚Üí    Info { path: Option<PathBuf> },
   101‚Üí    /// Initialize project workspace
   102‚Üí    Init { path: Option<PathBuf> },
   103‚Üí    /// Analyze project files
   104‚Üí    Analyze { path: Option<PathBuf> },
   105‚Üí}
   106‚Üí
   107‚Üí#[derive(Subcommand)]
   108‚Üíenum SecurityCommand {
   109‚Üí    /// Run security validation tests
   110‚Üí    Validate,
   111‚Üí    /// Check for security updates
   112‚Üí    Updates,
   113‚Üí    /// Audit configuration
   114‚Üí    Audit,
   115‚Üí}
   116‚Üí
   117‚Üí#[tokio::main]
   118‚Üíasync fn main() -> Result<()> {
   119‚Üí    let cli = Cli::parse();
   120‚Üí    
   121‚Üí    // Initialize logging
   122‚Üí    init_logging(&cli)?;
   123‚Üí    
   124‚Üí    // Load configuration
   125‚Üí    let config = load_config(cli.config).await?;
   126‚Üí    
   127‚Üí    // Change working directory if specified
   128‚Üí    if let Some(ref dir) = cli.directory {
   129‚Üí        std::env::set_current_dir(dir)?;
   130‚Üí        info!("Changed working directory to: {:?}", dir);
   131‚Üí    }
   132‚Üí    
   133‚Üí    // Execute command or start interactive mode
   134‚Üí    match cli.command {
   135‚Üí        Some(Commands::Init { force }) => {
   136‚Üí            handle_init(force).await
   137‚Üí        }
   138‚Üí        Some(Commands::Start { session, editor, files }) => {
   139‚Üí            handle_start(config, session, editor, files).await
   140‚Üí        }
   141‚Üí        Some(Commands::Session { action }) => {
   142‚Üí            handle_session_command(config, action).await
   143‚Üí        }
   144‚Üí        Some(Commands::Project { action }) => {
   145‚Üí            handle_project_command(action).await
   146‚Üí        }
   147‚Üí        Some(Commands::Info) => {
   148‚Üí            handle_info().await
   149‚Üí        }
   150‚Üí        Some(Commands::Security { action }) => {
   151‚Üí            handle_security_command(action).await
   152‚Üí        }
   153‚Üí        None => {
   154‚Üí            // Default: start interactive mode
   155‚Üí            handle_start(config, None, None, Vec::new()).await
   156‚Üí        }
   157‚Üí    }
   158‚Üí}
   159‚Üí
   160‚Üí/// Initialize logging based on CLI options
   161‚Üífn init_logging(cli: &Cli) -> Result<()> {
   162‚Üí    let log_level = if cli.debug {
   163‚Üí        "debug"
   164‚Üí    } else if cli.verbose {
   165‚Üí        "info"
   166‚Üí    } else {
   167‚Üí        "warn"
   168‚Üí    };
   169‚Üí    
   170‚Üí    let filter = tracing_subscriber::EnvFilter::try_from_default_env()
   171‚Üí        .unwrap_or_else(|_| tracing_subscriber::EnvFilter::new(log_level));
   172‚Üí    
   173‚Üí    tracing_subscriber::registry()
   174‚Üí        .with(filter)
   175‚Üí        .with(
   176‚Üí            tracing_subscriber::fmt::layer()
   177‚Üí                .with_target(cli.debug)
   178‚Üí                .with_thread_ids(cli.debug)
   179‚Üí                .with_file(cli.debug)
   180‚Üí                .with_line_number(cli.debug)
   181‚Üí        )
   182‚Üí        .try_init()?;
   183‚Üí    
   184‚Üí    info!("Vimmacs starting with log level: {}", log_level);
   185‚Üí    Ok(())
   186‚Üí}
   187‚Üí
   188‚Üí/// Load configuration with optional override
   189‚Üíasync fn load_config(config_path: Option<PathBuf>) -> Result<Config> {
   190‚Üí    match config_path {
   191‚Üí        Some(path) => {
   192‚Üí            info!("Loading configuration from: {:?}", path);
   193‚Üí            // In a full implementation, this would load from the specified file
   194‚Üí            Config::load().await
   195‚Üí        }
   196‚Üí        None => {
   197‚Üí            Config::load().await
   198‚Üí        }
   199‚Üí    }
   200‚Üí}
   201‚Üí
   202‚Üí/// Handle initialization command
   203‚Üíasync fn handle_init(force: bool) -> Result<()> {
   204‚Üí    info!("Initializing Vimmacs configuration");
   205‚Üí    
   206‚Üí    let config = Config::default();
   207‚Üí    let config_dir = &config.app.config_dir;
   208‚Üí    
   209‚Üí    // Check if configuration already exists
   210‚Üí    if config_dir.exists() && !force {
   211‚Üí        return Err(anyhow::anyhow!(
   212‚Üí            "Configuration directory already exists: {:?}\nUse --force to overwrite",
   213‚Üí            config_dir
   214‚Üí        ));
   215‚Üí    }
   216‚Üí    
   217‚Üí    // Create configuration directories
   218‚Üí    let dirs = [
   219‚Üí        config_dir.as_path(),
   220‚Üí        &config.app.data_dir,
   221‚Üí        &config.app.cache_dir,
   222‚Üí        &config.app.log_dir,
   223‚Üí    ];
   224‚Üí    
   225‚Üí    FileSystemUtils::ensure_directory_structure(&dirs).await?;
   226‚Üí    
   227‚Üí    // Save default configuration
   228‚Üí    config.save(None).await?;
   229‚Üí    
   230‚Üí    println!("‚úÖ Vimmacs initialized successfully!");
   231‚Üí    println!("üìÅ Configuration directory: {:?}", config_dir);
   232‚Üí    println!("üìù Edit configuration: {:?}", config_dir.join("config.toml"));
   233‚Üí    println!("üöÄ Start Vimmacs: vimmacs start");
   234‚Üí    
   235‚Üí    Ok(())
   236‚Üí}
   237‚Üí
   238‚Üí/// Handle start command - main interactive mode
   239‚Üíasync fn handle_start(
   240‚Üí    config: Config,
   241‚Üí    session_name: Option<String>,
   242‚Üí    editor: Option<String>,
   243‚Üí    files: Vec<PathBuf>,
   244‚Üí) -> Result<()> {
   245‚Üí    info!("Starting Vimmacs interactive mode");
   246‚Üí    
   247‚Üí    // Override editor preference if specified
   248‚Üí    let mut runtime_config = config;
   249‚Üí    if let Some(editor_name) = editor {
   250‚Üí        runtime_config.editor.primary_editor = editor_name;
   251‚Üí    }
   252‚Üí    
   253‚Üí    // Initialize Vimmacs core
   254‚Üí    let mut vimmacs = VimmacsCore::new().await?;
   255‚Üí    
   256‚Üí    // Open initial files if provided
   257‚Üí    for file in files {
   258‚Üí        info!("Opening initial file: {:?}", file);
   259‚Üí        // File opening logic would be implemented here
   260‚Üí    }
   261‚Üí    
   262‚Üí    // Print startup information
   263‚Üí    print_startup_banner(&runtime_config);
   264‚Üí    
   265‚Üí    // Run main loop
   266‚Üí    match vimmacs.run().await {
   267‚Üí        Ok(_) => {
   268‚Üí            info!("Vimmacs shut down successfully");
   269‚Üí            Ok(())
   270‚Üí        }
   271‚Üí        Err(e) => {
   272‚Üí            error!("Vimmacs encountered an error: {}", e);
   273‚Üí            Err(e)
   274‚Üí        }
   275‚Üí    }
   276‚Üí}
   277‚Üí
   278‚Üí/// Handle session management commands
   279‚Üíasync fn handle_session_command(config: Config, action: SessionCommand) -> Result<()> {
   280‚Üí    match action {
   281‚Üí        SessionCommand::List => {
   282‚Üí            println!("üìã Active Sessions:");
   283‚Üí            // List sessions logic would be implemented here
   284‚Üí            println!("  ‚îî‚îÄ‚îÄ main (current)");
   285‚Üí            println!("  ‚îî‚îÄ‚îÄ project-work");
   286‚Üí        }
   287‚Üí        SessionCommand::Create { name, directory } => {
   288‚Üí            println!("üÜï Creating session: {}", name);
   289‚Üí            if let Some(dir) = directory {
   290‚Üí                println!("üìÅ Working directory: {:?}", dir);
   291‚Üí            }
   292‚Üí            // Session creation logic would be implemented here
   293‚Üí        }
   294‚Üí        SessionCommand::Attach { name } => {
   295‚Üí            println!("üîó Attaching to session: {}", name);
   296‚Üí            // Session attachment logic would be implemented here
   297‚Üí        }
   298‚Üí        SessionCommand::Kill { name } => {
   299‚Üí            println!("üíÄ Killing session: {}", name);
   300‚Üí            // Session termination logic would be implemented here
   301‚Üí        }
   302‚Üí        SessionCommand::KillAll => {
   303‚Üí            println!("üíÄ Killing all sessions");
   304‚Üí            // All sessions termination logic would be implemented here
   305‚Üí        }
   306‚Üí    }
   307‚Üí    Ok(())
   308‚Üí}
   309‚Üí
   310‚Üí/// Handle project management commands
   311‚Üíasync fn handle_project_command(action: ProjectCommand) -> Result<()> {
   312‚Üí    match action {
   313‚Üí        ProjectCommand::Info { path } => {
   314‚Üí            let project_path = path.unwrap_or_else(|| SystemUtils::get_current_dir());
   315‚Üí            let project_info = FileSystemUtils::detect_project_info(&project_path).await?;
   316‚Üí            
   317‚Üí            println!("üìä Project Information");
   318‚Üí            println!("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ");
   319‚Üí            println!("üìÅ Path: {:?}", project_info.root_path);
   320‚Üí            println!("üìù Name: {}", project_info.name);
   321‚Üí            println!("üè∑Ô∏è  Type: {:?}", project_info.project_type);
   322‚Üí            if let Some(language) = project_info.main_language {
   323‚Üí                println!("üíª Language: {}", language);
   324‚Üí            }
   325‚Üí            if let Some(build_system) = project_info.build_system {
   326‚Üí                println!("üîß Build System: {}", build_system);
   327‚Üí            }
   328‚Üí            println!("üìö Git Repository: {}", if project_info.git_repository { "Yes" } else { "No" });
   329‚Üí            
   330‚Üí            if !project_info.config_files.is_empty() {
   331‚Üí                println!("‚öôÔ∏è  Configuration Files:");
   332‚Üí                for config_file in project_info.config_files {
   333‚Üí                    println!("   ‚îî‚îÄ‚îÄ {:?}", config_file);
   334‚Üí                }
   335‚Üí            }
   336‚Üí        }
   337‚Üí        ProjectCommand::Init { path } => {
   338‚Üí            let project_path = path.unwrap_or_else(|| SystemUtils::get_current_dir());
   339‚Üí            println!("üöÄ Initializing project workspace: {:?}", project_path);
   340‚Üí            // Project initialization logic would be implemented here
   341‚Üí        }
   342‚Üí        ProjectCommand::Analyze { path } => {
   343‚Üí            let project_path = path.unwrap_or_else(|| SystemUtils::get_current_dir());
   344‚Üí            println!("üîç Analyzing project: {:?}", project_path);
   345‚Üí            
   346‚Üí            let file_stats = FileSystemUtils::analyze_directory_files(&project_path).await?;
   347‚Üí            
   348‚Üí            println!("üìà File Statistics");
   349‚Üí            println!("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ");
   350‚Üí            println!("üìÑ Total Files: {}", file_stats.total_files);
   351‚Üí            println!("üíæ Total Size: {}", SystemUtils::format_file_size(file_stats.total_size_bytes));
   352‚Üí            
   353‚Üí            println!("\nüè∑Ô∏è  File Types:");
   354‚Üí            let mut sorted_types: Vec<_> = file_stats.file_types.iter().collect();
   355‚Üí            sorted_types.sort_by(|a, b| b.1.cmp(a.1));
   356‚Üí            for (ext, count) in sorted_types.iter().take(10) {
   357‚Üí                println!("   {} files: {}", count, ext);
   358‚Üí            }
   359‚Üí            
   360‚Üí            if !file_stats.largest_files.is_empty() {
   361‚Üí                println!("\nüìä Largest Files:");
   362‚Üí                for (file, size) in file_stats.largest_files.iter().take(5) {
   363‚Üí                    println!("   {} - {:?}", SystemUtils::format_file_size(*size), file.file_name().unwrap_or_default());
   364‚Üí                }
   365‚Üí            }
   366‚Üí        }
   367‚Üí    }
   368‚Üí    Ok(())
   369‚Üí}
   370‚Üí
   371‚Üí/// Handle info command
   372‚Üíasync fn handle_info() -> Result<()> {
   373‚Üí    let system_info = SystemUtils::get_system_info().await;
   374‚Üí    
   375‚Üí    println!("üñ•Ô∏è  Vimmacs System Information");
   376‚Üí    println!("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ");
   377‚Üí    println!("üåê OS: {} {}", system_info.os_type, system_info.os_version);
   378‚Üí    println!("üèóÔ∏è  Architecture: {}", system_info.architecture);
   379‚Üí    println!("‚ö° CPU Cores: {}", system_info.cpu_cores);
   380‚Üí    println!("üíæ Memory: {:.1} GB total, {:.1} GB available", 
   381‚Üí             system_info.total_memory_gb, system_info.available_memory_gb);
   382‚Üí    
   383‚Üí    if let Some(terminal) = system_info.terminal_type {
   384‚Üí        println!("üì∫ Terminal: {}", terminal);
   385‚Üí    }
   386‚Üí    
   387‚Üí    if let Some(shell) = system_info.shell {
   388‚Üí        println!("üêö Shell: {}", shell);
   389‚Üí    }
   390‚Üí    
   391‚Üí    // Check for required tools
   392‚Üí    println!("\nüîß Tool Availability:");
   393‚Üí    let tools = ["nvim", "emacs", "tmux", "zellij", "git"];
   394‚Üí    for tool in tools {
   395‚Üí        let available = SystemUtils::is_command_available(tool).await;
   396‚Üí        let status = if available { "‚úÖ" } else { "‚ùå" };
   397‚Üí        println!("   {} {}", status, tool);
   398‚Üí    }
   399‚Üí    
   400‚Üí    Ok(())
   401‚Üí}
