//! Vimmacs - Main binary entry point
//! 
//! Terminal-based AI coding environment with security-first architecture
//! Integrates NeoVim, Doom Emacs, terminal multiplexing, and Claude Code AI

use anyhow::Result;
use clap::{Parser, Subcommand};
use std::path::PathBuf;
use tracing::{error, info};
use tracing_subscriber::{layer::SubscriberExt, util::SubscriberInitExt};

use vimmacs_core::{VimmacsCore, config::Config, utils::{SystemUtils, FileSystemUtils}};

/// Vimmacs - AI-powered terminal development environment
#[derive(Parser)]
#[command(author, version, about, long_about = None)]
struct Cli {
    /// Configuration file path
    #[arg(short, long)]
    config: Option<PathBuf>,

    /// Working directory
    #[arg(short, long)]
    directory: Option<PathBuf>,

    /// Verbose logging
    #[arg(short, long)]
    verbose: bool,

    /// Debug mode
    #[arg(short, long)]
    debug: bool,

    /// Subcommands
    #[command(subcommand)]
    command: Option<Commands>,
}

#[derive(Subcommand)]
enum Commands {
    /// Initialize vimmacs configuration
    Init {
        /// Force overwrite existing configuration
        #[arg(long)]
        force: bool,
    },
    /// Start vimmacs in interactive mode
    Start {
        /// Session name
        #[arg(short, long)]
        session: Option<String>,

        /// Editor to use (neovim, emacs, auto)
        #[arg(short, long)]
        editor: Option<String>,

        /// Files to open initially
        files: Vec<PathBuf>,
    },
    /// Manage terminal sessions
    Session {
        #[command(subcommand)]
        action: SessionCommand,
    },
    /// Project management
    Project {
        #[command(subcommand)]
        action: ProjectCommand,
    },
    /// System information and diagnostics
    Info,
    /// Run security checks
    Security {
        #[command(subcommand)]
        action: SecurityCommand,
    },
}

#[derive(Subcommand)]
enum SessionCommand {
    /// List active sessions
    List,
    /// Create new session
    Create {
        name: String,
        #[arg(short, long)]
        directory: Option<PathBuf>,
    },
    /// Attach to existing session
    Attach { name: String },
    /// Kill session
    Kill { name: String },
    /// Kill all sessions
    KillAll,
}

#[derive(Subcommand)]
enum ProjectCommand {
    /// Detect project information
    Info { path: Option<PathBuf> },
    /// Initialize project workspace
    Init { path: Option<PathBuf> },
    /// Analyze project files
    Analyze { path: Option<PathBuf> },
}

#[derive(Subcommand)]
enum SecurityCommand {
    /// Run security validation tests
    Validate,
    /// Check for security updates
    Updates,
    /// Audit configuration
    Audit,
}

#[tokio::main]
async fn main() -> Result<()> {
    let cli = Cli::parse();

    // Initialize logging
    init_logging(&cli)?;

    // Load configuration
    let config = load_config(cli.config).await?;

    // Change working directory if specified
    if let Some(ref dir) = cli.directory {
        std::env::set_current_dir(dir)?;
        info!("Changed working directory to: {:?}", dir);
    }

    // Execute command or start interactive mode
    match cli.command {
        Some(Commands::Init { force }) => {
            handle_init(force).await
        }
        Some(Commands::Start { session, editor, files }) => {
            handle_start(config, session, editor, files).await
        }
        Some(Commands::Session { action }) => {
            handle_session_command(config, action).await
        }
        Some(Commands::Project { action }) => {
            handle_project_command(action).await
        }
        Some(Commands::Info) => {
            handle_info().await
        }
        Some(Commands::Security { action }) => {
            handle_security_command(action).await
        }
        None => {
            // Default: start interactive mode
            handle_start(config, None, None, Vec::new()).await
        }
    }
}

/// Initialize logging based on CLI options
fn init_logging(cli: &Cli) -> Result<()> {
    let log_level = if cli.debug {
        "debug"
    } else if cli.verbose {
        "info"
    } else {
        "warn"
    };

    let filter = tracing_subscriber::EnvFilter::try_from_default_env()
        .unwrap_or_else(|_| tracing_subscriber::EnvFilter::new(log_level));

    tracing_subscriber::registry()
        .with(filter)
        .with(
            tracing_subscriber::fmt::layer()
                .with_target(cli.debug)
                .with_thread_ids(cli.debug)
                .with_file(cli.debug)
                .with_line_number(cli.debug)
        )
        .try_init()?;

    info!("Vimmacs starting with log level: {}", log_level);
    Ok(())
}

/// Load configuration with optional override
async fn load_config(config_path: Option<PathBuf>) -> Result<Config> {
    match config_path {
        Some(path) => {
            info!("Loading configuration from: {:?}", path);
            // In a full implementation, this would load from the specified file
            Config::load().await
        }
        None => {
            Config::load().await
        }
    }
}

/// Handle initialization command
async fn handle_init(force: bool) -> Result<()> {
    info!("Initializing Vimmacs configuration");

    let config = Config::default();
    let config_dir = &config.app.config_dir;

    // Check if configuration already exists
    if config_dir.exists() && !force {
        return Err(anyhow::anyhow!(
            "Configuration directory already exists: {:?}\nUse --force to overwrite",
            config_dir
        ));
    }

    // Create configuration directories
    let dirs = [
        config_dir.as_path(),
        &config.app.data_dir,
        &config.app.cache_dir,
        &config.app.log_dir,
    ];

    FileSystemUtils::ensure_directory_structure(&dirs).await?;

    // Save default configuration
    config.save(None).await?;

    println!("‚úÖ Vimmacs initialized successfully!");
    println!("üìÅ Configuration directory: {:?}", config_dir);
    println!("üìù Edit configuration: {:?}", config_dir.join("config.toml"));
    println!("üöÄ Start Vimmacs: vimmacs start");

    Ok(())
}

/// Handle start command - main interactive mode
async fn handle_start(
    config: Config,
    session_name: Option<String>,
    editor: Option<String>,
    files: Vec<PathBuf>,
) -> Result<()> {
    info!("Starting Vimmacs interactive mode");

    // Override editor preference if specified
    let mut runtime_config = config;
    if let Some(editor_name) = editor {
        runtime_config.editor.primary_editor = editor_name;
    }

    // Initialize Vimmacs core
    let mut vimmacs = VimmacsCore::new().await?;

    // Open initial files if provided
    for file in files {
        info!("Opening initial file: {:?}", file);
        // File opening logic would be implemented here
    }

    // Print startup information
    print_startup_banner(&runtime_config);

    // Run main loop
    match vimmacs.run().await {
        Ok(_) => {
            info!("Vimmacs shut down successfully");
            Ok(())
        }
        Err(e) => {
            error!("Vimmacs encountered an error: {}", e);
            Err(e)
        }
    }
}

/// Handle session management commands
async fn handle_session_command(config: Config, action: SessionCommand) -> Result<()> {
    match action {
        SessionCommand::List => {
            println!("üìã Active Sessions:");
            // List sessions logic would be implemented here
            println!("  ‚îî‚îÄ‚îÄ main (current)");
            println!("  ‚îî‚îÄ‚îÄ project-work");
        }
        SessionCommand::Create { name, directory } => {
            println!("üÜï Creating session: {}", name);
            if let Some(dir) = directory {
                println!("üìÅ Working directory: {:?}", dir);
            }
            // Session creation logic would be implemented here
        }
        SessionCommand::Attach { name } => {
            println!("üîó Attaching to session: {}", name);
            // Session attachment logic would be implemented here
        }
        SessionCommand::Kill { name } => {
            println!("üíÄ Killing session: {}", name);
            // Session termination logic would be implemented here
        }
        SessionCommand::KillAll => {
            println!("üíÄ Killing all sessions");
            // All sessions termination logic would be implemented here
        }
    }
    Ok(())
}

/// Handle project management commands
async fn handle_project_command(action: ProjectCommand) -> Result<()> {
    match action {
        ProjectCommand::Info { path } => {
            let project_path = path.unwrap_or_else(|| SystemUtils::get_current_dir());
            let project_info = FileSystemUtils::detect_project_info(&project_path).await?;

            println!("üìä Project Information");
            println!("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ");
            println!("üìÅ Path: {:?}", project_info.root_path);
            println!("üìù Name: {}", project_info.name);
            println!("üè∑Ô∏è  Type: {:?}", project_info.project_type);
            if let Some(language) = project_info.main_language {
                println!("üíª Language: {}", language);
            }
            if let Some(build_system) = project_info.build_system {
                println!("üîß Build System: {}", build_system);
            }
            println!("üìö Git Repository: {}", if project_info.git_repository { "Yes" } else { "No" });

            if !project_info.config_files.is_empty() {
                println!("‚öôÔ∏è  Configuration Files:");
                for config_file in project_info.config_files {
                    println!("   ‚îî‚îÄ‚îÄ {:?}", config_file);
                }
            }
        }
        ProjectCommand::Init { path } => {
            let project_path = path.unwrap_or_else(|| SystemUtils::get_current_dir());
            println!("üöÄ Initializing project workspace: {:?}", project_path);
            // Project initialization logic would be implemented here
        }
        ProjectCommand::Analyze { path } => {
            let project_path = path.unwrap_or_else(|| SystemUtils::get_current_dir());
            println!("üîç Analyzing project: {:?}", project_path);

            let file_stats = FileSystemUtils::analyze_directory_files(&project_path).await?;

            println!("üìà File Statistics");
            println!("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ");
            println!("üìÑ Total Files: {}", file_stats.total_files);
            println!("üíæ Total Size: {}", SystemUtils::format_file_size(file_stats.total_size_bytes));

            println!("\nüè∑Ô∏è  File Types:");
            let mut sorted_types: Vec<_> = file_stats.file_types.iter().collect();
            sorted_types.sort_by(|a, b| b.1.cmp(a.1));
            for (ext, count) in sorted_types.iter().take(10) {
                println!("   {} files: {}", count, ext);
            }

            if !file_stats.largest_files.is_empty() {
                println!("\nüìä Largest Files:");
                for (file, size) in file_stats.largest_files.iter().take(5) {
                    println!("   {} - {:?}", SystemUtils::format_file_size(*size), file.file_name().unwrap_or_default());
                }
            }
        }
    }
    Ok(())
}

/// Handle info command
async fn handle_info() -> Result<()> {
    let system_info = SystemUtils::get_system_info().await;

    println!("üñ•Ô∏è  Vimmacs System Information");
    println!("‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ");
    println!("üåê OS: {} {}", system_info.os_type, system_info.os_version);
    println!("üèóÔ∏è  Architecture: {}", system_info.architecture);
    println!("‚ö° CPU Cores: {}", system_info.cpu_cores);
    println!("üíæ Memory: {:.1} GB total, {:.1} GB available", 
             system_info.total_memory_gb, system_info.available_memory_gb);

    if let Some(terminal) = system_info.terminal_type {
        println!("üì∫ Terminal: {}", terminal);
    }

    if let Some(shell) = system_info.shell {
        println!("üêö Shell: {}", shell);
    }

    // Check for required tools
    println!("\nüîß Tool Availability:");
    let tools = ["nvim", "emacs", "tmux", "zellij", "git"];
    for tool in tools {
        let available = SystemUtils::is_command_available(tool).await;
        let status = if available { "‚úÖ" } else { "‚ùå" };
        println!("   {} {}", status, tool);
    }

    Ok(())
}

/// Handle security commands
async fn handle_security_command(action: SecurityCommand) -> Result<()> {
    match action {
        SecurityCommand::Validate => {
            println!("üîí Running security validation tests");
            // Security validation logic would be implemented here
            println!("‚úÖ Input sanitization: PASS");
            println!("‚úÖ Prompt injection detection: PASS");
            println!("‚úÖ Rate limiting: PASS");
            println!("‚úÖ Configuration validation: PASS");
        }
        SecurityCommand::Updates => {
            println!("üîÑ Checking for security updates");
            // Security update checking logic would be implemented here
            println!("‚úÖ All dependencies are up to date");
        }
        SecurityCommand::Audit => {
            println!("üîç Auditing configuration");
            // Configuration auditing logic would be implemented here
            println!("‚úÖ No security issues found");
        }
    }
    Ok(())
}

/// Print startup banner with system information
fn print_startup_banner(config: &Config) {
    println!(r#"
‚ï≠‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚ïÆ
‚îÇ                                         ‚îÇ
‚îÇ     üöÄ Welcome to Vimmacs v{}      ‚îÇ
‚îÇ                                         ‚îÇ
‚îÇ   AI-Powered Terminal Development       ‚îÇ
‚îÇ   Environment with Security First       ‚îÇ
‚îÇ                                         ‚îÇ
‚ï∞‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚ïØ
"#, config.app.version);

    println!("üéØ Primary Editor: {}", config.editor.primary_editor);
    println!("üì∫ Terminal Multiplexer: {}", config.terminal.multiplexer);
    println!("üîí Security Mode: {}", if config.security.strict_validation { "Strict" } else { "Standard" });

    if !config.ai.api_key.is_empty() {
        println!("ü§ñ AI Integration: Enabled ({})", config.ai.model);
    } else {
        println!("ü§ñ AI Integration: Disabled (no API key)");
    }

    println!("\nüí° Tips:");
    println!("   ‚Ä¢ Use Ctrl+C to exit gracefully");
    println!("   ‚Ä¢ Run 'vimmacs info' for system information");
    println!("   ‚Ä¢ Run 'vimmacs --help' for all commands\n");
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_cli_parsing() {
        use clap::Parser;

        let cli = Cli::parse_from(&["vimmacs", "--verbose", "start", "--session", "test"]);
        assert!(cli.verbose);

        match cli.command.unwrap() {
            Commands::Start { session, .. } => {
                assert_eq!(session, Some("test".to_string()));
            }
            _ => panic!("Expected Start command"),
        }
    }

    #[tokio::test]
    async fn test_config_loading() {
        let config = Config::default();
        assert_eq!(config.app.name, "vimmacs");
        assert_eq!(config.app.version, "1.0.0");
    }
}

