//! Performance Monitoring and Optimization
//! 
//! Real-time performance tracking, metrics collection, and optimization
//! for neural field computations in Vimmacs.

use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::time::{Duration, Instant};
use chrono::{DateTime, Utc};

/// Performance timer for measuring operation durations
pub struct PerformanceTimer {
    start_time: Instant,
    operation_name: String,
}

impl PerformanceTimer {
    pub fn new(operation_name: String) -> Self {
        PerformanceTimer {
            start_time: Instant::now(),
            operation_name,
        }
    }

    pub fn elapsed(&self) -> Duration {
        self.start_time.elapsed()
    }

    pub fn elapsed_ms(&self) -> u64 {
        self.elapsed().as_millis() as u64
    }
}

impl Drop for PerformanceTimer {
    fn drop(&mut self) {
        let duration_ms = self.elapsed_ms();
        #[cfg(feature = "console_log")]
        web_sys::console::log_1(&format!("Operation '{}' took {}ms", self.operation_name, duration_ms).into());
    }
}

/// Performance metrics for neural field operations
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PerformanceMetrics {
    pub operation_counts: HashMap<String, u64>,
    pub operation_durations: HashMap<String, Vec<u64>>,
    pub total_operations: u64,
    pub average_duration_ms: f64,
    pub peak_memory_usage: usize,
    pub current_memory_usage: usize,
    pub cpu_utilization: f64,
    pub field_operations_per_second: f64,
    pub protocol_success_rate: f64,
    pub last_updated: DateTime<Utc>,
}

impl Default for PerformanceMetrics {
    fn default() -> Self {
        PerformanceMetrics {
            operation_counts: HashMap::new(),
            operation_durations: HashMap::new(),
            total_operations: 0,
            average_duration_ms: 0.0,
            peak_memory_usage: 0,
            current_memory_usage: 0,
            cpu_utilization: 0.0,
            field_operations_per_second: 0.0,
            protocol_success_rate: 100.0,
            last_updated: Utc::now(),
        }
    }
}

/// Memory usage statistics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MemoryStats {
    pub allocated_bytes: usize,
    pub peak_allocated_bytes: usize,
    pub allocations: u64,
    pub deallocations: u64,
    pub active_allocations: u64,
    pub fragmentation_ratio: f64,
}

impl Default for MemoryStats {
    fn default() -> Self {
        MemoryStats {
            allocated_bytes: 0,
            peak_allocated_bytes: 0,
            allocations: 0,
            deallocations: 0,
            active_allocations: 0,
            fragmentation_ratio: 0.0,
        }
    }
}

/// Resource usage limits and thresholds
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ResourceLimits {
    pub max_memory_bytes: usize,
    pub max_operation_duration_ms: u64,
    pub max_concurrent_operations: usize,
    pub cpu_throttle_threshold: f64,
    pub memory_warning_threshold: f64,
    pub memory_critical_threshold: f64,
}

impl Default for ResourceLimits {
    fn default() -> Self {
        ResourceLimits {
            max_memory_bytes: 512 * 1024 * 1024, // 512MB
            max_operation_duration_ms: 30000,      // 30 seconds
            max_concurrent_operations: 10,
            cpu_throttle_threshold: 80.0,          // 80%
            memory_warning_threshold: 75.0,        // 75%
            memory_critical_threshold: 90.0,       // 90%
        }
    }
}

/// Performance monitoring system
pub struct PerformanceMonitor {
    enabled: bool,
    metrics: PerformanceMetrics,
    memory_stats: MemoryStats,
    limits: ResourceLimits,
    active_timers: HashMap<String, Instant>,
    operation_history: Vec<OperationRecord>,
    warning_callbacks: Vec<Box<dyn Fn(&str)>>,
}

/// Record of a completed operation
#[derive(Debug, Clone)]
struct OperationRecord {
    name: String,
    duration_ms: u64,
    memory_used: usize,
    timestamp: DateTime<Utc>,
    success: bool,
}

impl PerformanceMonitor {
    /// Create a new performance monitor
    pub fn new(enabled: bool) -> Self {
        PerformanceMonitor {
            enabled,
            metrics: PerformanceMetrics::default(),
            memory_stats: MemoryStats::default(),
            limits: ResourceLimits::default(),
            active_timers: HashMap::new(),
            operation_history: Vec::new(),
            warning_callbacks: Vec::new(),
        }
    }

    /// Start timing an operation
    pub fn start_timer(&mut self, operation_name: &str) -> Option<PerformanceTimer> {
        if !self.enabled {
            return None;
        }

        let timer = PerformanceTimer::new(operation_name.to_string());
        self.active_timers.insert(operation_name.to_string(), timer.start_time);

        Some(timer)
    }

    /// Stop timing an operation and record metrics
    pub fn stop_timer(&mut self, operation_name: &str, success: bool) {
        if !self.enabled {
            return;
        }

        if let Some(start_time) = self.active_timers.remove(operation_name) {
            let duration = start_time.elapsed();
            let duration_ms = duration.as_millis() as u64;

            // Record operation
            self.record_operation(operation_name, duration_ms, success);

            // Check for performance issues
            self.check_performance_thresholds(operation_name, duration_ms);
        }
    }

    /// Record a completed operation
    fn record_operation(&mut self, operation_name: &str, duration_ms: u64, success: bool) {
        // Update operation counts
        *self.metrics.operation_counts.entry(operation_name.to_string()).or_insert(0) += 1;

        // Update duration tracking
        self.metrics.operation_durations
            .entry(operation_name.to_string())
            .or_insert_with(Vec::new)
            .push(duration_ms);

        // Update totals
        self.metrics.total_operations += 1;

        // Calculate average duration
        let total_duration: u64 = self.metrics.operation_durations
            .values()
            .flatten()
            .sum();
        self.metrics.average_duration_ms = total_duration as f64 / self.metrics.total_operations as f64;

        // Update success rate
        let successful_ops = self.operation_history
            .iter()
            .filter(|op| op.success)
            .count();

        if self.metrics.total_operations > 0 {
            self.metrics.protocol_success_rate = 
                (successful_ops as f64 / self.metrics.total_operations as f64) * 100.0;
        }

        // Store operation record
        let record = OperationRecord {
            name: operation_name.to_string(),
            duration_ms,
            memory_used: self.memory_stats.allocated_bytes,
            timestamp: Utc::now(),
            success,
        };

        self.operation_history.push(record);

        // Limit history size
        if self.operation_history.len() > 1000 {
            self.operation_history.drain(0..100); // Remove oldest 100 records
        }

        self.metrics.last_updated = Utc::now();
    }

    /// Update memory statistics
    pub fn update_memory_stats(&mut self, allocated: usize) {
        if !self.enabled {
            return;
        }

        self.memory_stats.allocated_bytes = allocated;

        if allocated > self.memory_stats.peak_allocated_bytes {
            self.memory_stats.peak_allocated_bytes = allocated;
        }

        self.metrics.current_memory_usage = allocated;
        if allocated > self.metrics.peak_memory_usage {
            self.metrics.peak_memory_usage = allocated;
        }

        // Check memory thresholds
        let usage_percent = (allocated as f64 / self.limits.max_memory_bytes as f64) * 100.0;

        if usage_percent > self.limits.memory_critical_threshold {
            self.trigger_warning(&format!("Critical memory usage: {:.1}%", usage_percent));
        } else if usage_percent > self.limits.memory_warning_threshold {
            self.trigger_warning(&format!("High memory usage: {:.1}%", usage_percent));
        }
    }

    /// Record memory allocation
    pub fn record_allocation(&mut self, size: usize) {
        if !self.enabled {
            return;
        }

        self.memory_stats.allocations += 1;
        self.memory_stats.active_allocations += 1;
        self.update_memory_stats(self.memory_stats.allocated_bytes + size);
    }

    /// Record memory deallocation
    pub fn record_deallocation(&mut self, size: usize) {
        if !self.enabled {
            return;
        }

        self.memory_stats.deallocations += 1;
        self.memory_stats.active_allocations = self.memory_stats.active_allocations.saturating_sub(1);
        self.update_memory_stats(self.memory_stats.allocated_bytes.saturating_sub(size));
    }

    /// Get current performance metrics
    pub fn get_metrics(&self) -> &PerformanceMetrics {
        &self.metrics
    }

    /// Get memory statistics
    pub fn get_memory_stats(&self) -> &MemoryStats {
        &self.memory_stats
    }

    /// Get resource limits
    pub fn get_limits(&self) -> &ResourceLimits {
        &self.limits
    }

    /// Update resource limits
    pub fn set_limits(&mut self, limits: ResourceLimits) {
        self.limits = limits;
    }

    /// Reset all metrics
    pub fn reset(&mut self) {
        self.metrics = PerformanceMetrics::default();
        self.memory_stats = MemoryStats::default();
        self.active_timers.clear();
        self.operation_history.clear();
    }

    /// Get performance summary report
    pub fn get_performance_report(&self) -> PerformanceReport {
        PerformanceReport {
            metrics: self.metrics.clone(),
            memory_stats: self.memory_stats.clone(),
            top_operations: self.get_top_operations(5),
            slowest_operations: self.get_slowest_operations(5),
            resource_utilization: self.calculate_resource_utilization(),
            recommendations: self.generate_recommendations(),
        }
    }

    /// Check if operation should be throttled
    pub fn should_throttle(&self) -> bool {
        if !self.enabled {
            return false;
        }

        // Check active operations
        if self.active_timers.len() >= self.limits.max_concurrent_operations {
            return true;
        }

        // Check memory usage
        let memory_usage_percent = (self.memory_stats.allocated_bytes as f64 / self.limits.max_memory_bytes as f64) * 100.0;
        if memory_usage_percent > self.limits.memory_critical_threshold {
            return true;
        }

        // Check CPU utilization (estimated from operation frequency)
        if self.metrics.cpu_utilization > self.limits.cpu_throttle_threshold {
            return true;
        }

        false
    }

    // Private helper methods

    fn check_performance_thresholds(&mut self, operation_name: &str, duration_ms: u64) {
        if duration_ms > self.limits.max_operation_duration_ms {
            self.trigger_warning(&format!(
                "Operation '{}' exceeded time limit: {}ms > {}ms", 
                operation_name, 
                duration_ms, 
                self.limits.max_operation_duration_ms
            ));
        }
    }

    fn trigger_warning(&self, message: &str) {
        #[cfg(feature = "console_log")]
        web_sys::console::warn_1(&format!("Performance Warning: {}", message).into());

        for callback in &self.warning_callbacks {
            callback(message);
        }
    }

    fn get_top_operations(&self, limit: usize) -> Vec<(String, u64)> {
        let mut operations: Vec<_> = self.metrics.operation_counts
            .iter()
            .map(|(name, &count)| (name.clone(), count))
            .collect();

        operations.sort_by(|a, b| b.1.cmp(&a.1));
        operations.truncate(limit);
        operations
    }

    fn get_slowest_operations(&self, limit: usize) -> Vec<(String, f64)> {
        let mut operations: Vec<_> = self.metrics.operation_durations
            .iter()
            .map(|(name, durations)| {
                let avg_duration = durations.iter().sum::<u64>() as f64 / durations.len() as f64;
                (name.clone(), avg_duration)
            })
            .collect();

        operations.sort_by(|a, b| b.1.partial_cmp(&a.1).unwrap_or(std::cmp::Ordering::Equal));
        operations.truncate(limit);
        operations
    }

    fn calculate_resource_utilization(&self) -> ResourceUtilization {
        let memory_utilization = (self.memory_stats.allocated_bytes as f64 / self.limits.max_memory_bytes as f64) * 100.0;
        let active_operations = self.active_timers.len();
        let operation_utilization = (active_operations as f64 / self.limits.max_concurrent_operations as f64) * 100.0;

        ResourceUtilization {
            memory_percent: memory_utilization,
            cpu_percent: self.metrics.cpu_utilization,
            operation_slots_percent: operation_utilization,
            overall_health_score: self.calculate_health_score(),
        }
    }

    fn calculate_health_score(&self) -> f64 {
        let memory_score = 100.0 - ((self.memory_stats.allocated_bytes as f64 / self.limits.max_memory_bytes as f64) * 100.0);
        let operation_score = 100.0 - ((self.active_timers.len() as f64 / self.limits.max_concurrent_operations as f64) * 100.0);
        let success_score = self.metrics.protocol_success_rate;

        (memory_score + operation_score + success_score) / 3.0
    }

    fn generate_recommendations(&self) -> Vec<String> {
        let mut recommendations = Vec::new();

        let memory_usage_percent = (self.memory_stats.allocated_bytes as f64 / self.limits.max_memory_bytes as f64) * 100.0;

        if memory_usage_percent > 80.0 {
            recommendations.push("Consider reducing field dimensions or clearing unused fields".to_string());
        }

        if self.metrics.average_duration_ms > 1000.0 {
            recommendations.push("Operations are taking longer than expected - consider optimization".to_string());
        }

        if self.metrics.protocol_success_rate < 95.0 {
            recommendations.push("Protocol success rate is below optimal - check error logs".to_string());
        }

        if self.active_timers.len() > self.limits.max_concurrent_operations / 2 {
            recommendations.push("High concurrent operation count - consider throttling".to_string());
        }

        recommendations
    }
}

/// Resource utilization summary
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ResourceUtilization {
    pub memory_percent: f64,
    pub cpu_percent: f64,
    pub operation_slots_percent: f64,
    pub overall_health_score: f64,
}

/// Comprehensive performance report
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PerformanceReport {
    pub metrics: PerformanceMetrics,
    pub memory_stats: MemoryStats,
    pub top_operations: Vec<(String, u64)>,
    pub slowest_operations: Vec<(String, f64)>,
    pub resource_utilization: ResourceUtilization,
    pub recommendations: Vec<String>,
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_performance_monitor_creation() {
        let monitor = PerformanceMonitor::new(true);
        assert!(monitor.enabled);
        assert_eq!(monitor.metrics.total_operations, 0);
    }

    #[test]
    fn test_timer_creation() {
        let mut monitor = PerformanceMonitor::new(true);
        let timer = monitor.start_timer("test_operation");
        assert!(timer.is_some());
        assert!(monitor.active_timers.contains_key("test_operation"));
    }

    #[test]
    fn test_memory_tracking() {
        let mut monitor = PerformanceMonitor::new(true);
        monitor.record_allocation(1024);
        assert_eq!(monitor.memory_stats.allocated_bytes, 1024);
        assert_eq!(monitor.memory_stats.allocations, 1);

        monitor.record_deallocation(512);
        assert_eq!(monitor.memory_stats.allocated_bytes, 512);
        assert_eq!(monitor.memory_stats.deallocations, 1);
    }

    #[test]
    fn test_resource_limits() {
        let monitor = PerformanceMonitor::new(true);
        let limits = monitor.get_limits();
        assert_eq!(limits.max_memory_bytes, 512 * 1024 * 1024);
        assert_eq!(limits.max_operation_duration_ms, 30000);
    }

    #[test]
    fn test_throttling() {
        let mut monitor = PerformanceMonitor::new(true);

        // Add many active operations
        for i in 0..15 {
            monitor.start_timer(&format!("op_{}", i));
        }

        assert!(monitor.should_throttle());
    }
}

