//! Security and Validation System
//! 
//! Comprehensive security validation, input sanitization, and safety measures
//! for neural field operations in Vimmacs.

use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use std::collections::{HashMap, HashSet};
use anyhow::{Result, anyhow};
use uuid::Uuid;
use chrono::{DateTime, Utc};

use crate::protocols::{Protocol, ProtocolParams};
use crate::sandbox::SandboxConfig;
use crate::resonance::ResonanceConfig;
use crate::RuntimeConfig;

/// Security threat levels
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub enum ThreatLevel {
    None,
    Low,
    Medium,
    High,
    Critical,
}

/// Security validation result
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ValidationResult {
    pub is_valid: bool,
    pub threat_level: ThreatLevel,
    pub warnings: Vec<String>,
    pub errors: Vec<String>,
    pub blocked_operations: Vec<String>,
    pub sanitized_input: Option<String>,
}

impl ValidationResult {
    pub fn new() -> Self {
        ValidationResult {
            is_valid: true,
            threat_level: ThreatLevel::None,
            warnings: Vec::new(),
            errors: Vec::new(),
            blocked_operations: Vec::new(),
            sanitized_input: None,
        }
    }

    pub fn invalid(error: String) -> Self {
        ValidationResult {
            is_valid: false,
            threat_level: ThreatLevel::High,
            warnings: Vec::new(),
            errors: vec![error],
            blocked_operations: Vec::new(),
            sanitized_input: None,
        }
    }

    pub fn add_warning(&mut self, warning: String) {
        self.warnings.push(warning);
    }

    pub fn add_error(&mut self, error: String) {
        self.errors.push(error);
        self.is_valid = false;
    }

    pub fn set_threat_level(&mut self, level: ThreatLevel) {
        self.threat_level = level;
        if matches!(level, ThreatLevel::High | ThreatLevel::Critical) {
            self.is_valid = false;
        }
    }
}

/// Security configuration for validation
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityConfig {
    pub enable_input_sanitization: bool,
    pub enable_protocol_validation: bool,
    pub enable_resource_limits: bool,
    pub enable_operation_logging: bool,
    pub max_field_dimensions: usize,
    pub max_protocol_depth: usize,
    pub max_execution_time_ms: u64,
    pub allowed_protocols: HashSet<String>,
    pub blocked_patterns: Vec<String>,
    pub suspicious_keywords: Vec<String>,
}

impl Default for SecurityConfig {
    fn default() -> Self {
        SecurityConfig {
            enable_input_sanitization: true,
            enable_protocol_validation: true,
            enable_resource_limits: true,
            enable_operation_logging: true,
            max_field_dimensions: 1024,
            max_protocol_depth: 10,
            max_execution_time_ms: 30000,
            allowed_protocols: [
                "attractor.co.emerge",
                "recursive.emergence",
                "field.resonance.scaffold",
                "memory.consolidation",
                "semantic.navigation",
                "coherence.amplification",
                "pattern.extraction",
                "adaptive.learning",
            ].iter().map(|s| s.to_string()).collect(),
            blocked_patterns: vec![
                "eval(".to_string(),
                "exec(".to_string(),
                "import(".to_string(),
                "require(".to_string(),
                "__proto__".to_string(),
                "constructor".to_string(),
                "prototype".to_string(),
            ],
            suspicious_keywords: vec![
                "javascript:".to_string(),
                "data:".to_string(),
                "vbscript:".to_string(),
                "file://".to_string(),
                "http://".to_string(),
                "https://".to_string(),
            ],
        }
    }
}

/// Audit log entry for security events
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityAuditEntry {
    pub id: String,
    pub timestamp: DateTime<Utc>,
    pub event_type: SecurityEventType,
    pub threat_level: ThreatLevel,
    pub description: String,
    pub source: String,
    pub action_taken: String,
    pub metadata: HashMap<String, String>,
}

/// Types of security events to log
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SecurityEventType {
    InputValidation,
    ProtocolValidation,
    ResourceLimitExceeded,
    SuspiciousActivity,
    AccessViolation,
    InjectionAttempt,
    ConfigurationChange,
    AuthenticationFailure,
}

/// Main security validator
pub struct SecurityValidator {
    config: SecurityConfig,
    audit_log: Vec<SecurityAuditEntry>,
    threat_patterns: Vec<regex::Regex>,
    session_id: String,
}

impl SecurityValidator {
    /// Create a new security validator
    pub fn new(runtime_config: &RuntimeConfig) -> Result<Self> {
        let config = SecurityConfig::default();
        let session_id = Uuid::new_v4().to_string();

        // Compile threat detection patterns
        let mut threat_patterns = Vec::new();
        for pattern in &config.blocked_patterns {
            match regex::Regex::new(pattern) {
                Ok(regex) => threat_patterns.push(regex),
                Err(e) => {
                    #[cfg(feature = "console_log")]
                    web_sys::console::warn_1(&format!("Invalid regex pattern '{}': {}", pattern, e).into());
                }
            }
        }

        let mut validator = SecurityValidator {
            config,
            audit_log: Vec::new(),
            threat_patterns,
            session_id,
        };

        // Log initialization
        validator.log_security_event(
            SecurityEventType::ConfigurationChange,
            ThreatLevel::None,
            "Security validator initialized".to_string(),
            "system".to_string(),
            "initialized".to_string(),
        );

        Ok(validator)
    }

    /// Validate field creation parameters
    pub fn validate_field_creation(&mut self, name: &str, dimensions: &[u32]) -> Result<()> {
        let mut result = ValidationResult::new();

        // Validate field name
        self.validate_identifier(name, &mut result)?;

        // Check dimensions
        for &dim in dimensions {
            if dim as usize > self.config.max_field_dimensions {
                result.add_error(format!("Field dimension {} exceeds maximum {}", dim, self.config.max_field_dimensions));
                result.set_threat_level(ThreatLevel::Medium);
            }
        }

        // Check total field size
        let total_size: u64 = dimensions.iter().map(|&d| d as u64).product();
        if total_size > 1_000_000 {
            result.add_error(format!("Total field size {} exceeds safety limit", total_size));
            result.set_threat_level(ThreatLevel::High);
        }

        self.process_validation_result(result, name, "field_creation")
    }

    /// Validate protocol execution
    pub fn validate_protocol(&mut self, protocol: &Protocol) -> Result<()> {
        let mut result = ValidationResult::new();

        // Check if protocol is allowed
        if !self.config.allowed_protocols.contains(&protocol.name) {
            result.add_error(format!("Protocol '{}' is not in allowed list", protocol.name));
            result.set_threat_level(ThreatLevel::High);

            self.log_security_event(
                SecurityEventType::AccessViolation,
                ThreatLevel::High,
                format!("Attempted to execute unauthorized protocol: {}", protocol.name),
                protocol.name.clone(),
                "blocked".to_string(),
            );
        }

        // Validate protocol name
        self.validate_identifier(&protocol.name, &mut result)?;

        // Check for suspicious patterns in description
        self.check_suspicious_content(&protocol.description, &mut result);

        // Validate parameters
        self.validate_protocol_parameters(&protocol.parameters, &mut result)?;

        // Check execution timeout
        if let Some(timeout) = protocol.timeout_ms {
            if timeout > self.config.max_execution_time_ms {
                result.add_error(format!("Protocol timeout {} exceeds maximum {}", timeout, self.config.max_execution_time_ms));
                result.set_threat_level(ThreatLevel::Medium);
            }
        }

        self.process_validation_result(result, &protocol.name, "protocol_execution")
    }

    /// Validate sandbox configuration
    pub fn validate_sandbox_config(&mut self, config: &SandboxConfig) -> Result<()> {
        let mut result = ValidationResult::new();

        // Validate sandbox name if present
        if let Some(ref name) = config.name {
            self.validate_identifier(name, &mut result)?;
        }

        // Check resource limits
        if config.memory_limit_mb > 1024 {
            result.add_warning("High memory limit requested for sandbox".to_string());
            result.set_threat_level(ThreatLevel::Low);
        }

        if config.execution_timeout_ms > self.config.max_execution_time_ms {
            result.add_error("Sandbox execution timeout exceeds system limit".to_string());
            result.set_threat_level(ThreatLevel::Medium);
        }

        self.process_validation_result(result, config.name.as_deref().unwrap_or("anonymous"), "sandbox_config")
    }

    /// Validate resonance configuration
    pub fn validate_resonance_config(&mut self, config: &ResonanceConfig) -> Result<()> {
        let mut result = ValidationResult::new();

        // Check frequency bounds
        if config.frequency < 0.0 || config.frequency > 100.0 {
            result.add_error("Resonance frequency out of safe range [0.0, 100.0]".to_string());
            result.set_threat_level(ThreatLevel::Medium);
        }

        // Check amplitude bounds
        if config.amplitude < 0.0 || config.amplitude > 10.0 {
            result.add_error("Resonance amplitude out of safe range [0.0, 10.0]".to_string());
            result.set_threat_level(ThreatLevel::Medium);
        }

        // Check coherence threshold
        if let Some(threshold) = config.coherence_threshold {
            if threshold < 0.0 || threshold > 1.0 {
                result.add_error("Coherence threshold must be between 0.0 and 1.0".to_string());
                result.set_threat_level(ThreatLevel::Low);
            }
        }

        self.process_validation_result(result, &format!("resonance_f{}_a{}", config.frequency, config.amplitude), "resonance_config")
    }

    /// Sanitize string input
    pub fn sanitize_input(&mut self, input: &str) -> Result<String> {
        if !self.config.enable_input_sanitization {
            return Ok(input.to_string());
        }

        let mut sanitized = input.to_string();
        let mut modifications = Vec::new();

        // Remove dangerous patterns
        for pattern in &self.threat_patterns {
            let before_len = sanitized.len();
            sanitized = pattern.replace_all(&sanitized, "").to_string();
            if sanitized.len() != before_len {
                modifications.push(format!("Removed pattern matching: {}", pattern.as_str()));
            }
        }

        // Remove suspicious keywords
        for keyword in &self.config.suspicious_keywords {
            let before_len = sanitized.len();
            sanitized = sanitized.replace(keyword, "");
            if sanitized.len() != before_len {
                modifications.push(format!("Removed suspicious keyword: {}", keyword));
            }
        }

        // Log modifications if any
        if !modifications.is_empty() {
            self.log_security_event(
                SecurityEventType::InputValidation,
                ThreatLevel::Medium,
                format!("Input sanitized: {}", modifications.join(", ")),
                "sanitizer".to_string(),
                "sanitized".to_string(),
            );
        }

        Ok(sanitized)
    }

    /// Get security audit log
    pub fn get_audit_log(&self) -> &[SecurityAuditEntry] {
        &self.audit_log
    }

    /// Get security configuration
    pub fn get_config(&self) -> &SecurityConfig {
        &self.config
    }

    /// Update security configuration
    pub fn update_config(&mut self, config: SecurityConfig) {
        self.config = config;

        self.log_security_event(
            SecurityEventType::ConfigurationChange,
            ThreatLevel::None,
            "Security configuration updated".to_string(),
            "admin".to_string(),
            "updated".to_string(),
        );
    }

    /// Check if operation should be blocked based on current threat level
    pub fn should_block_operation(&self, operation: &str) -> bool {
        // Check recent high-threat events
        let recent_threats = self.audit_log
            .iter()
            .rev()
            .take(10)
            .filter(|entry| matches!(entry.threat_level, ThreatLevel::High | ThreatLevel::Critical))
            .count();

        // Block if too many recent threats
        if recent_threats >= 3 {
            return true;
        }

        // Check operation-specific blocks
        self.config.blocked_patterns
            .iter()
            .any(|pattern| operation.contains(pattern))
    }

    /// Generate security report
    pub fn generate_security_report(&self) -> SecurityReport {
        let total_events = self.audit_log.len();
        let threat_distribution = self.calculate_threat_distribution();
        let recent_events = self.audit_log
            .iter()
            .rev()
            .take(10)
            .cloned()
            .collect();

        SecurityReport {
            session_id: self.session_id.clone(),
            total_events,
            threat_distribution,
            recent_events,
            blocked_operations: self.count_blocked_operations(),
            recommendations: self.generate_security_recommendations(),
            last_updated: Utc::now(),
        }
    }

    // Private helper methods

    fn validate_identifier(&self, identifier: &str, result: &mut ValidationResult) -> Result<()> {
        // Check for empty identifier
        if identifier.is_empty() {
            result.add_error("Identifier cannot be empty".to_string());
            return Ok(());
        }

        // Check length
        if identifier.len() > 100 {
            result.add_error("Identifier too long (max 100 characters)".to_string());
            result.set_threat_level(ThreatLevel::Low);
        }

        // Check for dangerous characters
        let dangerous_chars = ['<', '>', '"', '\'', '&', '/', '\\', '\0'];
        for ch in dangerous_chars.iter() {
            if identifier.contains(*ch) {
                result.add_error(format!("Identifier contains dangerous character: {}", ch));
                result.set_threat_level(ThreatLevel::Medium);
            }
        }

        // Check for path traversal
        if identifier.contains("..") || identifier.contains("./") || identifier.contains(".\\") {
            result.add_error("Identifier contains path traversal patterns".to_string());
            result.set_threat_level(ThreatLevel::High);
        }

        Ok(())
    }

    fn check_suspicious_content(&self, content: &str, result: &mut ValidationResult) {
        for keyword in &self.config.suspicious_keywords {
            if content.to_lowercase().contains(&keyword.to_lowercase()) {
                result.add_warning(format!("Content contains suspicious keyword: {}", keyword));
                result.set_threat_level(ThreatLevel::Low);
            }
        }

        // Check for potential injection patterns
        for pattern in &self.threat_patterns {
            if pattern.is_match(content) {
                result.add_error(format!("Content matches threat pattern: {}", pattern.as_str()));
                result.set_threat_level(ThreatLevel::High);
            }
        }
    }

    fn validate_protocol_parameters(&self, params: &ProtocolParams, result: &mut ValidationResult) -> Result<()> {
        // Check numeric parameters for reasonable bounds
        if let Some(attractors) = params.attractors {
            if attractors > 100 {
                result.add_warning("High number of attractors requested".to_string());
                result.set_threat_level(ThreatLevel::Low);
            }
        }

        if let Some(steps) = params.evolution_steps {
            if steps > 10000 {
                result.add_warning("High number of evolution steps requested".to_string());
                result.set_threat_level(ThreatLevel::Low);
            }
        }

        if let Some(depth) = params.depth {
            if depth > self.config.max_protocol_depth as u32 {
                result.add_error(format!("Protocol depth {} exceeds maximum {}", depth, self.config.max_protocol_depth));
                result.set_threat_level(ThreatLevel::Medium);
            }
        }

        // Validate custom parameters
        for (key, value) in &params.custom_params {
            self.validate_identifier(key, result)?;

            // Check value size
            let value_str = serde_json::to_string(value).unwrap_or_default();
            if value_str.len() > 10000 {
                result.add_error("Custom parameter value too large".to_string());
                result.set_threat_level(ThreatLevel::Medium);
            }
        }

        Ok(())
    }

    fn process_validation_result(&mut self, result: ValidationResult, source: &str, operation: &str) -> Result<()> {
        // Log validation events
        if !result.is_valid {
            self.log_security_event(
                SecurityEventType::InputValidation,
                result.threat_level.clone(),
                format!("Validation failed: {}", result.errors.join(", ")),
                source.to_string(),
                "blocked".to_string(),
            );

            return Err(anyhow!("Security validation failed: {}", result.errors.join(", ")));
        }

        // Log warnings
        if !result.warnings.is_empty() {
            self.log_security_event(
                SecurityEventType::InputValidation,
                result.threat_level.clone(),
                format!("Validation warnings: {}", result.warnings.join(", ")),
                source.to_string(),
                "warned".to_string(),
            );
        }

        Ok(())
    }

    fn log_security_event(
        &mut self,
        event_type: SecurityEventType,
        threat_level: ThreatLevel,
        description: String,
        source: String,
        action_taken: String,
    ) {
        if !self.config.enable_operation_logging {
            return;
        }

        let entry = SecurityAuditEntry {
            id: Uuid::new_v4().to_string(),
            timestamp: Utc::now(),
            event_type,
            threat_level,
            description,
            source,
            action_taken,
            metadata: HashMap::new(),
        };

        self.audit_log.push(entry);

        // Limit audit log size
        if self.audit_log.len() > 10000 {
            self.audit_log.drain(0..1000); // Remove oldest 1000 entries
        }

        #[cfg(feature = "console_log")]
        if matches!(entry.threat_level, ThreatLevel::High | ThreatLevel::Critical) {
            web_sys::console::warn_1(&format!("Security Event: {}", entry.description).into());
        }
    }

    fn calculate_threat_distribution(&self) -> HashMap<String, usize> {
        let mut distribution = HashMap::new();

        for entry in &self.audit_log {
            let key = format!("{:?}", entry.threat_level);
            *distribution.entry(key).or_insert(0) += 1;
        }

        distribution
    }

    fn count_blocked_operations(&self) -> usize {
        self.audit_log
            .iter()
            .filter(|entry| entry.action_taken == "blocked")
            .count()
    }

    fn generate_security_recommendations(&self) -> Vec<String> {
        let mut recommendations = Vec::new();

        let high_threat_events = self.audit_log
            .iter()
            .filter(|entry| matches!(entry.threat_level, ThreatLevel::High | ThreatLevel::Critical))
            .count();

        if high_threat_events > 10 {
            recommendations.push("Consider increasing security restrictions due to high threat activity".to_string());
        }

        let blocked_operations = self.count_blocked_operations();
        if blocked_operations > 50 {
            recommendations.push("High number of blocked operations - review security policies".to_string());
        }

        if self.audit_log.len() > 5000 {
            recommendations.push("Large audit log - consider archiving old entries".to_string());
        }

        recommendations
    }
}

/// Security report summary
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityReport {
    pub session_id: String,
    pub total_events: usize,
    pub threat_distribution: HashMap<String, usize>,
    pub recent_events: Vec<SecurityAuditEntry>,
    pub blocked_operations: usize,
    pub recommendations: Vec<String>,
    pub last_updated: DateTime<Utc>,
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::RuntimeConfig;

    #[test]
    fn test_security_validator_creation() {
        let config = RuntimeConfig::default();
