    }

    #[test]
    fn test_identifier_validation() {
        let config = RuntimeConfig::default();
        let mut validator = SecurityValidator::new(&config).unwrap();

        // Valid identifier
        let result = validator.validate_field_creation("valid_name", &[10, 10]);
        assert!(result.is_ok());

        // Invalid identifier with dangerous characters
        let result = validator.validate_field_creation("name<script>", &[10, 10]);
