        // Memory should be automatically consolidated
        assert!(memory.fragments.len() <= memory.config.max_fragments);
    }

    #[test]
    fn test_export_import() {
        let mut memory = MemorySystem::new(1024 * 1024).unwrap();

        // Add some test data
        let field_state = FieldState {
