//! Memory System for Neural Field State Management
//! 
//! Persistent memory management, state snapshots, and intelligent
//! memory consolidation for neural field operations.

use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use std::collections::{HashMap, VecDeque};
use uuid::Uuid;
use chrono::{DateTime, Utc};
use anyhow::{Result, anyhow};

use crate::neural_field::{NeuralField, FieldState};

/// Memory fragment containing field state and metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MemoryFragment {
    pub id: String,
    pub field_name: String,
    pub field_data: Vec<f64>,
    pub field_state: FieldState,
    pub timestamp: DateTime<Utc>,
    pub access_count: u32,
    pub last_accessed: DateTime<Utc>,
    pub similarity_embedding: Vec<f64>,
    pub metadata: HashMap<String, String>,
    pub tags: Vec<String>,
}

impl MemoryFragment {
    /// Create a new memory fragment from field data
    pub fn new(field_name: String, field_data: Vec<f64>, field_state: FieldState) -> Self {
        let id = Uuid::new_v4().to_string();
        let now = Utc::now();

        // Generate simple similarity embedding (in real implementation, use proper embeddings)
        let similarity_embedding = Self::generate_embedding(&field_data);

        MemoryFragment {
            id,
            field_name,
            field_data,
            field_state,
            timestamp: now,
            access_count: 0,
            last_accessed: now,
            similarity_embedding,
            metadata: HashMap::new(),
            tags: Vec::new(),
        }
    }

    /// Update access statistics
    pub fn mark_accessed(&mut self) {
        self.access_count += 1;
        self.last_accessed = Utc::now();
    }

    /// Calculate similarity to another fragment
    pub fn similarity(&self, other: &MemoryFragment) -> f64 {
        if self.similarity_embedding.len() != other.similarity_embedding.len() {
            return 0.0;
        }

        // Cosine similarity
        let dot_product: f64 = self.similarity_embedding
            .iter()
            .zip(&other.similarity_embedding)
            .map(|(a, b)| a * b)
            .sum();

        let norm_a: f64 = self.similarity_embedding.iter().map(|x| x * x).sum::<f64>().sqrt();
        let norm_b: f64 = other.similarity_embedding.iter().map(|x| x * x).sum::<f64>().sqrt();

        if norm_a == 0.0 || norm_b == 0.0 {
            0.0
        } else {
            dot_product / (norm_a * norm_b)
        }
    }

    fn generate_embedding(data: &[f64]) -> Vec<f64> {
        // Simple statistical embedding - in production, use proper neural embeddings
        let len = data.len() as f64;
        if len == 0.0 {
            return vec![0.0; 10];
        }

        let mean = data.iter().sum::<f64>() / len;
        let variance = data.iter().map(|x| (x - mean).powi(2)).sum::<f64>() / len;
        let std_dev = variance.sqrt();
        let skewness = data.iter().map(|x| ((x - mean) / std_dev).powi(3)).sum::<f64>() / len;
        let kurtosis = data.iter().map(|x| ((x - mean) / std_dev).powi(4)).sum::<f64>() / len;

        let min_val = data.iter().fold(f64::INFINITY, |a, &b| a.min(b));
        let max_val = data.iter().fold(f64::NEG_INFINITY, |a, &b| a.max(b));
        let range = max_val - min_val;

        // Energy in different frequency bands (simplified)
        let low_freq_energy = data.windows(2).map(|w| (w[1] - w[0]).abs()).sum::<f64>();
        let high_freq_energy = data.windows(3).map(|w| (w[2] - 2.0 * w[1] + w[0]).abs()).sum::<f64>();

        vec![
            mean, std_dev, skewness, kurtosis, min_val, max_val, range,
            low_freq_energy / len, high_freq_energy / len, data.len() as f64
        ]
    }
}

/// Memory system configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MemoryConfig {
    pub max_fragments: usize,
    pub max_memory_bytes: usize,
    pub consolidation_threshold: usize,
    pub similarity_threshold: f64,
    pub access_decay_factor: f64,
    pub enable_compression: bool,
    pub enable_similarity_search: bool,
}

impl Default for MemoryConfig {
    fn default() -> Self {
        MemoryConfig {
            max_fragments: 1000,
            max_memory_bytes: 100 * 1024 * 1024, // 100MB
            consolidation_threshold: 800,
            similarity_threshold: 0.7,
            access_decay_factor: 0.95,
            enable_compression: true,
            enable_similarity_search: true,
        }
    }
}

/// Memory usage statistics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MemoryStats {
    pub total_fragments: usize,
    pub memory_usage_bytes: usize,
    pub average_fragment_size: f64,
    pub oldest_fragment_age_days: f64,
    pub most_accessed_fragment_id: Option<String>,
    pub consolidation_events: u64,
    pub cache_hit_rate: f64,
    pub similarity_search_count: u64,
}

impl Default for MemoryStats {
    fn default() -> Self {
        MemoryStats {
            total_fragments: 0,
            memory_usage_bytes: 0,
            average_fragment_size: 0.0,
            oldest_fragment_age_days: 0.0,
            most_accessed_fragment_id: None,
            consolidation_events: 0,
            cache_hit_rate: 0.0,
            similarity_search_count: 0,
        }
    }
}

/// Memory system for neural field state management
pub struct MemorySystem {
    config: MemoryConfig,
    fragments: HashMap<String, MemoryFragment>,
    access_order: VecDeque<String>,
    similarity_cache: HashMap<String, Vec<String>>,
    stats: MemoryStats,
    session_id: String,
}

impl MemorySystem {
    /// Create a new memory system
    pub fn new(memory_limit_bytes: usize) -> Result<Self> {
        let mut config = MemoryConfig::default();
        config.max_memory_bytes = memory_limit_bytes;

        Ok(MemorySystem {
            config,
            fragments: HashMap::new(),
            access_order: VecDeque::new(),
            similarity_cache: HashMap::new(),
            stats: MemoryStats::default(),
            session_id: Uuid::new_v4().to_string(),
        })
    }

    /// Store a field snapshot in memory
    pub fn store_field_snapshot(&mut self, field_name: &str, field_state: &FieldState) -> Result<String> {
        let field_data = vec![0.0; 100]; // Placeholder - in real implementation, get actual field data
        let fragment = MemoryFragment::new(field_name.to_string(), field_data, field_state.clone());
        let fragment_id = fragment.id.clone();

        self.store_fragment(fragment)?;

        Ok(fragment_id)
    }

    /// Store a memory fragment
    pub fn store_fragment(&mut self, mut fragment: MemoryFragment) -> Result<()> {
        // Check memory limits
        if self.fragments.len() >= self.config.max_fragments {
            self.consolidate_memory()?;
        }

        // Update fragment metadata
        fragment.metadata.insert("session_id".to_string(), self.session_id.clone());

        let fragment_id = fragment.id.clone();

        // Store fragment
        self.fragments.insert(fragment_id.clone(), fragment);
        self.access_order.push_back(fragment_id.clone());

        // Invalidate similarity cache
        self.similarity_cache.clear();

        // Update statistics
        self.update_stats();

        #[cfg(feature = "console_log")]
        web_sys::console::log_1(&format!("Stored memory fragment: {}", fragment_id).into());

        Ok(())
    }

    /// Retrieve a fragment by ID
    pub fn get_fragment(&mut self, fragment_id: &str) -> Option<&mut MemoryFragment> {
        if let Some(fragment) = self.fragments.get_mut(fragment_id) {
            fragment.mark_accessed();
            self.move_to_front(fragment_id);
            Some(fragment)
        } else {
            None
        }
    }

    /// Find similar fragments to a given field
    pub fn find_similar_fragments(&mut self, field: &NeuralField, limit: usize) -> Vec<MemoryFragment> {
        if !self.config.enable_similarity_search {
            return Vec::new();
        }

        let field_data = field.get_field_data();
        let query_embedding = MemoryFragment::generate_embedding(&field_data);

        let mut similarities: Vec<_> = self.fragments
            .values()
            .map(|fragment| {
                let similarity = self.calculate_embedding_similarity(&query_embedding, &fragment.similarity_embedding);
                (fragment.clone(), similarity)
            })
            .filter(|(_, similarity)| *similarity >= self.config.similarity_threshold)
            .collect();

        similarities.sort_by(|a, b| b.1.partial_cmp(&a.1).unwrap_or(std::cmp::Ordering::Equal));
        similarities.truncate(limit);

        self.stats.similarity_search_count += 1;

        similarities.into_iter().map(|(fragment, _)| fragment).collect()
    }

    /// Find similar states (wrapper for compatibility)
    pub fn find_similar_states(&mut self, field: &NeuralField) -> Result<Vec<FieldState>> {
        let fragments = self.find_similar_fragments(field, 10);
        Ok(fragments.into_iter().map(|f| f.field_state).collect())
    }

    /// Consolidate field memory (for protocol compatibility)
    pub fn consolidate_field_memory(&mut self, field_name: &str, field_state: &FieldState, field_data: &[f64]) -> Result<()> {
        let fragment = MemoryFragment::new(field_name.to_string(), field_data.to_vec(), field_state.clone());
        self.store_fragment(fragment)
    }

    /// Search fragments by field name
    pub fn find_fragments_by_field(&self, field_name: &str) -> Vec<&MemoryFragment> {
        self.fragments
            .values()
            .filter(|fragment| fragment.field_name == field_name)
            .collect()
    }

    /// Search fragments by tags
    pub fn find_fragments_by_tag(&self, tag: &str) -> Vec<&MemoryFragment> {
        self.fragments
            .values()
            .filter(|fragment| fragment.tags.contains(&tag.to_string()))
            .collect()
    }

    /// Get fragments within a time range
    pub fn find_fragments_by_time_range(&self, start: DateTime<Utc>, end: DateTime<Utc>) -> Vec<&MemoryFragment> {
        self.fragments
            .values()
            .filter(|fragment| fragment.timestamp >= start && fragment.timestamp <= end)
            .collect()
    }

    /// Clear all memory fragments
    pub fn clear(&mut self) {
        self.fragments.clear();
        self.access_order.clear();
        self.similarity_cache.clear();
        self.stats = MemoryStats::default();

        #[cfg(feature = "console_log")]
        web_sys::console::log_1(&"Memory system cleared".into());
    }

    /// Get memory usage statistics
    pub fn get_stats(&self) -> &MemoryStats {
        &self.stats
    }

    /// Export memory fragments to JSON
    pub fn export_fragments(&self) -> Result<String> {
        let export_data = ExportData {
            session_id: self.session_id.clone(),
            fragments: self.fragments.values().cloned().collect(),
            stats: self.stats.clone(),
            exported_at: Utc::now(),
        };

        serde_json::to_string(&export_data)
            .map_err(|e| anyhow!("Failed to export memory fragments: {}", e))
    }

    /// Import memory fragments from JSON
    pub fn import_fragments(&mut self, json_data: &str) -> Result<usize> {
        let export_data: ExportData = serde_json::from_str(json_data)
            .map_err(|e| anyhow!("Failed to parse import data: {}", e))?;

        let mut imported_count = 0;

        for fragment in export_data.fragments {
            if self.fragments.len() < self.config.max_fragments {
                self.fragments.insert(fragment.id.clone(), fragment);
                imported_count += 1;
            }
        }

        self.update_stats();

        Ok(imported_count)
    }

    /// Perform memory consolidation
    pub fn consolidate_memory(&mut self) -> Result<()> {
        if self.fragments.len() < self.config.consolidation_threshold {
            return Ok(());
        }

        #[cfg(feature = "console_log")]
        web_sys::console::log_1(&"Starting memory consolidation".into());

        // Remove least recently used fragments
        let target_size = self.config.max_fragments * 3 / 4; // Reduce to 75% capacity
        let to_remove = self.fragments.len() - target_size;

        // Sort by access pattern (LRU + access frequency)
        let mut fragment_scores: Vec<_> = self.fragments
            .iter()
            .map(|(id, fragment)| {
                let recency_score = 1.0 / (1.0 + (Utc::now().timestamp() - fragment.last_accessed.timestamp()) as f64 / 86400.0);
                let frequency_score = fragment.access_count as f64;
                let combined_score = recency_score * frequency_score;
                (id.clone(), combined_score)
            })
            .collect();

        fragment_scores.sort_by(|a, b| a.1.partial_cmp(&b.1).unwrap_or(std::cmp::Ordering::Equal));

        // Remove lowest scoring fragments
        for (fragment_id, _) in fragment_scores.iter().take(to_remove) {
            self.fragments.remove(fragment_id);
            self.access_order.retain(|id| id != fragment_id);
        }

        // Clear similarity cache
        self.similarity_cache.clear();

        // Update statistics
        self.stats.consolidation_events += 1;
        self.update_stats();

        #[cfg(feature = "console_log")]
        web_sys::console::log_1(&format!("Memory consolidation completed. Removed {} fragments", to_remove).into());

        Ok(())
    }

    // Private helper methods

    fn move_to_front(&mut self, fragment_id: &str) {
        self.access_order.retain(|id| id != fragment_id);
        self.access_order.push_back(fragment_id.to_string());
    }

    fn calculate_embedding_similarity(&self, embedding1: &[f64], embedding2: &[f64]) -> f64 {
        if embedding1.len() != embedding2.len() {
            return 0.0;
        }

        let dot_product: f64 = embedding1.iter().zip(embedding2).map(|(a, b)| a * b).sum();
        let norm1: f64 = embedding1.iter().map(|x| x * x).sum::<f64>().sqrt();
        let norm2: f64 = embedding2.iter().map(|x| x * x).sum::<f64>().sqrt();

        if norm1 == 0.0 || norm2 == 0.0 {
            0.0
        } else {
            dot_product / (norm1 * norm2)
        }
    }

    fn update_stats(&mut self) {
        self.stats.total_fragments = self.fragments.len();

        // Calculate memory usage (simplified estimation)
        self.stats.memory_usage_bytes = self.fragments
            .values()
            .map(|fragment| {
                fragment.field_data.len() * 8 + // f64 data
                fragment.similarity_embedding.len() * 8 + // embedding data
                100 // estimated metadata size
            })
            .sum();

        if self.stats.total_fragments > 0 {
            self.stats.average_fragment_size = self.stats.memory_usage_bytes as f64 / self.stats.total_fragments as f64;
        }

        // Find oldest fragment
        if let Some(oldest_fragment) = self.fragments.values().min_by_key(|f| f.timestamp) {
            let age_duration = Utc::now().signed_duration_since(oldest_fragment.timestamp);
            self.stats.oldest_fragment_age_days = age_duration.num_days() as f64;
        }

        // Find most accessed fragment
        if let Some(most_accessed) = self.fragments.values().max_by_key(|f| f.access_count) {
            self.stats.most_accessed_fragment_id = Some(most_accessed.id.clone());
        }

        // Update cache hit rate (simplified)
        self.stats.cache_hit_rate = 85.0; // Placeholder - implement proper cache hit tracking
    }
}

/// Export data structure for memory fragments
#[derive(Debug, Clone, Serialize, Deserialize)]
struct ExportData {
    session_id: String,
    fragments: Vec<MemoryFragment>,
    stats: MemoryStats,
    exported_at: DateTime<Utc>,
}

