    let variance = data.iter().map(|&x| (x - mean).powi(2)).sum::<f64>() / data.len() as f64;

    // Normalize to [0, 1] range
    (variance / (variance + 1.0)).min(1.0).max(0.0)
}

fn calculate_spatial_correlation(data: &[f64], dimensions: &[usize]) -> f64 {
    if data.len() < 4 || dimensions.is_empty() {
