    // Calculate nearest neighbor correlation
    let mut correlation_sum = 0.0;
    let mut count = 0;

    if dimensions.len() >= 2 {
        for y in 0..(dimensions[1] - 1) {
            for x in 0..(dimensions[0] - 1) {
                let idx = y * dimensions[0] + x;
                let idx_right = idx + 1;
                let idx_down = idx + dimensions[0];

                if idx_down < data.len() && idx_right < data.len() {
                    correlation_sum += data[idx] * data[idx_right]; // Horizontal correlation
                    correlation_sum += data[idx] * data[idx_down];  // Vertical correlation
                    count += 2;
