//! Resonance Scaffold System
//! 
//! Advanced resonance patterns and field amplification for
//! coherent neural field dynamics.

use serde::{Deserialize, Serialize};
use anyhow::{Result, anyhow};
use std::f64::consts::PI;

use crate::neural_field::{NeuralField, AttractorConfig};

/// Resonance configuration parameters
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ResonanceConfig {
    pub frequency: f64,
    pub amplitude: f64,
    pub coherence_threshold: Option<f64>,
    pub phase_offset: Option<f64>,
    pub decay_rate: Option<f64>,
    pub harmonic_orders: Vec<u32>,
    pub spatial_pattern: SpatialPattern,
    pub temporal_evolution: TemporalEvolution,
}

impl Default for ResonanceConfig {
    fn default() -> Self {
        ResonanceConfig {
            frequency: 2.3,
            amplitude: 0.5,
            coherence_threshold: Some(0.8),
            phase_offset: Some(0.0),
            decay_rate: Some(0.05),
            harmonic_orders: vec![1, 2, 3],
            spatial_pattern: SpatialPattern::Radial,
            temporal_evolution: TemporalEvolution::Sinusoidal,
        }
    }
}

/// Spatial patterns for resonance application
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SpatialPattern {
    Uniform,        // Uniform field application
    Radial,         // Radial pattern from center
    Spiral,         // Spiral wave pattern
    Standing,       // Standing wave pattern
    Traveling,      // Traveling wave pattern
    Custom(Vec<f64>), // Custom spatial weights
}

/// Temporal evolution patterns
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum TemporalEvolution {
    Sinusoidal,     // Standard sine wave
    Square,         // Square wave pattern
    Sawtooth,       // Sawtooth wave pattern
    Exponential,    // Exponential decay/growth
    Gaussian,       // Gaussian pulse
    Chirp,          // Frequency-swept chirp
}

/// Resonance analysis results
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ResonanceAnalysis {
    pub dominant_frequency: f64,
    pub resonance_strength: f64,
    pub coherence_amplification: f64,
    pub spatial_correlation: f64,
    pub temporal_stability: f64,
    pub harmonic_content: Vec<HarmonicComponent>,
    pub phase_coherence: f64,
}

/// Harmonic component information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HarmonicComponent {
    pub order: u32,
    pub amplitude: f64,
    pub phase: f64,
    pub frequency: f64,
}

/// Apply resonance scaffold to neural field
pub fn apply_resonance_scaffold(field: &mut NeuralField, config: ResonanceConfig) -> Result<()> {
    validate_resonance_config(&config)?;

    let dimensions = field.dimensions();
    if dimensions.is_empty() {
        return Err(anyhow!("Cannot apply resonance to empty field"));
    }

    // Clear existing attractors to start fresh
    field.clear_attractors();

    // Generate spatial resonance pattern
    let spatial_weights = generate_spatial_pattern(&dimensions, &config.spatial_pattern)?;

    // Create resonance attractors based on pattern
    create_resonance_attractors(field, &config, &spatial_weights)?;

    // Apply temporal evolution
    apply_temporal_evolution(field, &config)?;

    Ok(())
}

/// Generate resonance analysis for a field
pub fn analyze_resonance(field: &mut NeuralField) -> Result<ResonanceAnalysis> {
    let field_data = field.get_field_data();

    if field_data.is_empty() {
        return Err(anyhow!("Cannot analyze empty field"));
    }

    // Perform FFT analysis (simplified implementation)
    let dominant_frequency = find_dominant_frequency(&field_data)?;
    let resonance_strength = calculate_resonance_strength(&field_data);
    let coherence_amplification = field.calculate_coherence()?;

    // Spatial correlation analysis
    let spatial_correlation = calculate_spatial_correlation(&field_data, &field.dimensions());

    // Temporal stability (simplified)
    let temporal_stability = 0.8; // Placeholder - would analyze field evolution over time

    // Harmonic analysis
    let harmonic_content = analyze_harmonics(&field_data, dominant_frequency);

    // Phase coherence
    let phase_coherence = calculate_phase_coherence(&field_data);

    Ok(ResonanceAnalysis {
        dominant_frequency,
        resonance_strength,
        coherence_amplification,
        spatial_correlation,
        temporal_stability,
        harmonic_content,
        phase_coherence,
    })
}

/// Create optimized resonance configuration based on field analysis
pub fn optimize_resonance_config(field: &mut NeuralField, target_coherence: f64) -> Result<ResonanceConfig> {
    let analysis = analyze_resonance(field)?;

    // Start with default config
    let mut config = ResonanceConfig::default();

    // Optimize frequency based on dominant frequency
    if analysis.dominant_frequency > 0.0 {
        config.frequency = analysis.dominant_frequency * 1.1; // Slightly above dominant
    }

    // Adjust amplitude based on current resonance strength
    config.amplitude = if analysis.resonance_strength < 0.5 {
        0.8 // Higher amplitude for weak resonance
    } else {
        0.3 // Lower amplitude for strong resonance
    };

    // Set coherence threshold
    config.coherence_threshold = Some(target_coherence);

    // Choose spatial pattern based on spatial correlation
    config.spatial_pattern = if analysis.spatial_correlation > 0.7 {
        SpatialPattern::Standing // Use standing waves for high correlation
    } else {
        SpatialPattern::Radial // Use radial pattern for low correlation
    };

    // Choose temporal evolution based on stability
    config.temporal_evolution = if analysis.temporal_stability > 0.8 {
        TemporalEvolution::Sinusoidal // Stable systems can use smooth evolution
    } else {
        TemporalEvolution::Exponential // Unstable systems need exponential control
    };

    Ok(config)
}

