fn validate_resonance_config(config: &ResonanceConfig) -> Result<()> {
    if config.frequency <= 0.0 {
        return Err(anyhow!("Resonance frequency must be positive"));
    }

    if config.amplitude < 0.0 {
        return Err(anyhow!("Resonance amplitude cannot be negative"));
    }

    if let Some(threshold) = config.coherence_threshold {
        if threshold < 0.0 || threshold > 1.0 {
            return Err(anyhow!("Coherence threshold must be between 0 and 1"));
        }
    }

    Ok(())
