    let total_size: usize = dimensions.iter().product();
    let mut weights = vec![1.0; total_size];

    match pattern {
        SpatialPattern::Uniform => {
            // Weights are already uniform (all 1.0)
        }
        SpatialPattern::Radial => {
            generate_radial_pattern(&mut weights, dimensions)?;
        }
        SpatialPattern::Spiral => {
            generate_spiral_pattern(&mut weights, dimensions)?;
        }
        SpatialPattern::Standing => {
            generate_standing_wave_pattern(&mut weights, dimensions)?;
        }
        SpatialPattern::Traveling => {
            generate_traveling_wave_pattern(&mut weights, dimensions)?;
        }
        SpatialPattern::Custom(custom_weights) => {
            if custom_weights.len() != total_size {
                return Err(anyhow!("Custom pattern size mismatch"));
            }
            weights = custom_weights.clone();
        }
    }

    Ok(weights)
}

fn generate_radial_pattern(weights: &mut [f64], dimensions: &[usize]) -> Result<()> {
    if dimensions.len() < 2 {
        return Ok(()); // 1D case - keep uniform
    }

    let center_x = dimensions[0] as f64 / 2.0;
    let center_y = dimensions[1] as f64 / 2.0;
    let max_radius = (center_x.powi(2) + center_y.powi(2)).sqrt();

    let mut index = 0;
    for y in 0..dimensions[1] {
        for x in 0..dimensions[0] {
            let dx = x as f64 - center_x;
            let dy = y as f64 - center_y;
            let radius = (dx.powi(2) + dy.powi(2)).sqrt();
            let normalized_radius = radius / max_radius;

            // Radial falloff pattern
            weights[index] = (1.0 - normalized_radius).max(0.1);
            index += 1;
        }
    }

    Ok(())
}

fn generate_spiral_pattern(weights: &mut [f64], dimensions: &[usize]) -> Result<()> {
    if dimensions.len() < 2 {
        return Ok(());
    }

    let center_x = dimensions[0] as f64 / 2.0;
    let center_y = dimensions[1] as f64 / 2.0;
