//! Neural Field Visualization System
//! 
//! Real-time visualization data generation for neural field states,
//! protocol execution, and semantic space navigation.

use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use anyhow::{Result, anyhow};

use crate::neural_field::NeuralField;

/// Visualization configuration parameters
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VisualizationConfig {
    pub visualization_type: VisualizationType,
    pub resolution: Resolution,
    pub color_scheme: ColorScheme,
    pub show_attractors: bool,
    pub show_trajectories: bool,
    pub show_field_lines: bool,
    pub show_energy_contours: bool,
    pub animation_frame_rate: u32,
    pub temporal_window_size: usize,
    pub intensity_scaling: f64,
    pub transparency: f64,
}

impl Default for VisualizationConfig {
    fn default() -> Self {
        VisualizationConfig {
            visualization_type: VisualizationType::Heatmap,
            resolution: Resolution::Medium,
            color_scheme: ColorScheme::Viridis,
            show_attractors: true,
            show_trajectories: false,
            show_field_lines: false,
            show_energy_contours: false,
            animation_frame_rate: 30,
            temporal_window_size: 100,
            intensity_scaling: 1.0,
            transparency: 1.0,
        }
    }
}

/// Types of visualizations available
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum VisualizationType {
    Heatmap,        // 2D heatmap of field intensity
    Contour,        // Contour lines of field values
    Surface3D,      // 3D surface plot
    VectorField,    // Vector field arrows
    Streamlines,    // Field flow lines
    Particles,      // Particle-based visualization
    Holographic,    // Holographic projection
    QuantumCloud,   // Quantum probability cloud
}

/// Resolution settings for visualization
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum Resolution {
    Low,      // 64x64 or equivalent
    Medium,   // 128x128 or equivalent  
    High,     // 256x256 or equivalent
    Ultra,    // 512x512 or equivalent
    Custom(u32, u32), // Custom resolution
}

impl Resolution {
    pub fn to_dimensions(&self) -> (u32, u32) {
        match self {
            Resolution::Low => (64, 64),
            Resolution::Medium => (128, 128),
            Resolution::High => (256, 256),
            Resolution::Ultra => (512, 512),
            Resolution::Custom(w, h) => (*w, *h),
        }
    }
}

/// Color schemes for visualization
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ColorScheme {
    Viridis,    // Purple-blue-green-yellow
    Plasma,     // Purple-pink-yellow
    Inferno,    // Black-red-yellow
    Magma,      // Black-purple-pink-yellow
    Grayscale,  // Black to white
    Rainbow,    // Full spectrum
    Ocean,      // Blue gradients
    Thermal,    // Heat colors
    Quantum,    // Quantum-inspired colors
    Neural,     // Neural network inspired
}

/// Generated visualization data
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VisualizationData {
    pub config: VisualizationConfig,
    pub field_data: FieldVisualizationData,
    pub attractors: Vec<AttractorVisualization>,
    pub trajectories: Vec<TrajectoryVisualization>,
    pub metadata: VisualizationMetadata,
    pub animation_frames: Vec<AnimationFrame>,
}

/// Field visualization data
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct FieldVisualizationData {
    pub dimensions: Vec<u32>,
    pub data_points: Vec<DataPoint>,
    pub intensity_range: (f64, f64),
    pub color_mapping: Vec<ColorRGBA>,
    pub mesh_data: Option<MeshData>,
}

/// Individual data point for visualization
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DataPoint {
    pub position: Vec<f64>,
    pub value: f64,
    pub color: ColorRGBA,
    pub normal: Option<Vec<f64>>, // For 3D visualization
}

/// RGBA color representation
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ColorRGBA {
    pub r: u8,
    pub g: u8,
    pub b: u8,
    pub a: u8,
}

impl ColorRGBA {
    pub fn new(r: u8, g: u8, b: u8, a: u8) -> Self {
        ColorRGBA { r, g, b, a }
    }

    pub fn from_hsv(h: f64, s: f64, v: f64, a: u8) -> Self {
        let c = v * s;
        let x = c * (1.0 - ((h / 60.0) % 2.0 - 1.0).abs());
        let m = v - c;

        let (r_prime, g_prime, b_prime) = if h < 60.0 {
            (c, x, 0.0)
        } else if h < 120.0 {
            (x, c, 0.0)
        } else if h < 180.0 {
            (0.0, c, x)
        } else if h < 240.0 {
            (0.0, x, c)
        } else if h < 300.0 {
            (x, 0.0, c)
        } else {
            (c, 0.0, x)
        };

        ColorRGBA {
            r: ((r_prime + m) * 255.0) as u8,
            g: ((g_prime + m) * 255.0) as u8,
            b: ((b_prime + m) * 255.0) as u8,
            a,
        }
    }
}

/// Mesh data for 3D visualization
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MeshData {
    pub vertices: Vec<Vec<f64>>,
    pub faces: Vec<Vec<u32>>,
    pub normals: Vec<Vec<f64>>,
    pub texture_coordinates: Vec<Vec<f64>>,
}

/// Attractor visualization data
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AttractorVisualization {
    pub position: Vec<f64>,
    pub strength: f64,
    pub radius: f64,
    pub color: ColorRGBA,
    pub pulse_phase: f64,
    pub influence_field: Vec<DataPoint>,
}

/// Trajectory visualization data
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TrajectoryVisualization {
    pub path_points: Vec<Vec<f64>>,
    pub velocities: Vec<Vec<f64>>,
    pub colors: Vec<ColorRGBA>,
    pub thickness: f64,
    pub opacity: f64,
}

/// Visualization metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VisualizationMetadata {
    pub field_name: String,
    pub timestamp: String,
    pub total_energy: f64,
    pub coherence: f64,
    pub entropy: f64,
    pub dominant_frequency: Option<f64>,
    pub render_time_ms: u64,
    pub frame_number: u32,
}

/// Animation frame data
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AnimationFrame {
    pub frame_index: u32,
    pub timestamp: f64,
    pub field_snapshot: Vec<f64>,
    pub attractor_states: Vec<AttractorVisualization>,
    pub energy: f64,
}

/// Generate visualization data from neural field
pub fn generate_visualization_data(
    field: &mut NeuralField,
    config: VisualizationConfig,
) -> Result<VisualizationData> {
    let start_time = std::time::Instant::now();

    // Generate field visualization data
    let field_vis_data = generate_field_data(field, &config)?;

    // Generate attractor visualizations
    let attractor_vis = generate_attractor_visualizations(field, &config);

    // Generate trajectories if requested
    let trajectories = if config.show_trajectories {
        generate_trajectory_visualizations(field, &config)?
    } else {
        Vec::new()
    };

    // Generate metadata
    let metadata = VisualizationMetadata {
        field_name: "neural_field".to_string(), // In production, get actual field name
        timestamp: chrono::Utc::now().to_rfc3339(),
        total_energy: field.calculate_energy()?,
        coherence: field.calculate_coherence()?,
        entropy: field.calculate_entropy()?,
        dominant_frequency: None, // Would be calculated from field analysis
        render_time_ms: start_time.elapsed().as_millis() as u64,
        frame_number: 0,
    };

    // Generate animation frames if needed
    let animation_frames = if config.animation_frame_rate > 0 {
        generate_animation_frames(field, &config)?
    } else {
        Vec::new()
    };

    Ok(VisualizationData {
        config,
        field_data: field_vis_data,
        attractors: attractor_vis,
        trajectories,
        metadata,
        animation_frames,
    })
}

/// Generate optimized visualization for web rendering
pub fn generate_web_visualization(
    field: &mut NeuralField,
    viewport_width: u32,
    viewport_height: u32,
) -> Result<WebVisualizationData> {
    let config = VisualizationConfig {
        resolution: Resolution::Custom(viewport_width / 4, viewport_height / 4), // Optimize for web
        visualization_type: VisualizationType::Heatmap,
        color_scheme: ColorScheme::Viridis,
        show_attractors: true,
        transparency: 0.8,
        ..Default::default()
    };

    let full_data = generate_visualization_data(field, config)?;

    // Convert to web-optimized format
    let web_data = WebVisualizationData {
        image_data: convert_to_image_data(&full_data.field_data)?,
        attractors: full_data.attractors.into_iter().map(convert_attractor_to_web).collect(),
        stats: WebStats {
            energy: full_data.metadata.total_energy,
            coherence: full_data.metadata.coherence,
            num_attractors: full_data.attractors.len() as u32,
        },
    };

    Ok(web_data)
}

/// Web-optimized visualization data
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WebVisualizationData {
    pub image_data: ImageData,
    pub attractors: Vec<WebAttractor>,
    pub stats: WebStats,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ImageData {
    pub width: u32,
    pub height: u32,
    pub pixels: Vec<u8>, // RGBA format
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WebAttractor {
    pub x: f64,
    pub y: f64,
    pub strength: f64,
    pub color: String, // CSS color string
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WebStats {
    pub energy: f64,
    pub coherence: f64,
    pub num_attractors: u32,
}

// Private helper functions

fn generate_field_data(field: &mut NeuralField, config: &VisualizationConfig) -> Result<FieldVisualizationData> {
    let field_data = field.get_field_data();
    let dimensions = field.dimensions();
    let (viz_width, viz_height) = config.resolution.to_dimensions();

    if field_data.is_empty() {
        return Err(anyhow!("Cannot visualize empty field"));
    }

    // Find intensity range
    let min_val = field_data.iter().fold(f64::INFINITY, |a, &b| a.min(b));
    let max_val = field_data.iter().fold(f64::NEG_INFINITY, |a, &b| a.max(b));
    let intensity_range = (min_val, max_val);

    // Generate data points
    let mut data_points = Vec::new();
    let mut color_mapping = Vec::new();

    // Resample field data to visualization resolution
    for y in 0..viz_height {
        for x in 0..viz_width {
            let field_x = (x as f64 / viz_width as f64) * dimensions[0] as f64;
            let field_y = if dimensions.len() > 1 {
                (y as f64 / viz_height as f64) * dimensions[1] as f64
            } else {
                0.0
            };

            // Bilinear interpolation for sampling
            let value = sample_field_value(&field_data, &dimensions, field_x, field_y);

            // Normalize value to [0, 1]
            let normalized_value = if (max_val - min_val).abs() > 1e-10 {
                (value - min_val) / (max_val - min_val)
            } else {
                0.5
            };

            // Apply intensity scaling
            let scaled_value = (normalized_value * config.intensity_scaling).min(1.0).max(0.0);

            // Generate color
            let color = generate_color(scaled_value, &config.color_scheme, config.transparency);

            data_points.push(DataPoint {
                position: vec![x as f64, y as f64],
                value: scaled_value,
                color: color.clone(),
                normal: None,
            });

            color_mapping.push(color);
        }
    }

    // Generate mesh data for 3D visualization if needed
    let mesh_data = if matches!(config.visualization_type, VisualizationType::Surface3D) {
        Some(generate_mesh_data(&data_points, viz_width, viz_height)?)
    } else {
        None
    };

    Ok(FieldVisualizationData {
        dimensions: vec![viz_width, viz_height],
        data_points,
        intensity_range,
        color_mapping,
        mesh_data,
    })
}

fn generate_attractor_visualizations(field: &NeuralField, config: &VisualizationConfig) -> Vec<AttractorVisualization> {
    if !config.show_attractors {
        return Vec::new();
    }

    let attractors = field.get_attractors();
    let dimensions = field.dimensions();

    attractors.iter().enumerate().map(|(i, attractor)| {
        // Generate color based on attractor properties
        let hue = (i as f64 * 60.0) % 360.0; // Spread hues evenly
        let saturation = 0.8;
        let value = (attractor.strength.abs() * 0.5 + 0.5).min(1.0);
        let alpha = (config.transparency * 255.0) as u8;

        let color = ColorRGBA::from_hsv(hue, saturation, value, alpha);

        // Generate influence field visualization
        let influence_field = generate_attractor_influence_field(attractor, &dimensions);

        AttractorVisualization {
            position: attractor.position.clone(),
            strength: attractor.strength,
            radius: attractor.radius,
            color,
            pulse_phase: 0.0, // Would be animated over time
            influence_field,
        }
    }).collect()
}

fn generate_trajectory_visualizations(field: &NeuralField, config: &VisualizationConfig) -> Result<Vec<TrajectoryVisualization>> {
    let mut trajectories = Vec::new();
    let dimensions = field.dimensions();

    // Generate a few sample trajectories
    for i in 0..5 {
        let start_x = dimensions[0] as f64 * (i as f64 + 1.0) / 6.0;
        let start_y = if dimensions.len() > 1 { dimensions[1] as f64 / 2.0 } else { 0.0 };
        let start_position = vec![start_x, start_y];

        let trajectory = simulate_trajectory(start_position, field, 50)?;
        trajectories.push(trajectory);
    }

    Ok(trajectories)
}

fn generate_animation_frames(field: &mut NeuralField, config: &VisualizationConfig) -> Result<Vec<AnimationFrame>> {
    let mut frames = Vec::new();
    let num_frames = config.temporal_window_size.min(50); // Limit for performance

    for frame_idx in 0..num_frames {
        let field_snapshot = field.get_field_data();
        let attractor_states = generate_attractor_visualizations(field, config);
        let energy = field.calculate_energy()?;

        frames.push(AnimationFrame {
            frame_index: frame_idx as u32,
            timestamp: frame_idx as f64 / config.animation_frame_rate as f64,
            field_snapshot,
            attractor_states,
            energy,
        });

        // Evolve field for next frame
        field.evolve(1)?;
    }

    Ok(frames)
}

fn sample_field_value(data: &[f64], dimensions: &[usize], x: f64, y: f64) -> f64 {
    if dimensions.is_empty() || data.is_empty() {
        return 0.0;
    }

    let x = x.max(0.0).min(dimensions[0] as f64 - 1.0);
    let y = if dimensions.len() > 1 {
        y.max(0.0).min(dimensions[1] as f64 - 1.0)
    } else {
        0.0
    };

    if dimensions.len() == 1 {
        // 1D case
        let idx = x as usize;
        return data[idx.min(data.len() - 1)];
    }

    // 2D bilinear interpolation
    let x0 = x.floor() as usize;
    let x1 = (x0 + 1).min(dimensions[0] - 1);
    let y0 = y.floor() as usize;
    let y1 = (y0 + 1).min(dimensions[1] - 1);

    let dx = x - x0 as f64;
    let dy = y - y0 as f64;

    let idx00 = y0 * dimensions[0] + x0;
    let idx01 = y0 * dimensions[0] + x1;
    let idx10 = y1 * dimensions[0] + x0;
    let idx11 = y1 * dimensions[0] + x1;

    if idx11 >= data.len() {
        return data[idx00.min(data.len() - 1)];
    }

    let val00 = data[idx00];
    let val01 = data[idx01];
    let val10 = data[idx10];
    let val11 = data[idx11];

    let val0 = val00 * (1.0 - dx) + val01 * dx;
    let val1 = val10 * (1.0 - dx) + val11 * dx;

    val0 * (1.0 - dy) + val1 * dy
}

fn generate_color(value: f64, scheme: &ColorScheme, transparency: f64) -> ColorRGBA {
    let alpha = (transparency * 255.0) as u8;

    match scheme {
        ColorScheme::Viridis => {
            // Viridis color map approximation
            let r = (255.0 * (0.267 + 0.005 * value + 0.322 * value * value)) as u8;
            let g = (255.0 * (0.004 + 0.443 * value + 0.211 * value * value)) as u8;
            let b = (255.0 * (0.329 + 0.184 * value + 0.074 * value * value)) as u8;
            ColorRGBA::new(r, g, b, alpha)
        }
        ColorScheme::Plasma => {
            // Plasma color map approximation
            let r = (255.0 * (0.050 + 0.839 * value)) as u8;
            let g = (255.0 * (0.030 + 0.718 * value - 0.827 * value * value)) as u8;
            let b = (255.0 * (0.527 + 0.166 * value - 0.482 * value * value)) as u8;
            ColorRGBA::new(r, g, b, alpha)
        }
        ColorScheme::Grayscale => {
            let intensity = (value * 255.0) as u8;
            ColorRGBA::new(intensity, intensity, intensity, alpha)
        }
        ColorScheme::Rainbow => {
            let hue = value * 300.0; // 0 to 300 degrees (purple to red)
            ColorRGBA::from_hsv(hue, 1.0, 1.0, alpha)
        }
        ColorScheme::Ocean => {
            let r = (255.0 * (0.1 + 0.2 * value)) as u8;
            let g = (255.0 * (0.3 + 0.4 * value)) as u8;
            let b = (255.0 * (0.6 + 0.4 * value)) as u8;
            ColorRGBA::new(r, g, b, alpha)
        }
        ColorScheme::Thermal => {
            if value < 0.5 {
                let t = value * 2.0;
                let r = (255.0 * t) as u8;
                let g = 0;
                let b = 0;
                ColorRGBA::new(r, g, b, alpha)
            } else {
                let t = (value - 0.5) * 2.0;
                let r = 255;
                let g = (255.0 * t) as u8;
                let b = 0;
                ColorRGBA::new(r, g, b, alpha)
            }
        }
        ColorScheme::Neural => {
            // Neural network inspired colors
            let r = (255.0 * (0.2 + 0.8 * (1.0 - (-5.0 * value).exp()))) as u8;
            let g = (255.0 * (0.1 + 0.6 * value)) as u8;
            let b = (255.0 * (0.8 - 0.6 * value)) as u8;
            ColorRGBA::new(r, g, b, alpha)
        }
        _ => {
            // Default to grayscale
            let intensity = (value * 255.0) as u8;
            ColorRGBA::new(intensity, intensity, intensity, alpha)
        }
    }
}

fn generate_mesh_data(data_points: &[DataPoint], width: u32, height: u32) -> Result<MeshData> {
    let mut vertices = Vec::new();
    let mut faces = Vec::new();
    let mut normals = Vec::new();
    let mut texture_coordinates = Vec::new();

    // Generate vertices
    for point in data_points {
        if point.position.len() >= 2 {
            vertices.push(vec![
                point.position[0],
                point.position[1],
                point.value * 10.0, // Z height based on field value
            ]);

            // Texture coordinates
            texture_coordinates.push(vec![
                point.position[0] / width as f64,
                point.position[1] / height as f64,
            ]);
        }
    }

    // Generate faces (triangles)
    for y in 0..(height - 1) {
        for x in 0..(width - 1) {
            let idx = y * width + x;

            // Two triangles per quad
            faces.push(vec![idx, idx + 1, idx + width]);
            faces.push(vec![idx + 1, idx + width + 1, idx + width]);
        }
    }

    // Generate normals (simplified)
    for _ in 0..vertices.len() {
        normals.push(vec![0.0, 0.0, 1.0]); // Placeholder - calculate proper normals
    }

    Ok(MeshData {
        vertices,
        faces,
        normals,
        texture_coordinates,
    })
}

fn generate_attractor_influence_field(
    attractor: &crate::neural_field::AttractorConfig,
    dimensions: &[usize],
) -> Vec<DataPoint> {
    let mut influence_points = Vec::new();
    let resolution = 20; // 20x20 grid around attractor

    for y in 0..resolution {
        for x in 0..resolution {
            let pos_x = attractor.position[0] + (x as f64 - resolution as f64 / 2.0) * 0.5;
            let pos_y = if attractor.position.len() > 1 {
                attractor.position[1] + (y as f64 - resolution as f64 / 2.0) * 0.5
            } else {
                0.0
            };

            let distance = ((pos_x - attractor.position[0]).powi(2) + 
                           (pos_y - attractor.position.get(1).unwrap_or(&0.0)).powi(2)).sqrt();

            let influence = attractor.strength * (-distance / attractor.radius).exp();
            let normalized_influence = (influence.abs() / attractor.strength.abs()).min(1.0);

            let color = generate_color(normalized_influence, &ColorScheme::Ocean, 0.5);

            influence_points.push(DataPoint {
                position: vec![pos_x, pos_y],
                value: normalized_influence,
                color,
                normal: None,
            });
        }
    }

    influence_points
}

fn simulate_trajectory(
    start_position: Vec<f64>,
    field: &NeuralField,
    num_steps: usize,
) -> Result<TrajectoryVisualization> {
    let mut path_points = Vec::new();
    let mut velocities = Vec::new();
    let mut colors = Vec::new();

    let mut current_pos = start_position;
    path_points.push(current_pos.clone());

    for step in 0..num_steps {
        // Simplified trajectory simulation - calculate field gradient at current position
        let gradient = calculate_field_gradient(&current_pos, field)?;

        // Update position based on gradient
        for i in 0..current_pos.len() {
            current_pos[i] += gradient[i] * 0.1; // Small step size
        }

        path_points.push(current_pos.clone());
        velocities.push(gradient.clone());

        // Color based on step (fade from blue to red)
        let t = step as f64 / num_steps as f64;
        let color = ColorRGBA::from_hsv(240.0 * (1.0 - t), 0.8, 0.9, 200);
        colors.push(color);
    }

    Ok(TrajectoryVisualization {
        path_points,
        velocities,
        colors,
        thickness: 2.0,
        opacity: 0.8,
    })
}

fn calculate_field_gradient(position: &[f64], field: &NeuralField) -> Result<Vec<f64>> {
    // Simplified gradient calculation
    let dimensions = field.dimensions();
    let mut gradient = vec![0.0; position.len()];

    // Use attractors to approximate gradient
    for attractor in field.get_attractors() {
        let mut distance_vec = Vec::new();
        let mut distance = 0.0;

        for (i, (&pos, &attr_pos)) in position.iter().zip(attractor.position.iter()).enumerate() {
            let diff = attr_pos - pos;
            distance_vec.push(diff);
            distance += diff * diff;
        }
        distance = distance.sqrt();

        if distance > 1e-10 {
            let force_magnitude = attractor.strength * (-distance / attractor.radius).exp();
            for (i, &diff) in distance_vec.iter().enumerate() {
                gradient[i] += force_magnitude * diff / distance;
            }
        }
    }

    Ok(gradient)
}

fn convert_to_image_data(field_data: &FieldVisualizationData) -> Result<ImageData> {
    let width = field_data.dimensions[0];
    let height = field_data.dimensions[1];
    let mut pixels = Vec::with_capacity((width * height * 4) as usize);

    for point in &field_data.data_points {
        pixels.push(point.color.r);
        pixels.push(point.color.g);
        pixels.push(point.color.b);
        pixels.push(point.color.a);
    }

    Ok(ImageData {
        width,
        height,
        pixels,
    })
}

fn convert_attractor_to_web(attractor: AttractorVisualization) -> WebAttractor {
    WebAttractor {
        x: attractor.position.get(0).copied().unwrap_or(0.0),
        y: attractor.position.get(1).copied().unwrap_or(0.0),
        strength: attractor.strength,
        color: format!("rgba({}, {}, {}, {})", 
                      attractor.color.r, 
                      attractor.color.g, 
                      attractor.color.b, 
                      attractor.color.a as f64 / 255.0),
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::neural_field::NeuralField;

    #[test]
    fn test_visualization_config() {
        let config = VisualizationConfig::default();
        assert!(matches!(config.visualization_type, VisualizationType::Heatmap));
        assert!(matches!(config.color_scheme, ColorScheme::Viridis));
    }

    #[test]
    fn test_resolution_conversion() {
        let resolution = Resolution::Medium;
        assert_eq!(resolution.to_dimensions(), (128, 128));

        let custom = Resolution::Custom(640, 480);
        assert_eq!(custom.to_dimensions(), (640, 480));
    }

    #[test]
    fn test_color_generation() {
        let color = generate_color(0.5, &ColorScheme::Grayscale, 1.0);
        assert_eq!(color.a, 255);

        let rainbow_color = generate_color(0.3, &ColorScheme::Rainbow, 0.8);
        assert_eq!(rainbow_color.a, 204); // 0.8 * 255
    }

    #[test]
    fn test_visualization_data_generation() {
        let mut field = NeuralField::new(vec![32, 32]).unwrap();
        let config = VisualizationConfig {
            resolution: Resolution::Low,
            ..Default::default()
        };

        let vis_data = generate_visualization_data(&mut field, config);
        assert!(vis_data.is_ok());

        let data = vis_data.unwrap();
        assert!(!data.field_data.data_points.is_empty());
    }
}

