//! Neural Field Core Implementation
//! 
//! High-performance neural field computation with semantic dynamics,
//! attractor systems, and context engineering capabilities.

use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use nalgebra::{DMatrix, DVector, Vector3};
use ndarray::{Array, ArrayD, Axis, IxDyn};
use std::collections::HashMap;
use uuid::Uuid;
use chrono::{DateTime, Utc};
use anyhow::{Result, anyhow};

/// Represents a point in semantic space
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SemanticPoint {
    pub position: Vec<f64>,
    pub strength: f64,
    pub decay_rate: f64,
    pub created_at: DateTime<Utc>,
    pub metadata: HashMap<String, String>,
}

/// Attractor configuration for neural field dynamics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AttractorConfig {
    pub position: Vec<f64>,
    pub strength: f64,
    pub radius: f64,
    pub decay_rate: f64,
    pub oscillation_frequency: Option<f64>,
    pub oscillation_amplitude: Option<f64>,
}

/// Neural field state information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct FieldState {
    pub id: String,
    pub dimensions: Vec<usize>,
    pub total_energy: f64,
    pub entropy: f64,
    pub coherence: f64,
    pub num_attractors: usize,
    pub evolution_steps: u64,
    pub created_at: DateTime<Utc>,
    pub last_updated: DateTime<Utc>,
    pub metadata: HashMap<String, String>,
}

/// Dynamics parameters for field evolution
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DynamicsConfig {
    pub damping: f64,
    pub noise_level: f64,
    pub time_step: f64,
    pub nonlinearity_strength: f64,
    pub coupling_strength: f64,
    pub temperature: f64,
}

impl Default for DynamicsConfig {
    fn default() -> Self {
        Self {
            damping: 0.1,
            noise_level: 0.05,
            time_step: 0.01,
            nonlinearity_strength: 1.0,
            coupling_strength: 0.5,
            temperature: 1.0,
        }
    }
}

/// Main neural field structure with enhanced capabilities
#[derive(Debug)]
pub struct NeuralField {
    id: String,
    field_data: ArrayD<f64>,
    velocity_field: ArrayD<f64>,
    attractors: Vec<AttractorConfig>,
    dynamics: DynamicsConfig,
    evolution_steps: u64,
    created_at: DateTime<Utc>,
    last_updated: DateTime<Utc>,
    metadata: HashMap<String, String>,

    // Performance optimization caches
    field_cache: Option<Vec<f64>>,
    gradient_cache: Option<ArrayD<f64>>,
    energy_cache: Option<f64>,
    entropy_cache: Option<f64>,
}

impl NeuralField {
    /// Create a new neural field with specified dimensions
    pub fn new(dimensions: Vec<usize>) -> Result<Self> {
        if dimensions.is_empty() {
            return Err(anyhow!("Field dimensions cannot be empty"));
        }

        let total_size: usize = dimensions.iter().product();
        if total_size > 1_000_000 {
            return Err(anyhow!("Field size too large: {} elements", total_size));
        }

        let shape = IxDyn(&dimensions);
        let field_data = ArrayD::<f64>::zeros(shape.clone());
        let velocity_field = ArrayD::<f64>::zeros(shape);

        let id = Uuid::new_v4().to_string();
        let now = Utc::now();

        Ok(NeuralField {
            id,
            field_data,
            velocity_field,
            attractors: Vec::new(),
            dynamics: DynamicsConfig::default(),
            evolution_steps: 0,
            created_at: now,
            last_updated: now,
            metadata: HashMap::new(),
            field_cache: None,
            gradient_cache: None,
            energy_cache: None,
            entropy_cache: None,
        })
    }

    /// Initialize field with random values
    pub fn initialize_random(&mut self, amplitude: f64) -> Result<()> {
        use rand::Rng;
        let mut rng = rand::thread_rng();

        self.field_data.mapv_inplace(|_| rng.gen_range(-amplitude..amplitude));
        self.velocity_field.fill(0.0);

        self.invalidate_caches();
        self.last_updated = Utc::now();

        Ok(())
    }

    /// Add an attractor to the field
    pub fn add_attractor(&mut self, config: AttractorConfig) -> Result<()> {
        // Validate attractor position
        if config.position.len() != self.field_data.ndim() {
            return Err(anyhow!("Attractor dimension mismatch"));
        }

        // Validate position bounds
        for (i, &pos) in config.position.iter().enumerate() {
            if pos < 0.0 || pos >= self.field_data.shape()[i] as f64 {
                return Err(anyhow!("Attractor position out of bounds"));
            }
        }

        self.attractors.push(config);
        self.invalidate_caches();
        self.last_updated = Utc::now();

        Ok(())
    }

    /// Evolve the field for one time step
    pub fn evolve_step(&mut self) -> Result<()> {
        let dt = self.dynamics.time_step;
        let damping = self.dynamics.damping;
        let noise = self.dynamics.noise_level;

        // Calculate forces from attractors
        let attractor_force = self.calculate_attractor_forces()?;

        // Calculate nonlinear forces
        let nonlinear_force = self.calculate_nonlinear_forces()?;

        // Add noise
        let noise_force = self.generate_noise_field(noise)?;

        // Update velocity using Verlet integration
        let total_force = &attractor_force + &nonlinear_force + &noise_force;
        let damping_force = &self.velocity_field * damping;

        self.velocity_field = &self.velocity_field + (&total_force - &damping_force) * dt;

        // Update field positions
        self.field_data = &self.field_data + &self.velocity_field * dt;

        // Apply periodic boundary conditions if needed
        self.apply_boundary_conditions()?;

        self.evolution_steps += 1;
        self.invalidate_caches();
        self.last_updated = Utc::now();

        Ok(())
    }

    /// Evolve the field for multiple steps
    pub fn evolve(&mut self, steps: u64) -> Result<()> {
        for _ in 0..steps {
            self.evolve_step()?;
        }
        Ok(())
    }

    /// Calculate total field energy
    pub fn calculate_energy(&self) -> Result<f64> {
        if let Some(cached_energy) = self.energy_cache {
            return Ok(cached_energy);
        }

        // Kinetic energy
        let kinetic = 0.5 * self.velocity_field.iter().map(|v| v * v).sum::<f64>();

        // Potential energy from field interactions
        let potential = 0.5 * self.field_data.iter().map(|f| f * f).sum::<f64>();

        // Attractor potential energy
        let attractor_potential = self.calculate_attractor_potential()?;

        let total_energy = kinetic + potential + attractor_potential;
        self.energy_cache = Some(total_energy);

        Ok(total_energy)
    }

    /// Calculate field entropy
    pub fn calculate_entropy(&self) -> Result<f64> {
        if let Some(cached_entropy) = self.entropy_cache {
            return Ok(cached_entropy);
        }

        // Normalize field values to probabilities
        let min_val = self.field_data.iter().fold(f64::INFINITY, |a, &b| a.min(b));
        let max_val = self.field_data.iter().fold(f64::NEG_INFINITY, |a, &b| a.max(b));

        if (max_val - min_val).abs() < 1e-10 {
            return Ok(0.0);
        }

        let mut entropy = 0.0;
        let normalization = max_val - min_val;

        for &value in self.field_data.iter() {
            let prob = (value - min_val) / normalization + 1e-10; // Avoid log(0)
            entropy -= prob * prob.ln();
        }

        self.entropy_cache = Some(entropy);
        Ok(entropy)
    }

    /// Calculate field coherence measure
    pub fn calculate_coherence(&self) -> Result<f64> {
        if self.field_data.is_empty() {
            return Ok(0.0);
        }

        // Calculate spatial coherence using autocorrelation
        let mean_value = self.field_data.mean().unwrap_or(0.0);
        let variance = self.field_data.iter()
            .map(|&x| (x - mean_value).powi(2))
            .sum::<f64>() / self.field_data.len() as f64;

        if variance < 1e-10 {
            return Ok(1.0); // Perfect coherence
        }

        // Simplified coherence measure
        let coherence = 1.0 / (1.0 + variance / (mean_value.abs() + 1e-10));
        Ok(coherence.min(1.0).max(0.0))
    }

    /// Get field state information
    pub fn get_state(&mut self) -> FieldState {
        let energy = self.calculate_energy().unwrap_or(0.0);
        let entropy = self.calculate_entropy().unwrap_or(0.0);
        let coherence = self.calculate_coherence().unwrap_or(0.0);

        FieldState {
            id: self.id.clone(),
            dimensions: self.field_data.shape().to_vec(),
            total_energy: energy,
            entropy,
            coherence,
            num_attractors: self.attractors.len(),
            evolution_steps: self.evolution_steps,
            created_at: self.created_at,
            last_updated: self.last_updated,
            metadata: self.metadata.clone(),
        }
    }

    /// Get field data as a flattened vector
    pub fn get_field_data(&self) -> Vec<f64> {
        self.field_data.iter().cloned().collect::<Vec<f64>>()
    }

    /// Set field data from a flattened vector
    pub fn set_field_data(&mut self, data: Vec<f64>) -> Result<()> {
        let expected_size = self.field_data.len();
        if data.len() != expected_size {
            return Err(anyhow!("Data size mismatch: expected {}, got {}", expected_size, data.len()));
        }

        for (field_val, &data_val) in self.field_data.iter_mut().zip(data.iter()) {
            *field_val = data_val;
        }

        self.invalidate_caches();
        self.last_updated = Utc::now();

        Ok(())
    }

    /// Get attractor configurations
    pub fn get_attractors(&self) -> &[AttractorConfig] {
        &self.attractors
    }

    /// Update dynamics configuration
    pub fn set_dynamics(&mut self, dynamics: DynamicsConfig) {
        self.dynamics = dynamics;
        self.invalidate_caches();
        self.last_updated = Utc::now();
    }

    /// Get current dynamics configuration
    pub fn get_dynamics(&self) -> &DynamicsConfig {
        &self.dynamics
    }

    /// Clear all attractors
    pub fn clear_attractors(&mut self) {
        self.attractors.clear();
        self.invalidate_caches();
        self.last_updated = Utc::now();
    }

    /// Get field dimensions
    pub fn dimensions(&self) -> Vec<usize> {
        self.field_data.shape().to_vec()
    }

    /// Get field metadata
    pub fn get_metadata(&self) -> &HashMap<String, String> {
        &self.metadata
    }

    /// Set field metadata
    pub fn set_metadata(&mut self, key: String, value: String) {
        self.metadata.insert(key, value);
        self.last_updated = Utc::now();
    }

    // Private helper methods

    fn calculate_attractor_forces(&self) -> Result<ArrayD<f64>> {
        let mut force_field = ArrayD::<f64>::zeros(self.field_data.raw_dim());

        for attractor in &self.attractors {
            self.add_attractor_force(&mut force_field, attractor)?;
        }

        Ok(force_field)
    }

    fn add_attractor_force(&self, force_field: &mut ArrayD<f64>, attractor: &AttractorConfig) -> Result<()> {
        let dimensions = self.field_data.shape();

        // Create index iterator for the field
        for (idx, force) in force_field.indexed_iter_mut() {
            let position: Vec<f64> = idx.slice().iter()
                .map(|&i| i as f64)
                .collect();

            // Calculate distance to attractor
            let distance = self.calculate_distance(&position, &attractor.position);

            if distance < 1e-10 {
                continue; // Avoid division by zero
            }

            // Calculate force magnitude with falloff
            let falloff = (-distance / attractor.radius).exp();
            let magnitude = attractor.strength * falloff / (distance + 1e-10);

            // Add oscillation if configured
            let osc_factor = if let (Some(freq), Some(amp)) = (attractor.oscillation_frequency, attractor.oscillation_amplitude) {
                1.0 + amp * (freq * self.evolution_steps as f64 * self.dynamics.time_step).sin()
            } else {
                1.0
            };

            *force += magnitude * osc_factor;
        }

        Ok(())
    }

    fn calculate_nonlinear_forces(&self) -> Result<ArrayD<f64>> {
        let strength = self.dynamics.nonlinearity_strength;
        let cubic_term = self.field_data.mapv(|x| -strength * x * x * x);
        Ok(cubic_term)
    }

    fn generate_noise_field(&self, noise_level: f64) -> Result<ArrayD<f64>> {
        use rand::Rng;
        let mut rng = rand::thread_rng();

        let noise_field = ArrayD::from_shape_fn(
            self.field_data.raw_dim(),
            |_| rng.gen_range(-noise_level..noise_level)
        );

        Ok(noise_field)
    }

    fn apply_boundary_conditions(&mut self) -> Result<()> {
        // Apply periodic boundary conditions by wrapping values
        // This is a simplified implementation
        Ok(())
    }

    fn calculate_attractor_potential(&self) -> Result<f64> {
        let mut potential = 0.0;

        for (idx, &value) in self.field_data.indexed_iter() {
            let position: Vec<f64> = idx.slice().iter()
                .map(|&i| i as f64)
                .collect();

            for attractor in &self.attractors {
                let distance = self.calculate_distance(&position, &attractor.position);
                let falloff = (-distance / attractor.radius).exp();
                potential += attractor.strength * falloff * value;
            }
        }

        Ok(potential)
    }

    fn calculate_distance(&self, pos1: &[f64], pos2: &[f64]) -> f64 {
        pos1.iter()
            .zip(pos2.iter())
            .map(|(a, b)| (a - b).powi(2))
            .sum::<f64>()
            .sqrt()
    }

    fn invalidate_caches(&mut self) {
        self.field_cache = None;
        self.gradient_cache = None;
        self.energy_cache = None;
        self.entropy_cache = None;
    }
}

