[package]
name = "vimmacs-wasm-runtime"
version = "1.0.0"
edition = "2021"
authors = ["Vimmacs Contributors"]
description = "Wasmer-based WebAssembly runtime for Vimmacs with Arch Linux integration"
license = "MIT"

[dependencies]
# WebAssembly runtime
wasmer = { workspace = true }
wasmer-wasi = { workspace = true }
wasmtime = { workspace = true }
wasmtime-wasi = { workspace = true }

# Async runtime
tokio = { workspace = true }
tokio-tungstenite = { workspace = true }
futures = "0.3"

# Networking and HTTP
axum = { workspace = true }
tower = { workspace = true }
tower-http = { workspace = true }
hyper = "0.14"

# Serialization
serde = { workspace = true }
serde_json = { workspace = true }
bincode = { workspace = true }

# Security and cryptography
ring = { workspace = true }
rustls = { workspace = true }
ed25519-dalek = { workspace = true }
x25519-dalek = { workspace = true }

# Error handling and logging
anyhow = { workspace = true }
thiserror = { workspace = true }
tracing = { workspace = true }
tracing-subscriber = { workspace = true }

# Configuration
figment = { workspace = true }
toml = { workspace = true }

# Time and UUID
chrono = { workspace = true }
uuid = { workspace = true }

# File system
walkdir = "2.4"
notify = { workspace = true }

# System utilities
which = { workspace = true }
dirs = { workspace = true }
nix = "0.27"

# Performance monitoring
metrics = { workspace = true }
metrics-exporter-prometheus = { workspace = true }

# Container orchestration
bollard = "0.15" # Docker API client
k8s-openapi = { version = "0.20", features = ["v1_28"] }
kube = { version = "0.87", features = ["runtime", "derive"] }

# Archive handling
tar = "0.4"
flate2 = "1.0"

# Command execution
subprocess = "0.2"

[features]
default = ["wasmer", "kata", "arch-integration"]
wasmer = ["dep:wasmer", "dep:wasmer-wasi"]
wasmtime = ["dep:wasmtime", "dep:wasmtime-wasi"]
kata = ["dep:bollard"]
arch-integration = []
kubernetes = ["dep:k8s-openapi", "dep:kube"]

[dev-dependencies]
tempfile = "3.8"
criterion = "0.5"

[[bin]]
name = "vimmacs-runtime"
path = "src/main.rs"

