   146‚Üí    pub data_points: Vec<f64>,
   147‚Üí    pub energy: f64,
   148‚Üí    pub entropy: f64,
   149‚Üí    pub coherence: f64,
   150‚Üí    pub num_attractors: i32,
   151‚Üí    pub evolution_steps: u64,
   152‚Üí    pub metadata: String, // JSON string
   153‚Üí}
   154‚Üí
   155‚Üí/// Protocol execution data prepared for export
   156‚Üí#[derive(Debug, Clone)]
   157‚Üípub struct ProtocolExportData {
   158‚Üí    pub execution_id: String,
   159‚Üí    pub protocol_name: String,
   160‚Üí    pub timestamp: DateTime<Utc>,
   161‚Üí    pub duration_ms: u64,
   162‚Üí    pub success: bool,
   163‚Üí    pub energy_change: f64,
   164‚Üí    pub entropy_change: f64,
   165‚Üí    pub coherence_change: f64,
   166‚Üí    pub field_modifications: i32,
   167‚Üí    pub outputs: String, // JSON string
   168‚Üí    pub error_message: Option<String>,
   169‚Üí}
   170‚Üí
   171‚Üí/// Export neural field to Parquet format
   172‚Üípub fn export_field_to_parquet(field: &mut NeuralField) -> Result<Vec<u8>> {
   173‚Üí    let config = ParquetConfig::default();
   174‚Üí    export_field_to_parquet_with_config(field, config)
   175‚Üí}
   176‚Üí
   177‚Üí/// Export neural field to Parquet with custom configuration
   178‚Üípub fn export_field_to_parquet_with_config(field: &mut NeuralField, config: ParquetConfig) -> Result<Vec<u8>> {
   179‚Üí    let start_time = std::time::Instant::now();
   180‚Üí    
   181‚Üí    // Prepare field data for export
   182‚Üí    let field_data = prepare_field_export_data(field)?;
   183‚Üí    
   184‚Üí    // Create Arrow schema for field data
   185‚Üí    let schema = create_field_schema()?;
   186‚Üí    
   187‚Üí    // Create record batch
   188‚Üí    let record_batch = create_field_record_batch(&field_data, schema.clone())?;
   189‚Üí    
   190‚Üí    // Configure writer properties
   191‚Üí    let props = WriterProperties::builder()
   192‚Üí        .set_compression(config.compression.into())
   193‚Üí        .set_encoding(config.encoding.into())
   194‚Üí        .set_writer_version(config.writer_version.into())
   195‚Üí        .set_max_row_group_size(config.row_group_size)
   196‚Üí        .set_statistics_enabled(config.enable_statistics.into())
   197‚Üí        .build();
   198‚Üí    
   199‚Üí    // Write to buffer
   200‚Üí    let buffer = Vec::new();
   201‚Üí    let cursor = Cursor::new(buffer);
   202‚Üí    
   203‚Üí    let mut writer = ArrowWriter::try_new(cursor, schema, Some(props))?;
   204‚Üí    writer.write(&record_batch)?;
   205‚Üí    writer.close()?;
   206‚Üí    
   207‚Üí    let buffer = writer.into_inner()?.into_inner();
   208‚Üí    
   209‚Üí    let duration_ms = start_time.elapsed().as_millis() as u64;
   210‚Üí    
   211‚Üí    #[cfg(feature = "console_log")]
   212‚Üí    web_sys::console::log_1(&format!("Exported field to Parquet: {} bytes in {}ms", buffer.len(), duration_ms).into());
   213‚Üí    
   214‚Üí    Ok(buffer)
   215‚Üí}
   216‚Üí
   217‚Üí/// Export protocol execution results to Parquet
   218‚Üípub fn export_protocol_results_to_parquet(results: &[ProtocolExecutionResult]) -> Result<Vec<u8>> {
   219‚Üí    let config = ParquetConfig::default();
   220‚Üí    export_protocol_results_to_parquet_with_config(results, config)
   221‚Üí}
   222‚Üí
   223‚Üí/// Export protocol execution results with custom configuration
   224‚Üípub fn export_protocol_results_to_parquet_with_config(
   225‚Üí    results: &[ProtocolExecutionResult], 
   226‚Üí    config: ParquetConfig
   227‚Üí) -> Result<Vec<u8>> {
   228‚Üí    let start_time = std::time::Instant::now();
   229‚Üí    
   230‚Üí    if results.is_empty() {
   231‚Üí        return Err(anyhow!("Cannot export empty protocol results"));
   232‚Üí    }
   233‚Üí    
   234‚Üí    // Prepare protocol data for export
   235‚Üí    let protocol_data = prepare_protocol_export_data(results)?;
   236‚Üí    
   237‚Üí    // Create Arrow schema for protocol data
   238‚Üí    let schema = create_protocol_schema()?;
   239‚Üí    
   240‚Üí    // Create record batch
   241‚Üí    let record_batch = create_protocol_record_batch(&protocol_data, schema.clone())?;
   242‚Üí    
   243‚Üí    // Configure writer properties
   244‚Üí    let props = WriterProperties::builder()
   245‚Üí        .set_compression(config.compression.into())
   246‚Üí        .set_encoding(config.encoding.into())
   247‚Üí        .set_writer_version(config.writer_version.into())
   248‚Üí        .set_max_row_group_size(config.row_group_size)
   249‚Üí        .set_statistics_enabled(config.enable_statistics.into())
   250‚Üí        .build();
   251‚Üí    
   252‚Üí    // Write to buffer
   253‚Üí    let buffer = Vec::new();
   254‚Üí    let cursor = Cursor::new(buffer);
   255‚Üí    
   256‚Üí    let mut writer = ArrowWriter::try_new(cursor, schema, Some(props))?;
   257‚Üí    writer.write(&record_batch)?;
   258‚Üí    writer.close()?;
   259‚Üí    
   260‚Üí    let buffer = writer.into_inner()?.into_inner();
   261‚Üí    
   262‚Üí    let duration_ms = start_time.elapsed().as_millis() as u64;
   263‚Üí    
   264‚Üí    #[cfg(feature = "console_log")]
   265‚Üí    web_sys::console::log_1(&format!("Exported {} protocol results to Parquet: {} bytes in {}ms", 
   266‚Üí                                     results.len(), buffer.len(), duration_ms).into());
   267‚Üí    
   268‚Üí    Ok(buffer)
   269‚Üí}
   270‚Üí
   271‚Üí/// Export combined analytics data to Parquet
   272‚Üípub fn export_combined_analytics(
   273‚Üí    fields: &[&mut NeuralField],
   274‚Üí    protocol_results: &[ProtocolExecutionResult],
   275‚Üí) -> Result<Vec<u8>> {
   276‚Üí    let start_time = std::time::Instant::now();
   277‚Üí    
   278‚Üí    // Create combined analytics schema
   279‚Üí    let schema = create_combined_analytics_schema()?;
   280‚Üí    
   281‚Üí    // Prepare combined data
   282‚Üí    let mut combined_records = Vec::new();
   283‚Üí    
   284‚Üí    // Add field data records
   285‚Üí    for field in fields {
   286‚Üí        let field_data = prepare_field_export_data(field)?;
   287‚Üí        combined_records.push(CombinedAnalyticsRecord {
   288‚Üí            record_type: "field_state".to_string(),
   289‚Üí            timestamp: field_data.timestamp,
   290‚Üí            field_name: Some(field_data.field_name),
   291‚Üí            protocol_name: None,
   292‚Üí            numeric_value: field_data.energy,
   293‚Üí            metadata: field_data.metadata,
   294‚Üí        });
   295‚Üí    }
   296‚Üí    
   297‚Üí    // Add protocol execution records
   298‚Üí    for result in protocol_results {
   299‚Üí        combined_records.push(CombinedAnalyticsRecord {
   300‚Üí            record_type: "protocol_execution".to_string(),
   301‚Üí            timestamp: result.created_at,
   302‚Üí            field_name: None,
   303‚Üí            protocol_name: Some(result.protocol_name.clone()),
   304‚Üí            numeric_value: result.duration_ms as f64,
   305‚Üí            metadata: serde_json::to_string(&result.outputs).unwrap_or_default(),
   306‚Üí        });
   307‚Üí    }
   308‚Üí    
   309‚Üí    // Create record batch
   310‚Üí    let record_batch = create_combined_analytics_record_batch(&combined_records, schema.clone())?;
   311‚Üí    
   312‚Üí    // Configure writer properties
   313‚Üí    let props = WriterProperties::builder()
   314‚Üí        .set_compression(Compression::SNAPPY)
   315‚Üí        .set_writer_version(WriterVersion::PARQUET_2_0)
   316‚Üí        .set_statistics_enabled(parquet::basic::LogicalType::String.into())
   317‚Üí        .build();
   318‚Üí    
   319‚Üí    // Write to buffer
   320‚Üí    let buffer = Vec::new();
   321‚Üí    let cursor = Cursor::new(buffer);
   322‚Üí    
   323‚Üí    let mut writer = ArrowWriter::try_new(cursor, schema, Some(props))?;
   324‚Üí    writer.write(&record_batch)?;
   325‚Üí    writer.close()?;
   326‚Üí    
   327‚Üí    let buffer = writer.into_inner()?.into_inner();
   328‚Üí    
   329‚Üí    let duration_ms = start_time.elapsed().as_millis() as u64;
   330‚Üí    
   331‚Üí    #[cfg(feature = "console_log")]
   332‚Üí    web_sys::console::log_1(&format!("Exported combined analytics to Parquet: {} bytes in {}ms", 
   333‚Üí                                     buffer.len(), duration_ms).into());
   334‚Üí    
   335‚Üí    Ok(buffer)
   336‚Üí}
