//! Parquet Export System
//! 
//! High-performance data export to Apache Parquet format for
//! neural field states, protocol execution results, and analytics.

use serde::{Deserialize, Serialize};
use anyhow::{Result, anyhow};
use std::sync::Arc;
use arrow::array::{Array, Float64Array, StringArray, UInt64Array, TimestampMillisecondArray, Int32Array};
use arrow::datatypes::{DataType, Field, Schema, TimeUnit};
use arrow::record_batch::RecordBatch;
use parquet::arrow::arrow_writer::ArrowWriter;
use parquet::basic::{Compression, Encoding};
use parquet::file::properties::{WriterProperties, WriterVersion};
use std::io::Cursor;
use chrono::{DateTime, Utc};
use uuid::Uuid;

use crate::neural_field::{NeuralField, FieldState};
use crate::protocols::ProtocolExecutionResult;

/// Parquet export configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ParquetConfig {
    pub compression: CompressionType,
    pub encoding: EncodingType,
    pub row_group_size: usize,
    pub writer_version: WriterVersionType,
    pub enable_statistics: bool,
    pub enable_bloom_filter: bool,
    pub schema_validation: bool,
}

impl Default for ParquetConfig {
    fn default() -> Self {
        ParquetConfig {
            compression: CompressionType::Snappy,
            encoding: EncodingType::Plain,
            row_group_size: 1000,
            writer_version: WriterVersionType::V2,
            enable_statistics: true,
            enable_bloom_filter: false,
            schema_validation: true,
        }
    }
}

/// Compression types for Parquet export
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum CompressionType {
    Uncompressed,
    Snappy,
    Gzip,
    Lzo,
    Brotli,
    Lz4,
    Zstd,
}

impl From<CompressionType> for Compression {
    fn from(comp_type: CompressionType) -> Self {
        match comp_type {
            CompressionType::Uncompressed => Compression::UNCOMPRESSED,
            CompressionType::Snappy => Compression::SNAPPY,
            CompressionType::Gzip => Compression::GZIP,
            CompressionType::Lzo => Compression::LZO,
            CompressionType::Brotli => Compression::BROTLI,
            CompressionType::Lz4 => Compression::LZ4,
            CompressionType::Zstd => Compression::ZSTD,
        }
    }
}

/// Encoding types for Parquet columns
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum EncodingType {
    Plain,
    Dictionary,
    RunLength,
    BitPacked,
    DeltaBinaryPacked,
    DeltaLengthByteArray,
    DeltaByteArray,
}

impl From<EncodingType> for Encoding {
    fn from(enc_type: EncodingType) -> Self {
        match enc_type {
            EncodingType::Plain => Encoding::PLAIN,
            EncodingType::Dictionary => Encoding::PLAIN_DICTIONARY,
            EncodingType::RunLength => Encoding::RLE,
            EncodingType::BitPacked => Encoding::BIT_PACKED,
            EncodingType::DeltaBinaryPacked => Encoding::DELTA_BINARY_PACKED,
            EncodingType::DeltaLengthByteArray => Encoding::DELTA_LENGTH_BYTE_ARRAY,
            EncodingType::DeltaByteArray => Encoding::DELTA_BYTE_ARRAY,
        }
    }
}

/// Writer version types
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum WriterVersionType {
    V1,
    V2,
}

impl From<WriterVersionType> for WriterVersion {
    fn from(version: WriterVersionType) -> Self {
        match version {
            WriterVersionType::V1 => WriterVersion::PARQUET_1_0,
            WriterVersionType::V2 => WriterVersion::PARQUET_2_0,
        }
    }
}

/// Export statistics and metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExportMetadata {
    pub export_id: String,
    pub timestamp: DateTime<Utc>,
    pub record_count: usize,
    pub file_size_bytes: usize,
    pub schema_version: String,
    pub export_type: ExportType,
    pub compression_ratio: f64,
    pub export_duration_ms: u64,
}

/// Types of data that can be exported
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ExportType {
    FieldState,
    FieldData,
    ProtocolExecution,
    CombinedAnalytics,
    TimeSeries,
}

/// Neural field data prepared for export
#[derive(Debug, Clone)]
pub struct FieldExportData {
    pub field_id: String,
    pub field_name: String,
    pub timestamp: DateTime<Utc>,
    pub dimensions: Vec<i32>,
    pub data_points: Vec<f64>,
    pub energy: f64,
    pub entropy: f64,
    pub coherence: f64,
    pub num_attractors: i32,
    pub evolution_steps: u64,
    pub metadata: String, // JSON string
}

/// Protocol execution data prepared for export
#[derive(Debug, Clone)]
pub struct ProtocolExportData {
    pub execution_id: String,
    pub protocol_name: String,
    pub timestamp: DateTime<Utc>,
    pub duration_ms: u64,
    pub success: bool,
    pub energy_change: f64,
    pub entropy_change: f64,
    pub coherence_change: f64,
    pub field_modifications: i32,
    pub outputs: String, // JSON string
    pub error_message: Option<String>,
}

/// Export neural field to Parquet format
pub fn export_field_to_parquet(field: &mut NeuralField) -> Result<Vec<u8>> {
    let config = ParquetConfig::default();
    export_field_to_parquet_with_config(field, config)
}

/// Export neural field to Parquet with custom configuration
pub fn export_field_to_parquet_with_config(field: &mut NeuralField, config: ParquetConfig) -> Result<Vec<u8>> {
    let start_time = std::time::Instant::now();

    // Prepare field data for export
    let field_data = prepare_field_export_data(field)?;

    // Create Arrow schema for field data
    let schema = create_field_schema()?;

    // Create record batch
    let record_batch = create_field_record_batch(&field_data, schema.clone())?;

    // Configure writer properties
    let props = WriterProperties::builder()
        .set_compression(config.compression.into())
        .set_encoding(config.encoding.into())
        .set_writer_version(config.writer_version.into())
        .set_max_row_group_size(config.row_group_size)
        .set_statistics_enabled(config.enable_statistics.into())
        .build();

    // Write to buffer
    let buffer = Vec::new();
    let cursor = Cursor::new(buffer);

    let mut writer = ArrowWriter::try_new(cursor, schema, Some(props))?;
    writer.write(&record_batch)?;
    writer.close()?;

    let buffer = writer.into_inner()?.into_inner();

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    web_sys::console::log_1(&format!("Exported field to Parquet: {} bytes in {}ms", buffer.len(), duration_ms).into());

    Ok(buffer)
}

/// Export protocol execution results to Parquet
pub fn export_protocol_results_to_parquet(results: &[ProtocolExecutionResult]) -> Result<Vec<u8>> {
    let config = ParquetConfig::default();
    export_protocol_results_to_parquet_with_config(results, config)
}

/// Export protocol execution results with custom configuration
pub fn export_protocol_results_to_parquet_with_config(
    results: &[ProtocolExecutionResult], 
    config: ParquetConfig
) -> Result<Vec<u8>> {
    let start_time = std::time::Instant::now();

    if results.is_empty() {
        return Err(anyhow!("Cannot export empty protocol results"));
    }

    // Prepare protocol data for export
    let protocol_data = prepare_protocol_export_data(results)?;

    // Create Arrow schema for protocol data
    let schema = create_protocol_schema()?;

    // Create record batch
    let record_batch = create_protocol_record_batch(&protocol_data, schema.clone())?;

    // Configure writer properties
    let props = WriterProperties::builder()
        .set_compression(config.compression.into())
        .set_encoding(config.encoding.into())
        .set_writer_version(config.writer_version.into())
        .set_max_row_group_size(config.row_group_size)
        .set_statistics_enabled(config.enable_statistics.into())
        .build();

    // Write to buffer
    let buffer = Vec::new();
    let cursor = Cursor::new(buffer);

    let mut writer = ArrowWriter::try_new(cursor, schema, Some(props))?;
    writer.write(&record_batch)?;
    writer.close()?;

    let buffer = writer.into_inner()?.into_inner();

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    web_sys::console::log_1(&format!("Exported {} protocol results to Parquet: {} bytes in {}ms", 
                                     results.len(), buffer.len(), duration_ms).into());

    Ok(buffer)
}

/// Export combined analytics data to Parquet
pub fn export_combined_analytics(
    fields: &[&mut NeuralField],
    protocol_results: &[ProtocolExecutionResult],
) -> Result<Vec<u8>> {
    let start_time = std::time::Instant::now();

    // Create combined analytics schema
    let schema = create_combined_analytics_schema()?;

    // Prepare combined data
    let mut combined_records = Vec::new();

    // Add field data records
    for field in fields {
        let field_data = prepare_field_export_data(field)?;
        combined_records.push(CombinedAnalyticsRecord {
            record_type: "field_state".to_string(),
            timestamp: field_data.timestamp,
            field_name: Some(field_data.field_name),
            protocol_name: None,
            numeric_value: field_data.energy,
            metadata: field_data.metadata,
        });
    }

    // Add protocol execution records
    for result in protocol_results {
        combined_records.push(CombinedAnalyticsRecord {
            record_type: "protocol_execution".to_string(),
            timestamp: result.created_at,
            field_name: None,
            protocol_name: Some(result.protocol_name.clone()),
            numeric_value: result.duration_ms as f64,
            metadata: serde_json::to_string(&result.outputs).unwrap_or_default(),
        });
    }

    // Create record batch
    let record_batch = create_combined_analytics_record_batch(&combined_records, schema.clone())?;

    // Configure writer properties
    let props = WriterProperties::builder()
        .set_compression(Compression::SNAPPY)
        .set_writer_version(WriterVersion::PARQUET_2_0)
        .set_statistics_enabled(parquet::basic::LogicalType::String.into())
        .build();

    // Write to buffer
    let buffer = Vec::new();
    let cursor = Cursor::new(buffer);

    let mut writer = ArrowWriter::try_new(cursor, schema, Some(props))?;
    writer.write(&record_batch)?;
    writer.close()?;

    let buffer = writer.into_inner()?.into_inner();

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    web_sys::console::log_1(&format!("Exported combined analytics to Parquet: {} bytes in {}ms", 
                                     buffer.len(), duration_ms).into());

    Ok(buffer)
}

// Helper structures and functions

#[derive(Debug, Clone)]
struct CombinedAnalyticsRecord {
    record_type: String,
    timestamp: DateTime<Utc>,
    field_name: Option<String>,
    protocol_name: Option<String>,
    numeric_value: f64,
    metadata: String,
}

fn prepare_field_export_data(field: &mut NeuralField) -> Result<FieldExportData> {
    let field_state = field.get_state();
    let field_data = field.get_field_data();

    Ok(FieldExportData {
        field_id: field_state.id,
        field_name: "neural_field".to_string(), // In production, get actual field name
        timestamp: Utc::now(),
        dimensions: field_state.dimensions.into_iter().map(|d| d as i32).collect(),
        data_points: field_data,
        energy: field_state.total_energy,
        entropy: field_state.entropy,
        coherence: field_state.coherence,
        num_attractors: field_state.num_attractors as i32,
        evolution_steps: field_state.evolution_steps,
        metadata: serde_json::to_string(&field_state.metadata).unwrap_or_default(),
    })
}

fn prepare_protocol_export_data(results: &[ProtocolExecutionResult]) -> Result<Vec<ProtocolExportData>> {
    results.iter().map(|result| {
        Ok(ProtocolExportData {
            execution_id: result.execution_id.clone(),
            protocol_name: result.protocol_name.clone(),
            timestamp: result.created_at,
            duration_ms: result.duration_ms,
            success: result.success,
            energy_change: result.metrics.energy_change,
            entropy_change: result.metrics.entropy_change,
            coherence_change: result.metrics.coherence_change,
            field_modifications: result.metrics.field_modifications as i32,
            outputs: serde_json::to_string(&result.outputs).unwrap_or_default(),
            error_message: result.error_message.clone(),
        })
    }).collect()
}

fn create_field_schema() -> Result<Arc<Schema>> {
    let fields = vec![
        Field::new("field_id", DataType::Utf8, false),
        Field::new("field_name", DataType::Utf8, false),
        Field::new("timestamp", DataType::Timestamp(TimeUnit::Millisecond, None), false),
        Field::new("dimensions", DataType::List(Arc::new(Field::new("item", DataType::Int32, false))), false),
        Field::new("data_points", DataType::List(Arc::new(Field::new("item", DataType::Float64, false))), false),
        Field::new("energy", DataType::Float64, false),
        Field::new("entropy", DataType::Float64, false),
        Field::new("coherence", DataType::Float64, false),
        Field::new("num_attractors", DataType::Int32, false),
        Field::new("evolution_steps", DataType::UInt64, false),
        Field::new("metadata", DataType::Utf8, true),
    ];

    Ok(Arc::new(Schema::new(fields)))
}

fn create_protocol_schema() -> Result<Arc<Schema>> {
    let fields = vec![
        Field::new("execution_id", DataType::Utf8, false),
        Field::new("protocol_name", DataType::Utf8, false),
        Field::new("timestamp", DataType::Timestamp(TimeUnit::Millisecond, None), false),
        Field::new("duration_ms", DataType::UInt64, false),
        Field::new("success", DataType::Boolean, false),
        Field::new("energy_change", DataType::Float64, false),
        Field::new("entropy_change", DataType::Float64, false),
        Field::new("coherence_change", DataType::Float64, false),
        Field::new("field_modifications", DataType::Int32, false),
        Field::new("outputs", DataType::Utf8, true),
        Field::new("error_message", DataType::Utf8, true),
    ];

    Ok(Arc::new(Schema::new(fields)))
}

fn create_combined_analytics_schema() -> Result<Arc<Schema>> {
    let fields = vec![
        Field::new("record_type", DataType::Utf8, false),
        Field::new("timestamp", DataType::Timestamp(TimeUnit::Millisecond, None), false),
        Field::new("field_name", DataType::Utf8, true),
        Field::new("protocol_name", DataType::Utf8, true),
        Field::new("numeric_value", DataType::Float64, false),
        Field::new("metadata", DataType::Utf8, true),
    ];

    Ok(Arc::new(Schema::new(fields)))
}

fn create_field_record_batch(data: &FieldExportData, schema: Arc<Schema>) -> Result<RecordBatch> {
    // Create arrays for each column
    let field_ids = StringArray::from(vec![data.field_id.as_str()]);
    let field_names = StringArray::from(vec![data.field_name.as_str()]);
    let timestamps = TimestampMillisecondArray::from(vec![data.timestamp.timestamp_millis()]);

    // For single record, create arrays with one element
    let dimensions_data: Vec<Option<Vec<Option<i32>>>> = vec![Some(data.dimensions.iter().map(|&d| Some(d)).collect())];
    let dimensions = arrow::array::ListArray::from_iter_primitive::<arrow::array::types::Int32Type, _, _>(dimensions_data);

    let data_points_data: Vec<Option<Vec<Option<f64>>>> = vec![Some(data.data_points.iter().map(|&d| Some(d)).collect())];
    let data_points = arrow::array::ListArray::from_iter_primitive::<arrow::array::types::Float64Type, _, _>(data_points_data);

    let energies = Float64Array::from(vec![data.energy]);
    let entropies = Float64Array::from(vec![data.entropy]);
    let coherences = Float64Array::from(vec![data.coherence]);
    let num_attractors = Int32Array::from(vec![data.num_attractors]);
    let evolution_steps = UInt64Array::from(vec![data.evolution_steps]);
    let metadata = StringArray::from(vec![data.metadata.as_str()]);

    // Create record batch
    RecordBatch::try_new(
        schema,
        vec![
            Arc::new(field_ids),
            Arc::new(field_names),
            Arc::new(timestamps),
            Arc::new(dimensions),
            Arc::new(data_points),
            Arc::new(energies),
            Arc::new(entropies),
            Arc::new(coherences),
            Arc::new(num_attractors),
            Arc::new(evolution_steps),
            Arc::new(metadata),
        ],
    ).map_err(|e| anyhow!("Failed to create record batch: {}", e))
}

fn create_protocol_record_batch(data: &[ProtocolExportData], schema: Arc<Schema>) -> Result<RecordBatch> {
    let execution_ids: Vec<&str> = data.iter().map(|d| d.execution_id.as_str()).collect();
    let protocol_names: Vec<&str> = data.iter().map(|d| d.protocol_name.as_str()).collect();
    let timestamps: Vec<i64> = data.iter().map(|d| d.timestamp.timestamp_millis()).collect();
    let durations: Vec<u64> = data.iter().map(|d| d.duration_ms).collect();
    let successes: Vec<bool> = data.iter().map(|d| d.success).collect();
    let energy_changes: Vec<f64> = data.iter().map(|d| d.energy_change).collect();
    let entropy_changes: Vec<f64> = data.iter().map(|d| d.entropy_change).collect();
    let coherence_changes: Vec<f64> = data.iter().map(|d| d.coherence_change).collect();
    let field_modifications: Vec<i32> = data.iter().map(|d| d.field_modifications).collect();
    let outputs: Vec<&str> = data.iter().map(|d| d.outputs.as_str()).collect();
    let error_messages: Vec<Option<&str>> = data.iter().map(|d| d.error_message.as_deref()).collect();

    RecordBatch::try_new(
        schema,
        vec![
            Arc::new(StringArray::from(execution_ids)),
            Arc::new(StringArray::from(protocol_names)),
            Arc::new(TimestampMillisecondArray::from(timestamps)),
            Arc::new(UInt64Array::from(durations)),
            Arc::new(arrow::array::BooleanArray::from(successes)),
            Arc::new(Float64Array::from(energy_changes)),
            Arc::new(Float64Array::from(entropy_changes)),
            Arc::new(Float64Array::from(coherence_changes)),
            Arc::new(Int32Array::from(field_modifications)),
            Arc::new(StringArray::from(outputs)),
            Arc::new(StringArray::from(error_messages)),
        ],
    ).map_err(|e| anyhow!("Failed to create protocol record batch: {}", e))
}

fn create_combined_analytics_record_batch(
    data: &[CombinedAnalyticsRecord], 
    schema: Arc<Schema>
) -> Result<RecordBatch> {
    let record_types: Vec<&str> = data.iter().map(|d| d.record_type.as_str()).collect();
    let timestamps: Vec<i64> = data.iter().map(|d| d.timestamp.timestamp_millis()).collect();
    let field_names: Vec<Option<&str>> = data.iter().map(|d| d.field_name.as_deref()).collect();
    let protocol_names: Vec<Option<&str>> = data.iter().map(|d| d.protocol_name.as_deref()).collect();
    let numeric_values: Vec<f64> = data.iter().map(|d| d.numeric_value).collect();
    let metadata: Vec<&str> = data.iter().map(|d| d.metadata.as_str()).collect();

    RecordBatch::try_new(
        schema,
        vec![
            Arc::new(StringArray::from(record_types)),
            Arc::new(TimestampMillisecondArray::from(timestamps)),
            Arc::new(StringArray::from(field_names)),
            Arc::new(StringArray::from(protocol_names)),
            Arc::new(Float64Array::from(numeric_values)),
            Arc::new(StringArray::from(metadata)),
        ],
    ).map_err(|e| anyhow!("Failed to create combined analytics record batch: {}", e))
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::neural_field::NeuralField;

    #[test]
    fn test_parquet_config() {
        let config = ParquetConfig::default();
        assert!(matches!(config.compression, CompressionType::Snappy));
        assert!(config.enable_statistics);
    }

    #[test]
    fn test_field_export_data_preparation() {
        let mut field = NeuralField::new(vec![10, 10]).unwrap();
        let result = prepare_field_export_data(&mut field);
        assert!(result.is_ok());

        let data = result.unwrap();
        assert_eq!(data.dimensions, vec![10, 10]);
        assert_eq!(data.data_points.len(), 100);
    }

    #[test]
    fn test_schema_creation() {
        let field_schema = create_field_schema().unwrap();
        assert_eq!(field_schema.fields().len(), 11);

        let protocol_schema = create_protocol_schema().unwrap();
        assert_eq!(protocol_schema.fields().len(), 11);

        let combined_schema = create_combined_analytics_schema().unwrap();
        assert_eq!(combined_schema.fields().len(), 6);
    }

    #[test]
    fn test_field_parquet_export() {
        let mut field = NeuralField::new(vec![5, 5]).unwrap();
        let result = export_field_to_parquet(&mut field);
        assert!(result.is_ok());

        let parquet_data = result.unwrap();
        assert!(!parquet_data.is_empty());
    }
}

