//! CSV Export System (Parquet disabled due to dependency conflicts)
//! 
//! High-performance data export to CSV format for neural field states,
//! protocol execution results, and analytics. Parquet support will be
//! re-enabled once chrono version conflicts are resolved.

use serde::{Deserialize, Serialize};
use anyhow::{Result, anyhow};
use std::collections::HashMap;
use chrono::{DateTime, Utc};
use uuid::Uuid;

use crate::neural_field::{NeuralField, FieldState};
use crate::protocols::ProtocolExecutionResult;

/// CSV export configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExportConfig {
    pub delimiter: char,
    pub include_headers: bool,
    pub date_format: String,
    pub float_precision: usize,
}

impl Default for ExportConfig {
    fn default() -> Self {
        ExportConfig {
            delimiter: ',',
            include_headers: true,
            date_format: "%Y-%m-%d %H:%M:%S UTC".to_string(),
            float_precision: 6,
        }
    }
}

/// Export statistics and metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExportMetadata {
    pub export_id: String,
    pub timestamp: DateTime<Utc>,
    pub record_count: usize,
    pub file_size_bytes: usize,
    pub export_type: ExportType,
    pub export_duration_ms: u64,
}

/// Types of data that can be exported
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ExportType {
    FieldState,
    FieldData,
    ProtocolExecution,
    CombinedAnalytics,
    TimeSeries,
}

/// Neural field data prepared for export
#[derive(Debug, Clone)]
pub struct FieldExportData {
    pub field_id: String,
    pub field_name: String,
    pub timestamp: DateTime<Utc>,
    pub dimensions: Vec<i32>,
    pub energy: f64,
    pub entropy: f64,
    pub coherence: f64,
    pub num_attractors: i32,
    pub evolution_steps: u64,
    pub metadata: String,
}

/// Protocol execution data prepared for export
#[derive(Debug, Clone)]
pub struct ProtocolExportData {
    pub execution_id: String,
    pub protocol_name: String,
    pub timestamp: DateTime<Utc>,
    pub duration_ms: u64,
    pub success: bool,
    pub energy_change: f64,
    pub entropy_change: f64,
    pub coherence_change: f64,
    pub field_modifications: i32,
    pub outputs: String,
    pub error_message: Option<String>,
}

/// Export neural field to CSV format
pub fn export_field_to_csv(field: &mut NeuralField) -> Result<String> {
    let config = ExportConfig::default();
    export_field_to_csv_with_config(field, config)
}

/// Export neural field to CSV with custom configuration
pub fn export_field_to_csv_with_config(field: &mut NeuralField, config: ExportConfig) -> Result<String> {
    let start_time = std::time::Instant::now();

    let field_data = prepare_field_export_data(field)?;
    let mut csv_content = String::new();

    if config.include_headers {
        csv_content.push_str("field_id,field_name,timestamp,dimensions,energy,entropy,coherence,num_attractors,evolution_steps,metadata\n");
    }

    let timestamp_str = field_data.timestamp.format(&config.date_format).to_string();
    let dimensions_str = field_data.dimensions.iter()
        .map(|d| d.to_string())
        .collect::<Vec<_>>()
        .join(";");

    csv_content.push_str(&format!(
        "\"{}\",\"{}\",\"{}\",\"{}\",{:.prec$},{:.prec$},{:.prec$},{},{},\"{}\"\n",
        field_data.field_id,
        field_data.field_name,
        timestamp_str,
        dimensions_str,
        field_data.energy,
        field_data.entropy,
        field_data.coherence,
        field_data.num_attractors,
        field_data.evolution_steps,
        field_data.metadata.replace("\"", "\"\""),
        prec = config.float_precision
    ));

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    log::info!("Exported field to CSV: {} bytes in {}ms", csv_content.len(), duration_ms);

    Ok(csv_content)
}

/// Export protocol execution results to CSV
pub fn export_protocol_results_to_csv(results: &[ProtocolExecutionResult]) -> Result<String> {
    let config = ExportConfig::default();
    export_protocol_results_to_csv_with_config(results, config)
}

/// Export protocol execution results with custom configuration
pub fn export_protocol_results_to_csv_with_config(
    results: &[ProtocolExecutionResult], 
    config: ExportConfig
) -> Result<String> {
    let start_time = std::time::Instant::now();

    if results.is_empty() {
        return Err(anyhow!("Cannot export empty protocol results"));
    }

    let protocol_data = prepare_protocol_export_data(results)?;
    let mut csv_content = String::new();

    if config.include_headers {
        csv_content.push_str("execution_id,protocol_name,timestamp,duration_ms,success,energy_change,entropy_change,coherence_change,field_modifications,outputs,error_message\n");
    }

    for data in protocol_data {
        let timestamp_str = data.timestamp.format(&config.date_format).to_string();
        let error_str = data.error_message.unwrap_or_default();

        csv_content.push_str(&format!(
            "\"{}\",\"{}\",\"{}\",{},{},{:.prec$},{:.prec$},{:.prec$},{},\"{}\",\"{}\"\n",
            data.execution_id,
            data.protocol_name,
            timestamp_str,
            data.duration_ms,
            data.success,
            data.energy_change,
            data.entropy_change,
            data.coherence_change,
            data.field_modifications,
            data.outputs.replace("\"", "\"\""),
            error_str.replace("\"", "\"\""),
            prec = config.float_precision
        ));
    }

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    log::info!("Exported {} protocol results to CSV: {} bytes in {}ms", 
               results.len(), csv_content.len(), duration_ms);

    Ok(csv_content)
}

/// Export combined analytics data to CSV
pub fn export_combined_analytics(
    fields: &[&mut NeuralField],
    protocol_results: &[ProtocolExecutionResult],
) -> Result<String> {
    let start_time = std::time::Instant::now();
    let config = ExportConfig::default();

    let mut csv_content = String::new();

    if config.include_headers {
        csv_content.push_str("record_type,timestamp,field_name,protocol_name,numeric_value,metadata\n");
    }

    // Add field data records
    for field in fields {
        let field_data = prepare_field_export_data(field)?;
        let timestamp_str = field_data.timestamp.format(&config.date_format).to_string();

        csv_content.push_str(&format!(
            "\"field_state\",\"{}\",\"{}\",\"\",{:.prec$},\"{}\"\n",
            timestamp_str,
            field_data.field_name,
            field_data.energy,
            field_data.metadata.replace("\"", "\"\""),
            prec = config.float_precision
        ));
    }

    // Add protocol execution records
    for result in protocol_results {
        let timestamp_str = result.created_at.format(&config.date_format).to_string();
        let outputs_str = serde_json::to_string(&result.outputs).unwrap_or_default();

        csv_content.push_str(&format!(
            "\"protocol_execution\",\"{}\",\"\",\"{}\",{},{}\"\n",
            timestamp_str,
            result.protocol_name,
            result.duration_ms,
            outputs_str.replace("\"", "\"\"")
        ));
    }

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    log::info!("Exported combined analytics to CSV: {} bytes in {}ms", 
               csv_content.len(), duration_ms);

    Ok(csv_content)
}

// Helper functions

fn prepare_field_export_data(field: &mut NeuralField) -> Result<FieldExportData> {
    let field_state = field.get_state();

    Ok(FieldExportData {
        field_id: field_state.id,
        field_name: "neural_field".to_string(),
        timestamp: Utc::now(),
        dimensions: field_state.dimensions.into_iter().map(|d| d as i32).collect(),
        energy: field_state.total_energy,
        entropy: field_state.entropy,
        coherence: field_state.coherence,
        num_attractors: field_state.num_attractors as i32,
        evolution_steps: field_state.evolution_steps,
        metadata: serde_json::to_string(&field_state.metadata).unwrap_or_default(),
    })
}

fn prepare_protocol_export_data(results: &[ProtocolExecutionResult]) -> Result<Vec<ProtocolExportData>> {
    results.iter().map(|result| {
        Ok(ProtocolExportData {
            execution_id: result.execution_id.clone(),
            protocol_name: result.protocol_name.clone(),
            timestamp: result.created_at,
            duration_ms: result.duration_ms,
            success: result.success,
            energy_change: result.metrics.energy_change,
            entropy_change: result.metrics.entropy_change,
            coherence_change: result.metrics.coherence_change,
            field_modifications: result.metrics.field_modifications as i32,
            outputs: serde_json::to_string(&result.outputs).unwrap_or_default(),
            error_message: result.error_message.clone(),
        })
    }).collect()
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::neural_field::NeuralField;

    #[test]
    fn test_export_config() {
        let config = ExportConfig::default();
        assert_eq!(config.delimiter, ',');
        assert!(config.include_headers);
        assert_eq!(config.float_precision, 6);
    }

    #[test]
    fn test_field_export_data_preparation() {
        let mut field = NeuralField::new(vec![10, 10]).unwrap();
        let result = prepare_field_export_data(&mut field);
        assert!(result.is_ok());

        let data = result.unwrap();
        assert_eq!(data.dimensions, vec![10, 10]);
    }

    #[test]
    fn test_field_csv_export() {
        let mut field = NeuralField::new(vec![5, 5]).unwrap();
        let result = export_field_to_csv(&mut field);
        assert!(result.is_ok());

        let csv_data = result.unwrap();
        assert!(!csv_data.is_empty());
        assert!(csv_data.contains("field_id"));
    }
}

