//! Vimmacs Neural Field Core
//! 
//! Production-ready neural field computation engine with Context Engineering protocols,
//! semantic field manipulation, and high-performance WebAssembly integration.

use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use serde_json;
use std::collections::HashMap;
use uuid::Uuid;

pub mod neural_field;
pub mod protocols;
pub mod sandbox;
pub mod memory;
pub mod resonance;
pub mod parquet_export;
pub mod performance;
pub mod security;
pub mod visualization;

use neural_field::NeuralField;
use protocols::{Protocol, ProtocolEngine, ProtocolExecutionResult};
use memory::MemorySystem;
use sandbox::SandboxContext;
use performance::PerformanceMonitor;

// Console logging for WebAssembly
#[wasm_bindgen]
extern "C" {
    #[wasm_bindgen(js_namespace = console)]
    fn log(s: &str);

    #[wasm_bindgen(js_namespace = console)]
    fn error(s: &str);

    #[wasm_bindgen(js_namespace = console)]
    fn warn(s: &str);

    #[wasm_bindgen(js_namespace = console)]
    fn info(s: &str);
}

macro_rules! console_log {
    ($($t:tt)*) => (log(&format_args!($($t)*).to_string()))
}

macro_rules! console_error {
    ($($t:tt)*) => (error(&format_args!($($t)*).to_string()))
}

macro_rules! console_warn {
    ($($t:tt)*) => (warn(&format_args!($($t)*).to_string()))
}

macro_rules! console_info {
    ($($t:tt)*) => (info(&format_args!($($t)*).to_string()))
}

/// Runtime configuration for the neural field system
#[derive(Debug, Clone, Serialize, Deserialize)]
#[wasm_bindgen]
pub struct RuntimeConfig {
    /// Maximum number of concurrent fields
    pub max_fields: usize,
    /// Maximum field dimensions
    pub max_dimensions: usize,
    /// Enable performance monitoring
    pub enable_monitoring: bool,
    /// Enable security validation
    pub enable_security: bool,
    /// Memory limit in MB
    pub memory_limit_mb: usize,
    /// Protocol execution timeout in milliseconds
    pub protocol_timeout_ms: u64,
}

#[wasm_bindgen]
impl RuntimeConfig {
    #[wasm_bindgen(constructor)]
    pub fn new() -> RuntimeConfig {
        RuntimeConfig {
            max_fields: 10,
            max_dimensions: 1024,
            enable_monitoring: true,
            enable_security: true,
            memory_limit_mb: 512,
            protocol_timeout_ms: 30000,
        }
    }

    #[wasm_bindgen]
    pub fn from_json(json: &str) -> Result<RuntimeConfig, JsValue> {
        serde_json::from_str(json)
            .map_err(|e| JsValue::from_str(&e.to_string()))
    }

    #[wasm_bindgen]
    pub fn to_json(&self) -> Result<String, JsValue> {
        serde_json::to_string(self)
            .map_err(|e| JsValue::from_str(&e.to_string()))
    }
}

impl Default for RuntimeConfig {
    fn default() -> Self {
        Self::new()
    }
}

/// Main neural field runtime for Vimmacs
/// 
/// This is the primary interface for neural field computation, protocol execution,
/// and semantic field manipulation in the Vimmacs environment.
#[wasm_bindgen]
pub struct VimmacsNeuralRuntime {
    config: RuntimeConfig,
    engine: ProtocolEngine,
    fields: HashMap<String, NeuralField>,
    sandbox_contexts: HashMap<String, SandboxContext>,
    memory_system: MemorySystem,
    performance_monitor: PerformanceMonitor,
    session_id: String,
    security_validator: security::SecurityValidator,
}

#[wasm_bindgen]
impl VimmacsNeuralRuntime {
    /// Create a new neural field runtime with default configuration
    #[wasm_bindgen(constructor)]
    pub fn new() -> Result<VimmacsNeuralRuntime, JsValue> {
        Self::new_with_config(RuntimeConfig::default())
    }

    /// Create a new neural field runtime with custom configuration
    #[wasm_bindgen]
    pub fn new_with_config(config: RuntimeConfig) -> Result<VimmacsNeuralRuntime, JsValue> {
        let session_id = Uuid::new_v4().to_string();

        console_info!("Initializing Vimmacs Neural Runtime - Session: {}", session_id);

        let runtime = VimmacsNeuralRuntime {
            engine: ProtocolEngine::new(&config)?,
            fields: HashMap::new(),
            sandbox_contexts: HashMap::new(),
            memory_system: MemorySystem::new(config.memory_limit_mb * 1024 * 1024)?,
            performance_monitor: PerformanceMonitor::new(config.enable_monitoring),
            session_id: session_id.clone(),
            security_validator: security::SecurityValidator::new(&config)?,
            config,
        };

        console_info!("Neural Runtime initialized successfully");
        Ok(runtime)
    }

    /// Get the current session ID
    #[wasm_bindgen]
    pub fn get_session_id(&self) -> String {
        self.session_id.clone()
    }

    /// Get runtime configuration as JSON
    #[wasm_bindgen]
    pub fn get_config(&self) -> Result<String, JsValue> {
        self.config.to_json()
    }

    /// Create a new neural field with specified dimensions
    #[wasm_bindgen]
    pub fn create_field(&mut self, name: String, dimensions: Vec<u32>) -> Result<(), JsValue> {
        let _timer = self.performance_monitor.start_timer("create_field");

        // Security validation
        self.security_validator.validate_field_creation(&name, &dimensions)?;

        // Check limits
        if self.fields.len() >= self.config.max_fields {
            return Err(JsValue::from_str("Maximum number of fields exceeded"));
        }

        let dims: Vec<usize> = dimensions.iter().map(|&d| d as usize).collect();

        // Validate dimensions
        for &dim in &dims {
            if dim > self.config.max_dimensions {
                return Err(JsValue::from_str("Dimension size exceeds maximum allowed"));
            }
        }

        let field = NeuralField::new(dims)?;
        self.fields.insert(name.clone(), field);

        console_info!("Created neural field '{}' with dimensions: {:?}", name, dimensions);
        Ok(())
    }

    /// Execute a protocol on the neural field system
    #[wasm_bindgen]
    pub fn execute_protocol(&mut self, protocol_json: String) -> Result<String, JsValue> {
        let _timer = self.performance_monitor.start_timer("execute_protocol");

        // Parse protocol
        let protocol: Protocol = serde_json::from_str(&protocol_json)
            .map_err(|e| JsValue::from_str(&format!("Protocol parsing error: {}", e)))?;

        // Security validation
        self.security_validator.validate_protocol(&protocol)?;

        console_info!("Executing protocol: {}", protocol.name);

        // Execute protocol with timeout
        let result = self.engine.execute_with_timeout(
            protocol, 
            &mut self.fields,
            &mut self.memory_system,
            self.config.protocol_timeout_ms
        ).map_err(|e| JsValue::from_str(&format!("Protocol execution error: {}", e)))?;

        // Serialize result
        serde_json::to_string(&result)
            .map_err(|e| JsValue::from_str(&format!("Result serialization error: {}", e)))
    }

    /// Create a sandboxed execution context
    #[wasm_bindgen]
    pub fn create_sandbox(&mut self, name: String, config_json: String) -> Result<(), JsValue> {
        let _timer = self.performance_monitor.start_timer("create_sandbox");

        let config: sandbox::SandboxConfig = serde_json::from_str(&config_json)
            .map_err(|e| JsValue::from_str(&format!("Sandbox config error: {}", e)))?;

        // Security validation
        self.security_validator.validate_sandbox_config(&config)?;

        let sandbox = SandboxContext::new(config)?;
        self.sandbox_contexts.insert(name.clone(), sandbox);

        console_info!("Created sandbox context: {}", name);
        Ok(())
    }

    /// Execute protocol in sandbox
    #[wasm_bindgen]
    pub fn execute_in_sandbox(&mut self, sandbox_name: String, protocol_json: String) -> Result<String, JsValue> {
        let _timer = self.performance_monitor.start_timer("execute_in_sandbox");

        let sandbox = self.sandbox_contexts.get_mut(&sandbox_name)
            .ok_or_else(|| JsValue::from_str("Sandbox not found"))?;

        let protocol: Protocol = serde_json::from_str(&protocol_json)
            .map_err(|e| JsValue::from_str(&format!("Protocol parsing error: {}", e)))?;

        // Security validation
        self.security_validator.validate_protocol(&protocol)?;

        console_info!("Executing protocol '{}' in sandbox '{}'", protocol.name, sandbox_name);

        let result = sandbox.execute_protocol(protocol, &mut self.fields)
            .map_err(|e| JsValue::from_str(&format!("Sandbox execution error: {}", e)))?;

        serde_json::to_string(&result)
            .map_err(|e| JsValue::from_str(&format!("Result serialization error: {}", e)))
    }

    /// Export field data to Parquet format
    #[wasm_bindgen]
    pub fn export_to_parquet(&self, field_name: String) -> Result<Vec<u8>, JsValue> {
        let _timer = self.performance_monitor.start_timer("export_parquet");

        let field = self.fields.get(&field_name)
            .ok_or_else(|| JsValue::from_str("Field not found"))?;

        console_info!("Exporting field '{}' to Parquet format", field_name);

        parquet_export::export_field_to_parquet(field)
            .map_err(|e| JsValue::from_str(&format!("Parquet export error: {}", e)))
    }

    /// Get field state as JSON
    #[wasm_bindgen]
    pub fn get_field_state(&self, name: String) -> Result<String, JsValue> {
        let field = self.fields.get(&name)
            .ok_or_else(|| JsValue::from_str("Field not found"))?;

        let state = field.get_state();
        serde_json::to_string(&state)
            .map_err(|e| JsValue::from_str(&format!("State serialization error: {}", e)))
    }

    /// Apply resonance scaffold to a field
    #[wasm_bindgen]
    pub fn apply_resonance(&mut self, field_name: String, config_json: String) -> Result<(), JsValue> {
        let _timer = self.performance_monitor.start_timer("apply_resonance");

        let field = self.fields.get_mut(&field_name)
            .ok_or_else(|| JsValue::from_str("Field not found"))?;

        let config: resonance::ResonanceConfig = serde_json::from_str(&config_json)
            .map_err(|e| JsValue::from_str(&format!("Resonance config error: {}", e)))?;

        // Security validation
        self.security_validator.validate_resonance_config(&config)?;

        console_info!("Applying resonance scaffold to field '{}'", field_name);

        resonance::apply_resonance_scaffold(field, config)
            .map_err(|e| JsValue::from_str(&format!("Resonance application error: {}", e)))
    }

    /// Get performance metrics
    #[wasm_bindgen]
    pub fn get_performance_metrics(&self) -> Result<String, JsValue> {
        let metrics = self.performance_monitor.get_metrics();
        serde_json::to_string(&metrics)
            .map_err(|e| JsValue::from_str(&format!("Metrics serialization error: {}", e)))
    }

    /// Get memory usage statistics
    #[wasm_bindgen]
    pub fn get_memory_stats(&self) -> Result<String, JsValue> {
        let stats = self.memory_system.get_stats();
        serde_json::to_string(&stats)
            .map_err(|e| JsValue::from_str(&format!("Memory stats serialization error: {}", e)))
    }

    /// List all available fields
    #[wasm_bindgen]
    pub fn list_fields(&self) -> Vec<String> {
        self.fields.keys().cloned().collect()
    }

    /// Remove a field
    #[wasm_bindgen]
    pub fn remove_field(&mut self, name: String) -> Result<(), JsValue> {
        match self.fields.remove(&name) {
            Some(_) => {
                console_info!("Removed field '{}'", name);
                Ok(())
            }
            None => Err(JsValue::from_str("Field not found"))
        }
    }

    /// Clear all fields and reset runtime
    #[wasm_bindgen]
    pub fn reset(&mut self) -> Result<(), JsValue> {
        let _timer = self.performance_monitor.start_timer("reset");

        self.fields.clear();
        self.sandbox_contexts.clear();
        self.memory_system.clear();
        self.performance_monitor.reset();

        console_info!("Runtime reset completed");
        Ok(())
    }

    /// Get visualization data for a field
    #[wasm_bindgen]
    pub fn get_visualization_data(&self, field_name: String, config_json: String) -> Result<String, JsValue> {
        let field = self.fields.get(&field_name)
            .ok_or_else(|| JsValue::from_str("Field not found"))?;

        let config: visualization::VisualizationConfig = serde_json::from_str(&config_json)
            .map_err(|e| JsValue::from_str(&format!("Visualization config error: {}", e)))?;

        let data = visualization::generate_visualization_data(field, config)?;

        serde_json::to_string(&data)
            .map_err(|e| JsValue::from_str(&format!("Visualization data serialization error: {}", e)))
    }
}

/// Initialize the WASM module
#[wasm_bindgen(start)]
pub fn init() {
    #[cfg(feature = "console_error_panic_hook")]
    console_error_panic_hook::set_once();

    #[cfg(feature = "console_log")]
    console_log::init_with_level(log::Level::Info)
        .expect("Failed to initialize logger");

    console_info!("Vimmacs Neural Field WASM Runtime initialized successfully");
}

/// Get version information
#[wasm_bindgen]
pub fn get_version() -> String {
    format!("vimmacs-neural-field v{}", env!("CARGO_PKG_VERSION"))
}

/// Check if the runtime is supported in current environment
#[wasm_bindgen]
pub fn check_support() -> Result<String, JsValue> {
    let support_info = serde_json::json!({
        "wasm_support": true,
        "version": env!("CARGO_PKG_VERSION"),
        "features": {
            "parallel": cfg!(feature = "parallel"),
            "browser": cfg!(feature = "browser"),
            "console_log": cfg!(feature = "console_log")
        }
    });

    Ok(support_info.to_string())
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_runtime_config() {
        let config = RuntimeConfig::new();
        assert_eq!(config.max_fields, 10);
        assert_eq!(config.max_dimensions, 1024);
        assert!(config.enable_monitoring);
        assert!(config.enable_security);
    }

    #[test]
    fn test_config_serialization() {
        let config = RuntimeConfig::new();
        let json = config.to_json().unwrap();
        let deserialized = RuntimeConfig::from_json(&json).unwrap();
        assert_eq!(config.max_fields, deserialized.max_fields);
    }
}

