/// Generate visualization data from neural field
pub fn generate_visualization_data(
    field: &mut NeuralField,
    config: VisualizationConfig,
) -> Result<VisualizationData> {
    let start_time = std::time::Instant::now();

    // Generate field visualization data
    let field_vis_data = generate_field_data(field, &config)?;

    // Generate attractor visualizations
    let attractor_vis = generate_attractor_visualizations(field, &config);

    // Generate trajectories if requested
    let trajectories = if config.show_trajectories {
        generate_trajectory_visualizations(field, &config)?
    } else {
        Vec::new()
    };

    // Generate metadata
    let metadata = VisualizationMetadata {
        field_name: "neural_field".to_string(), // In production, get actual field name
        timestamp: chrono::Utc::now().to_rfc3339(),
        total_energy: field.calculate_energy()?,
        coherence: field.calculate_coherence()?,
        entropy: field.calculate_entropy()?,
        dominant_frequency: None, // Would be calculated from field analysis
        render_time_ms: start_time.elapsed().as_millis() as u64,
        frame_number: 0,
    };

    // Generate animation frames if needed
    let animation_frames = if config.animation_frame_rate > 0 {
        generate_animation_frames(field, &config)?
    } else {
        Vec::new()
    };

    Ok(VisualizationData {
        config,
        field_data: field_vis_data,
        attractors: attractor_vis,
        trajectories,
        metadata,
        animation_frames,
    })
}

/// Generate optimized visualization for web rendering
pub fn generate_web_visualization(
    field: &mut NeuralField,
    viewport_width: u32,
    viewport_height: u32,
) -> Result<WebVisualizationData> {
    let config = VisualizationConfig {
        resolution: Resolution::Custom(viewport_width / 4, viewport_height / 4), // Optimize for web
        visualization_type: VisualizationType::Heatmap,
        color_scheme: ColorScheme::Viridis,
        show_attractors: true,
        transparency: 0.8,
        ..Default::default()
    };

    let full_data = generate_visualization_data(field, config)?;

    // Convert to web-optimized format
    let web_data = WebVisualizationData {
        image_data: convert_to_image_data(&full_data.field_data)?,
        attractors: {
            let num_attractors = full_data.attractors.len() as u32;
            let web_attractors = full_data.attractors.into_iter().map(convert_attractor_to_web).collect();
            web_attractors
        },
        stats: WebStats {
            energy: full_data.metadata.total_energy,
            coherence: full_data.metadata.coherence,
            num_attractors: full_data.attractors.len() as u32,
        },
    };

    Ok(web_data)
}

