//! Sandboxed Execution Environment
//! 
//! Secure, isolated execution contexts for neural field protocols
//! with resource limits and security boundaries.

use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use uuid::Uuid;
use chrono::{DateTime, Utc};
use anyhow::{Result, anyhow};

use crate::neural_field::NeuralField;
use crate::protocols::{Protocol, ProtocolExecutionResult};

/// Sandbox configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SandboxConfig {
    pub name: Option<String>,
    pub memory_limit_mb: usize,
    pub execution_timeout_ms: u64,
    pub max_field_operations: u32,
    pub allowed_protocols: Vec<String>,
    pub resource_monitoring: bool,
    pub isolation_level: IsolationLevel,
}

impl Default for SandboxConfig {
    fn default() -> Self {
        SandboxConfig {
            name: None,
            memory_limit_mb: 64,
            execution_timeout_ms: 10000,
            max_field_operations: 100,
            allowed_protocols: vec![
                "attractor.co.emerge".to_string(),
                "field.resonance.scaffold".to_string(),
            ],
            resource_monitoring: true,
            isolation_level: IsolationLevel::Standard,
        }
    }
}

/// Isolation levels for sandbox execution
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum IsolationLevel {
    Minimal,   // Basic resource limits only
    Standard,  // Standard isolation with protocol restrictions
    Strict,    // Strict isolation with full monitoring
    Maximum,   // Maximum security with minimal permissions
}

/// Sandbox execution context
pub struct SandboxContext {
    id: String,
    config: SandboxConfig,
    created_at: DateTime<Utc>,
    execution_count: u64,
    resource_usage: ResourceUsage,
}

/// Resource usage tracking
#[derive(Debug, Clone, Default)]
struct ResourceUsage {
    memory_used_mb: usize,
    operations_executed: u32,
    protocols_run: u32,
    total_execution_time_ms: u64,
}

impl SandboxContext {
    /// Create a new sandbox context
    pub fn new(config: SandboxConfig) -> Result<Self> {
        let id = Uuid::new_v4().to_string();

        Ok(SandboxContext {
            id,
            config,
            created_at: Utc::now(),
            execution_count: 0,
            resource_usage: ResourceUsage::default(),
        })
    }

    /// Execute a protocol within the sandbox
    pub fn execute_protocol(
        &mut self,
        protocol: Protocol,
        fields: &mut HashMap<String, NeuralField>,
    ) -> Result<ProtocolExecutionResult> {
        // Validate protocol is allowed
        if !self.config.allowed_protocols.contains(&protocol.name) {
            return Err(anyhow!("Protocol '{}' not allowed in this sandbox", protocol.name));
        }

        // Check resource limits before execution
        self.check_resource_limits()?;

        let start_time = std::time::Instant::now();
        let execution_id = Uuid::new_v4().to_string();

        let protocol_name = protocol.name.clone();

        // Execute protocol (simplified - in real implementation, would use proper isolation)
        let result = match protocol_name.as_str() {
            "attractor.co.emerge" => {
                self.execute_attractor_emergence_sandbox(protocol.clone(), fields)
            }
            "field.resonance.scaffold" => {
                self.execute_resonance_scaffold_sandbox(protocol, fields)
            }
            _ => {
                Err(anyhow!("Unsupported protocol in sandbox: {}", protocol_name))
            }
        };

        let duration_ms = start_time.elapsed().as_millis() as u64;

        // Update resource usage
        self.resource_usage.protocols_run += 1;
        self.resource_usage.total_execution_time_ms += duration_ms;
        self.execution_count += 1;

        // Create execution result
        let (success, outputs) = match result {
            Ok(outputs) => (true, outputs),
            Err(_) => (false, HashMap::new()),
        };

        let execution_result = ProtocolExecutionResult {
            protocol_name,
            execution_id,
            success,
            duration_ms,
            outputs,
            metrics: crate::protocols::ExecutionMetrics {
                energy_change: 0.0,
                entropy_change: 0.0,
                coherence_change: 0.0,
                field_modifications: 0,
                memory_operations: 0,
                computation_cycles: duration_ms,
            },
            error_message: None,
            warnings: Vec::new(),
            created_at: Utc::now(),
        };

        Ok(execution_result)
    }

    /// Get sandbox statistics
    pub fn get_stats(&self) -> SandboxStats {
        SandboxStats {
            id: self.id.clone(),
            name: self.config.name.clone(),
            created_at: self.created_at,
            execution_count: self.execution_count,
            resource_usage: self.resource_usage.clone(),
            uptime_ms: Utc::now().signed_duration_since(self.created_at).num_milliseconds() as u64,
        }
    }

    // Private methods for sandboxed protocol execution

    fn check_resource_limits(&self) -> Result<()> {
        if self.resource_usage.memory_used_mb > self.config.memory_limit_mb {
            return Err(anyhow!("Memory limit exceeded in sandbox"));
        }

        if self.resource_usage.operations_executed > self.config.max_field_operations {
            return Err(anyhow!("Operation limit exceeded in sandbox"));
        }

        Ok(())
    }

    fn execute_attractor_emergence_sandbox(
        &mut self,
        protocol: Protocol,
        fields: &mut HashMap<String, NeuralField>,
    ) -> Result<HashMap<String, serde_json::Value>> {
        let mut results = HashMap::new();

        let num_attractors = protocol.parameters.attractors.unwrap_or(2).min(5) as usize; // Limit in sandbox
        let evolution_steps = protocol.parameters.evolution_steps.unwrap_or(50).min(200) as u64; // Limit steps

        // Apply to first available field only (sandbox limitation)
        if let Some((field_name, field)) = fields.iter_mut().next() {
            field.clear_attractors();

            // Create limited attractors
            let dimensions = field.dimensions();
            for i in 0..num_attractors {
                let position = vec![
                    dimensions[0] as f64 * (i + 1) as f64 / (num_attractors + 1) as f64,
                    if dimensions.len() > 1 { dimensions[1] as f64 / 2.0 } else { 0.0 },
                ];

                let attractor = crate::neural_field::AttractorConfig {
                    position,
                    strength: 0.5, // Reduced strength in sandbox
                    radius: 3.0,
                    decay_rate: 0.02,
                    oscillation_frequency: None,
                    oscillation_amplitude: None,
                };

                field.add_attractor(attractor)?;
                self.resource_usage.operations_executed += 1;
            }

            // Limited evolution
            let initial_energy = field.calculate_energy()?;
            field.evolve(evolution_steps)?;
            let final_energy = field.calculate_energy()?;

            results.insert("energy_change".to_string(), 
                          serde_json::to_value(final_energy - initial_energy)?);
            results.insert("attractors_created".to_string(), 
                          serde_json::to_value(num_attractors)?);
        }

        Ok(results)
    }

    fn execute_resonance_scaffold_sandbox(
        &mut self,
        protocol: Protocol,
        fields: &mut HashMap<String, NeuralField>,
    ) -> Result<HashMap<String, serde_json::Value>> {
        let mut results = HashMap::new();

        let frequency = protocol.parameters.frequency.unwrap_or(1.0).min(5.0); // Limit frequency
        let amplitude = protocol.parameters.amplitude.unwrap_or(0.3).min(0.5); // Limit amplitude

        for (field_name, field) in fields.iter_mut() {
            let initial_coherence = field.calculate_coherence()?;

            // Limited resonance cycles
            for cycle in 0..5 { // Reduced cycles in sandbox
                let phase = frequency * cycle as f64;
                let resonance_strength = amplitude * phase.sin();

                let dimensions = field.dimensions();
                let center_pos = dimensions.iter().map(|&d| d as f64 / 2.0).collect();

                let resonance_attractor = crate::neural_field::AttractorConfig {
                    position: center_pos,
                    strength: resonance_strength,
                    radius: 4.0,
                    decay_rate: 0.1,
                    oscillation_frequency: Some(frequency),
                    oscillation_amplitude: Some(amplitude),
                };

                field.add_attractor(resonance_attractor)?;
                field.evolve(5)?; // Limited evolution steps
                field.clear_attractors();

                self.resource_usage.operations_executed += 1;
            }

            let final_coherence = field.calculate_coherence()?;

            results.insert(format!("{}_coherence_change", field_name), 
                          serde_json::to_value(final_coherence - initial_coherence)?);
        }

        Ok(results)
    }
}

/// Sandbox statistics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SandboxStats {
    pub id: String,
    pub name: Option<String>,
    pub created_at: DateTime<Utc>,
    pub execution_count: u64,
    pub resource_usage: ResourceUsage,
    pub uptime_ms: u64,
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_sandbox_creation() {
        let config = SandboxConfig::default();
        let sandbox = SandboxContext::new(config);
        assert!(sandbox.is_ok());
    }

    #[test]
    fn test_sandbox_config() {
        let mut config = SandboxConfig::default();
        config.memory_limit_mb = 128;
        config.execution_timeout_ms = 5000;

        assert_eq!(config.memory_limit_mb, 128);
        assert_eq!(config.execution_timeout_ms, 5000);
    }
}

