        let runtime = VimmacsNeuralRuntime {
            engine: ProtocolEngine::new(&config).map_err(|e| JsValue::from_str(&e.to_string()))?,
            fields: HashMap::new(),
            sandbox_contexts: HashMap::new(),
            memory_system: MemorySystem::new(config.memory_limit_mb * 1024 * 1024).map_err(|e| JsValue::from_str(&e.to_string()))?,
            performance_monitor: PerformanceMonitor::new(config.enable_monitoring),
            session_id: session_id.clone(),
            security_validator: security::SecurityValidator::new(&config).map_err(|e| JsValue::from_str(&e.to_string()))?,
            config,
        };

        console_info!("Neural Runtime initialized successfully");
        Ok(runtime)
    }

