    pub timestamp: DateTime<Utc>,
    pub duration_ms: u64,
    pub success: bool,
    pub energy_change: f64,
    pub entropy_change: f64,
    pub coherence_change: f64,
    pub field_modifications: i32,
    pub outputs: String,
    pub error_message: Option<String>,
}

/// Export neural field to CSV format
pub fn export_field_to_csv(field: &mut NeuralField) -> Result<String> {
    let config = ExportConfig::default();
    export_field_to_csv_with_config(field, config)
}

/// Export neural field to CSV with custom configuration
pub fn export_field_to_csv_with_config(field: &mut NeuralField, config: ExportConfig) -> Result<String> {
    let start_time = std::time::Instant::now();

    let field_data = prepare_field_export_data(field)?;
    let mut csv_content = String::new();

    if config.include_headers {
        csv_content.push_str("field_id,field_name,timestamp,dimensions,energy,entropy,coherence,num_attractors,evolution_steps,metadata\n");
    }

    let timestamp_str = field_data.timestamp.format(&config.date_format).to_string();
    let dimensions_str = field_data.dimensions.iter()
        .map(|d| d.to_string())
        .collect::<Vec<_>>()
        .join(";");

    csv_content.push_str(&format!(
        "\"{}\",\"{}\",\"{}\",\"{}\",{:.prec$},{:.prec$},{:.prec$},{},{},\"{}\"\n",
        field_data.field_id,
        field_data.field_name,
        timestamp_str,
        dimensions_str,
        field_data.energy,
        field_data.entropy,
        field_data.coherence,
        field_data.num_attractors,
        field_data.evolution_steps,
        field_data.metadata.replace("\"", "\"\""),
        prec = config.float_precision
    ));

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    log::info!("Exported field to CSV: {} bytes in {}ms", csv_content.len(), duration_ms);

    Ok(csv_content)
}

/// Export protocol execution results to CSV
pub fn export_protocol_results_to_csv(results: &[ProtocolExecutionResult]) -> Result<String> {
    let config = ExportConfig::default();
    export_protocol_results_to_csv_with_config(results, config)
}

/// Export protocol execution results with custom configuration
pub fn export_protocol_results_to_csv_with_config(
    results: &[ProtocolExecutionResult], 
    config: ExportConfig
) -> Result<String> {
    let start_time = std::time::Instant::now();

    if results.is_empty() {
        return Err(anyhow!("Cannot export empty protocol results"));
    }

    let protocol_data = prepare_protocol_export_data(results)?;
    let mut csv_content = String::new();

    if config.include_headers {
        csv_content.push_str("execution_id,protocol_name,timestamp,duration_ms,success,energy_change,entropy_change,coherence_change,field_modifications,outputs,error_message\n");
    }

    for data in protocol_data {
        let timestamp_str = data.timestamp.format(&config.date_format).to_string();
        let error_str = data.error_message.unwrap_or_default();

        csv_content.push_str(&format!(
            "\"{}\",\"{}\",\"{}\",{},{},{:.prec$},{:.prec$},{:.prec$},{},\"{}\",\"{}\"\n",
            data.execution_id,
            data.protocol_name,
            timestamp_str,
            data.duration_ms,
            data.success,
            data.energy_change,
            data.entropy_change,
            data.coherence_change,
            data.field_modifications,
            data.outputs.replace("\"", "\"\""),
            error_str.replace("\"", "\"\""),
            prec = config.float_precision
        ));
    }

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
