    for data in protocol_data {
        let timestamp_str = data.timestamp.format(&config.date_format).to_string();
        let error_str = data.error_message.unwrap_or_default();

        csv_content.push_str(&format!(
            "\"{}\",\"{}\",\"{}\",{},{},{:.prec$},{:.prec$},{:.prec$},{},\"{}\",\"{}\"\n",
            data.execution_id,
            data.protocol_name,
            timestamp_str,
            data.duration_ms,
            data.success,
            data.energy_change,
            data.entropy_change,
            data.coherence_change,
            data.field_modifications,
            data.outputs.replace("\"", "\"\""),
            error_str.replace("\"", "\"\""),
            prec = config.float_precision
        ));
    }

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    log::info!("Exported {} protocol results to CSV: {} bytes in {}ms", 
               results.len(), csv_content.len(), duration_ms);

    Ok(csv_content)
}

/// Export combined analytics data to CSV
pub fn export_combined_analytics(
    fields: &mut [&mut NeuralField],
    protocol_results: &[ProtocolExecutionResult],
) -> Result<String> {
    let start_time = std::time::Instant::now();
    let config = ExportConfig::default();

    let mut csv_content = String::new();

    if config.include_headers {
        csv_content.push_str("record_type,timestamp,field_name,protocol_name,numeric_value,metadata\n");
    }

    // Add field data records
    for field in fields {
        let field_data = prepare_field_export_data(*field)?;
        let timestamp_str = field_data.timestamp.format(&config.date_format).to_string();

        csv_content.push_str(&format!(
            "\"field_state\",\"{}\",\"{}\",\"\",{:.prec$},\"{}\"\n",
            timestamp_str,
            field_data.field_name,
            field_data.energy,
            field_data.metadata.replace("\"", "\"\""),
            prec = config.float_precision
        ));
    }

    // Add protocol execution records
    for result in protocol_results {
        let timestamp_str = result.created_at.format(&config.date_format).to_string();
        let outputs_str = serde_json::to_string(&result.outputs).unwrap_or_default();

        csv_content.push_str(&format!(
            "\"protocol_execution\",\"{}\",\"\",\"{}\",{},{}\"\n",
            timestamp_str,
            result.protocol_name,
            result.duration_ms,
            outputs_str.replace("\"", "\"\"")
        ));
    }

    let duration_ms = start_time.elapsed().as_millis() as u64;

    #[cfg(feature = "console_log")]
    log::info!("Exported combined analytics to CSV: {} bytes in {}ms", 
               csv_content.len(), duration_ms);

    Ok(csv_content)
}

// Helper functions

fn prepare_field_export_data(field: &mut NeuralField) -> Result<FieldExportData> {
    let field_state = field.get_state();

    Ok(FieldExportData {
        field_id: field_state.id,
        field_name: "neural_field".to_string(),
        timestamp: Utc::now(),
        dimensions: field_state.dimensions.into_iter().map(|d| d as i32).collect(),
        energy: field_state.total_energy,
        entropy: field_state.entropy,
        coherence: field_state.coherence,
        num_attractors: field_state.num_attractors as i32,
        evolution_steps: field_state.evolution_steps,
        metadata: serde_json::to_string(&field_state.metadata).unwrap_or_default(),
    })
}

fn prepare_protocol_export_data(results: &[ProtocolExecutionResult]) -> Result<Vec<ProtocolExportData>> {
    results.iter().map(|result| {
        Ok(ProtocolExportData {
            execution_id: result.execution_id.clone(),
            protocol_name: result.protocol_name.clone(),
            timestamp: result.created_at,
            duration_ms: result.duration_ms,
            success: result.success,
            energy_change: result.metrics.energy_change,
            entropy_change: result.metrics.entropy_change,
            coherence_change: result.metrics.coherence_change,
            field_modifications: result.metrics.field_modifications as i32,
            outputs: serde_json::to_string(&result.outputs).unwrap_or_default(),
            error_message: result.error_message.clone(),
        })
    }).collect()
}

