}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VimmacsConfig {
    pub feature_flags: FeatureFlags,
    pub performance_limits: PerformanceLimits,
    pub container_config: ContainerConfig,
    pub editor_config: EditorConfig,
}

impl VimmacsConfig {
    pub fn load() -> Result<Self> {
        Self::load_from_path("../../config/feature_flags.toml")
    }

    pub fn load_from_path(path: &str) -> Result<Self> {
        let content = fs::read_to_string(path)
            .with_context(|| format!("Failed to read config file: {}", path))?;

        let config: VimmacsConfig = toml::from_str(&content)
            .with_context(|| "Failed to parse TOML configuration")?;

        Ok(config)
    }

    pub fn is_neural_field_enabled(&self) -> bool {
        self.feature_flags.neural_field_enabled
    }

    pub fn is_neural_field_component_enabled(&self, component: NeuralFieldComponent) -> bool {
        if !self.feature_flags.neural_field_enabled {
            return false;
        }

        match component {
            NeuralFieldComponent::Computation => self.feature_flags.neural_field_computation,
            NeuralFieldComponent::Protocols => self.feature_flags.neural_field_protocols,
            NeuralFieldComponent::Visualization => self.feature_flags.neural_field_visualization,
            NeuralFieldComponent::Memory => self.feature_flags.neural_field_memory,
            NeuralFieldComponent::Resonance => self.feature_flags.neural_field_resonance,
            NeuralFieldComponent::Sandbox => self.feature_flags.neural_field_sandbox,
        }
    }

