        self.container_config.single_layer_isolation
    }

    pub fn get_memory_limit(&self) -> u32 {
        self.performance_limits.max_memory_mb
    }

    pub fn get_terminal_timeout(&self) -> u32 {
        self.performance_limits.terminal_response_timeout_ms
    }
}

