    Computation,
    Protocols,
    Visualization,
    Memory,
    Resonance,
    Sandbox,
}

impl Default for VimmacsConfig {
    fn default() -> Self {
        Self {
            feature_flags: FeatureFlags {
                terminal_multiplexer: true,
                text_editor: true,
                lsp_integration: true,
                syntax_highlighting: true,
                file_management: true,

                neural_field_enabled: false, // Disabled by default for production
                neural_field_computation: false,
                neural_field_protocols: false,
                neural_field_visualization: false,
                neural_field_memory: false,
                neural_field_resonance: false,
                neural_field_sandbox: false,

                performance_monitoring: true,
                security_validation: true,
                resource_monitoring: true,

                kata_containers: true,
                wasm_runtime: true,
                arch_linux_integration: true,

                debug_logging: false,
                performance_profiling: false,
                security_audit_mode: false,
            },
            performance_limits: PerformanceLimits {
                max_memory_mb: 1024,
                max_cpu_percent: 80,
                max_file_descriptors: 4096,
                terminal_response_timeout_ms: 50,
            },
            container_config: ContainerConfig {
                single_layer_isolation: false,
                kata_runtime: true,
                wasm_sandbox: true,
            },
            editor_config: EditorConfig {
                vim_mode: true,
                doom_emacs_integration: true,
                neovim_integration: true,
                unified_keybindings: true,
            },
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_default_config() {
        let config = VimmacsConfig::default();
        assert!(!config.is_neural_field_enabled());
        assert!(config.feature_flags.terminal_multiplexer);
        assert_eq!(config.performance_limits.max_memory_mb, 1024);
    }

    #[test]
    fn test_neural_field_component_check() {
        let mut config = VimmacsConfig::default();
        config.feature_flags.neural_field_enabled = true;
        config.feature_flags.neural_field_computation = true;

        assert!(config.is_neural_field_component_enabled(NeuralFieldComponent::Computation));
        assert!(!config.is_neural_field_component_enabled(NeuralFieldComponent::Protocols));
    }
}

