        config.feature_flags.neural_field_enabled = true;
        config.feature_flags.neural_field_computation = true;

        assert!(config.is_neural_field_component_enabled(NeuralFieldComponent::Computation));
        assert!(!config.is_neural_field_component_enabled(NeuralFieldComponent::Protocols));
    }
}

