use crate::config::{VimmacsConfig, NeuralFieldComponent};
use wasm_bindgen::prelude::*;
use serde_json;

#[wasm_bindgen]
pub struct VimmacsIntegration {
    config: VimmacsConfig,
}

#[wasm_bindgen]
impl VimmacsIntegration {
    #[wasm_bindgen(constructor)]
    pub fn new() -> Result<VimmacsIntegration, JsValue> {
        let config = VimmacsConfig::load().unwrap_or_else(|e| {
            console_log!("Failed to load Vimmacs config, using defaults: {}", e);
            VimmacsConfig::default()
        });

        Ok(VimmacsIntegration { config })
    }

    /// Check if the neural field system should be available
