events {
    worker_connections 1024;
}

http {
    log_format claude_access '[$time_local] $remote_addr - $remote_user '
                             '"$request" $status $body_bytes_sent '
                             '"$http_referer" "$http_user_agent" '
                             'rt=$request_time uct="$upstream_connect_time" '
                             'uht="$upstream_header_time" urt="$upstream_response_time" '
                             'node="$upstream_addr"';

    access_log /var/log/nginx/claude_access.log claude_access;
    error_log /var/log/nginx/claude_error.log;

    # Load balancing for Claude Code sessions
    upstream claude_nodes {
        # Least connections for optimal Claude Code distribution
        least_conn;

        # Node 1 - Primary
        server vimmacs-claude-1:8080 max_fails=3 fail_timeout=30s;
        # Node 2 - Secondary
        server vimmacs-claude-2:8080 max_fails=3 fail_timeout=30s;

        # Health checks
        keepalive 32;
    }

    # WASM runtime load balancing
    upstream wasm_runtime {
        least_conn;
        server vimmacs-claude-1:8081;
        server vimmacs-claude-2:8081;
        keepalive 16;
    }

    # Session persistence for Claude Code continuity
    map $arg_session_id $session_pool {
        ~.{1,16}$ claude_nodes;
        default claude_nodes;
    }

    # Main server configuration
    server {
        listen 80;
        server_name localhost claude-router;

        # Enable gzip compression
        gzip on;
        gzip_types text/plain text/css application/json application/javascript text/xml application/xml;

        # Claude Code API routing with session affinity
        location /claude/ {
            proxy_pass http://claude_nodes;

            # Session persistence using IP hash for now
            # TODO: Implement proper session tokens
            hash $remote_addr consistent;

            # Proxy headers for proper forwarding
            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
            proxy_set_header X-Forwarded-Proto $scheme;

            # Timeouts for Claude Code responses
            proxy_connect_timeout 30s;
            proxy_send_timeout 60s;
            proxy_read_timeout 300s;  # Allow for longer Claude Code responses

            # Enable WebSocket support for real-time features
            proxy_http_version 1.1;
            proxy_set_header Upgrade $http_upgrade;
            proxy_set_header Connection "upgrade";
        }

        # @vimmacs API routing
        location /vimmacs/ {
            proxy_pass http://claude_nodes;

            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;

            proxy_connect_timeout 10s;
            proxy_send_timeout 30s;
            proxy_read_timeout 60s;
        }

        # WASM runtime routing
        location /wasm/ {
            proxy_pass http://wasm_runtime;

            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;

            # WASM operations may take time
            proxy_connect_timeout 15s;
            proxy_send_timeout 45s;
            proxy_read_timeout 120s;
        }

        # Health check endpoint
        location /health {
            access_log off;
            return 200 "Claude Router Healthy\n";
            add_header Content-Type text/plain;
        }

        # Load balancer status page
        location /status {
            access_log off;
            return 200 "Claude Nodes: 2 active\nMode: High-Performance\nResource: 2x(4-core,16GB)\n";
            add_header Content-Type text/plain;
        }

        # Metrics endpoint for monitoring
        location /metrics {
            proxy_pass http://vimmacs-claude-1:9090/metrics;
            proxy_set_header Host $host;
        }

        # Static assets and UI (if applicable)
        location / {
            proxy_pass http://claude_nodes;
            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        }
    }

    # Separate server for metrics and monitoring
    server {
        listen 8090;
        server_name metrics;

        location /nginx-metrics {
            access_log off;
            return 200 "# Nginx Claude Router Metrics\nnginx_active_connections{} 1\nnginx_claude_requests_total{} $request_time\n";
            add_header Content-Type text/plain;
        }

        location /health {
            return 200 "OK";
        }
    }
}

