import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ReactQueryDevtools } from '@tanstack/react-query-devtools';

// Katalyst Core Imports
import { KatalystProvider } from '@katalyst/core/components/KatalystProvider';
import { MultithreadingProvider } from '@katalyst/core/components/MultithreadingProvider';
import { UnifiedRuntimeProvider } from '@katalyst/core/components/UnifiedRuntimeProvider';

// Katalyst Feature Imports
import { PaymentProvider } from '@katalyst/payments/payments/providers/PaymentProvider';
import { PWAProvider } from '@katalyst/pwa/core/PWAProvider';
import { SecurityAIProvider } from '@katalyst/security-ai/components/SecurityAIProvider';

// Design System
import { KatalystDesignSystem } from '@katalyst/design-system/themes/KatalystDesignSystem';
import { ThemeProvider } from '@katalyst/design-system/themes/theme-provider';

// Router
import { AppRouter } from './router/AppRouter';

// Hooks
import { useKatalyst } from '@katalyst/hooks/use-katalyst';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';
import { usePWA } from '@katalyst/hooks/use-pwa';

// Create QueryClient with optimized config for multithreading
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      staleTime: 1000 * 60 * 5, // 5 minutes
      cacheTime: 1000 * 60 * 30, // 30 minutes
      retry: 3,
      refetchOnWindowFocus: false,
    },
    mutations: {
      retry: 1,
    },
  },
});

function AppContent() {
  const { isInitialized, metrics } = useKatalyst();
  const { threadPools, executingTasks } = useMultithreading();

  return (
    <div className="min-h-screen bg-background">
      {/* Main Application with Router */}
      <AppRouter />

      {/* Development Tools */}
      {import.meta.env.DEV && (
        <div className="fixed bottom-4 right-4 z-50">
          <div className="bg-black/80 text-white p-2 rounded text-xs space-y-1">
            <div>üßµ Threads: {threadPools.length}</div>
            <div>‚ö° Tasks: {executingTasks}</div>
            <div>üìä Performance: {metrics?.averageResponseTime || 0}ms</div>
            <div>üöÄ Katalyst: {isInitialized ? 'Ready' : 'Loading'}</div>
          </div>
        </div>
      )}
    </div>
  );
}

export default function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <KatalystProvider
        config={{
          multithreading: {
            enabled: true,
            defaultPoolType: 'rayon',
            maxWorkers: navigator.hardwareConcurrency || 4
          },
          ai: {
            provider: 'cloudflare',
            models: ['@cf/meta/llama-3.1-8b-instruct'],
            features: ['recommendations', 'search', 'support']
          },
          performance: {
            monitoring: true,
            optimization: 'balanced'
          }
        }}
      >
        <MultithreadingProvider>
          <UnifiedRuntimeProvider>
            <ThemeProvider defaultTheme="system" storageKey="katalyst-storefront-theme">
              <KatalystDesignSystem>
                <PaymentProvider
                  providers={{
                    stripe: { publishableKey: import.meta.env.VITE_STRIPE_PUBLISHABLE_KEY },
                    paypal: { clientId: import.meta.env.VITE_PAYPAL_CLIENT_ID },
                    crypto: { networks: ['ethereum', 'polygon'] }
                  }}
                >
                  <PWAProvider>
                    <SecurityAIProvider>
                      <AppContent />
                    </SecurityAIProvider>
                  </PWAProvider>
                </PaymentProvider>
              </KatalystDesignSystem>
            </ThemeProvider>
          </UnifiedRuntimeProvider>
        </MultithreadingProvider>
      </KatalystProvider>
      <ReactQueryDevtools initialIsOpen={false} />
    </QueryClientProvider>
  );
}

{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": ["**/.env.*local"],
  "globalEnv": ["NODE_ENV", "DENO_ENV", "TAURI_ENV"],
  "remoteCache": {
    "enabled": true,
    "signature": true
  },
  "pipeline": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**", ".next/**", "build/**", "target/**"],
      "env": ["DENO_ENV", "NODE_ENV", "TAURI_*"],
      "cache": true
    },
    "dev": {
      "cache": false,
      "persistent": true,
      "dependsOn": ["^build"]
    },
    "test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"],
      "cache": true,
      "inputs": ["src/**", "tests/**", "*.test.ts", "*.test.tsx"]
    },
    "lint": {
      "outputs": [],
      "cache": true,
      "inputs": ["**/*.ts", "**/*.tsx", "**/*.js", "**/*.jsx"]
    },
    "typecheck": {
      "dependsOn": ["^build"],
      "outputs": [],
      "cache": true
    },
    "clean": {
      "cache": false
    },
    "deploy": {
      "dependsOn": ["build", "test", "lint"],
      "outputs": ["dist/**"],
      "cache": false
    },
    "@katalyst/multithreading#build": {
      "outputs": ["*.node", "target/**"],
      "cache": true,
      "env": ["CARGO_*", "RUST_*"]
    },
    "@katalyst/desktop#build": {
      "dependsOn": ["@katalyst/core#build", "@katalyst/multithreading#build"],
      "outputs": ["src-tauri/target/**", "dist/**"],
      "env": ["TAURI_*"],
      "cache": true
    },
    "@katalyst/mobile#build": {
      "dependsOn": ["@katalyst/core#build"],
      "outputs": ["ios/build/**", "android/build/**", "dist/**"],
      "cache": true
    },
    "@katalyst/metaverse#build": {
      "dependsOn": ["@katalyst/core#build", "@katalyst/webxr#build"],
      "outputs": ["dist/**", "wasm/**"],
      "env": ["WASM_*", "WASMER_*"],
      "cache": true
    },
    "@katalyst/admin-svelte#build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**"],
      "env": ["NODE_ENV", "VITE_*"],
      "cache": true
    },
    "@katalyst/admin-svelte#dev": {
      "cache": false,
      "persistent": true,
      "dependsOn": []
    },
    "@katalyst/admin-svelte#test": {
      "dependsOn": ["build"],
      "outputs": ["coverage/**"],
      "cache": true,
      "inputs": ["src/**", "*.test.ts", "*.test.svelte"]
    }
  }
}

