import { defineConfig } from 'vite';
import react from '@vitejs/plugin-react';
import { resolve } from 'path';
import wasm from 'rollup-plugin-wasm';

export default defineConfig({
  plugins: [
    react(),
    wasm()
  ],
  resolve: {
    alias: {
      '@': resolve(__dirname, './src'),
      '@shared': resolve(__dirname, '../shared'),
      '@katalyst/core': resolve(__dirname, '../../packages/core/src'),
      '@katalyst/multithreading': resolve(__dirname, '../../packages/multithreading'),
      '@katalyst/design-system': resolve(__dirname, '../../packages/design-system/src'),
      '@katalyst/ai': resolve(__dirname, '../../packages/ai/src'),
      '@katalyst/payments': resolve(__dirname, '../../packages/payments/src'),
      '@katalyst/security-ai': resolve(__dirname, '../../packages/security-ai/src'),
      '@katalyst/pwa': resolve(__dirname, '../../packages/pwa/src'),
      '@katalyst/hooks': resolve(__dirname, '../../packages/hooks/src'),
      '@katalyst/utils': resolve(__dirname, '../../packages/utils/src'),
      '@katalyst/integrations': resolve(__dirname, '../../packages/integrations/src')
    }
  },
  define: {
    global: 'globalThis',
  },
  server: {
    port: 3001,
    host: true,
    proxy: {
      '/api': {
        target: 'http://localhost:4000',
        changeOrigin: true,
        rewrite: (path) => path.replace(/^\/api/, '')
      },
      '/medusa': {
        target: process.env.MEDUSA_BACKEND_URL || 'http://localhost:9000',
        changeOrigin: true,
        rewrite: (path) => path.replace(/^\/medusa/, '')
      }
    }
  },
  build: {
    target: 'esnext',
    minify: 'esbuild',
    rollupOptions: {
      output: {
        manualChunks: {
          vendor: ['react', 'react-dom'],
          medusa: ['@medusajs/client', '@medusajs/types'],
          ui: ['framer-motion', '@headlessui/react'],
          utils: ['axios', 'date-fns', 'clsx', 'tailwind-merge']
        }
      }
    },
    assetsDir: 'static',
    sourcemap: true
  },
  optimizeDeps: {
    include: [
      'react',
      'react-dom',
      'react-router-dom',
      '@tanstack/react-query',
      '@medusajs/client'
    ]
  },
  worker: {
    format: 'es'
  }
});

