     1‚Üíimport { useState, useRef, useEffect, useCallback } from 'react';
     2‚Üíimport { motion, AnimatePresence } from 'framer-motion';
     3‚Üí
     4‚Üí// Katalyst Core Imports
     5‚Üíimport { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
     6‚Üíimport { useAI } from '@katalyst/hooks/use-ai';
     7‚Üíimport { useMultithreading } from '@katalyst/hooks/use-multithreading';
     8‚Üí
     9‚Üí// Design System
    10‚Üíimport { Button } from '@katalyst/design-system/ui/button';
    11‚Üíimport { Card, CardContent, CardHeader, CardTitle } from '@katalyst/design-system/ui/card';
    12‚Üíimport { Input } from '@katalyst/design-system/ui/input';
    13‚Üíimport { Badge } from '@katalyst/design-system/ui/badge';
    14‚Üíimport { Avatar, AvatarFallback } from '@katalyst/design-system/ui/avatar';
    15‚Üíimport { ScrollArea } from '@katalyst/design-system/ui/scroll-area';
    16‚Üí
    17‚Üí// Icons
    18‚Üíimport {
    19‚Üí  MessageCircle, Send, Bot, User, Zap, Brain, Cpu, Loader2,
    20‚Üí  X, Minimize2, Maximize2, RotateCcw, Copy, ThumbsUp, ThumbsDown,
    21‚Üí  Sparkles, Code, Search, ShoppingBag, Settings, HelpCircle
    22‚Üí} from 'lucide-react';
    23‚Üí
    24‚Üíinterface ChatMessage {
    25‚Üí  id: string;
    26‚Üí  type: 'user' | 'assistant' | 'system';
    27‚Üí  content: string;
    28‚Üí  timestamp: Date;
    29‚Üí  status?: 'sending' | 'sent' | 'error';
    30‚Üí  metadata?: {
    31‚Üí    processingTime?: number;
    32‚Üí    threadPoolId?: string;
    33‚Üí    model?: string;
    34‚Üí    confidence?: number;
    35‚Üí    suggestions?: string[];
    36‚Üí  };
    37‚Üí}
    38‚Üí
    39‚Üíinterface ChatContext {
    40‚Üí  sessionId: string;
    41‚Üí  userPreferences: {
    42‚Üí    theme: 'light' | 'dark';
    43‚Üí    language: string;
    44‚Üí    aiPersonality: 'professional' | 'friendly' | 'technical';
    45‚Üí  };
    46‚Üí  conversationHistory: ChatMessage[];
    47‚Üí  currentPage?: string;
    48‚Üí  userIntent?: string;
    49‚Üí}
    50‚Üí
    51‚Üíinterface KatalystAIChatbotProps {
    52‚Üí  isOpen: boolean;
    53‚Üí  onClose: () => void;
    54‚Üí  initialMessage?: string;
    55‚Üí  context?: Partial<ChatContext>;
    56‚Üí}
    57‚Üí
    58‚Üíexport function KatalystAIChatbot({ 
    59‚Üí  isOpen, 
    60‚Üí  onClose, 
    61‚Üí  initialMessage,
    62‚Üí  context = {}
    63‚Üí}: KatalystAIChatbotProps) {
    64‚Üí  const [messages, setMessages] = useState<ChatMessage[]>([]);
    65‚Üí  const [inputValue, setInputValue] = useState('');
    66‚Üí  const [isTyping, setIsTyping] = useState(false);
    67‚Üí  const [isMinimized, setIsMinimized] = useState(false);
    68‚Üí  const [chatContext, setChatContext] = useState<ChatContext>({
    69‚Üí    sessionId: `chat-${Date.now()}`,
    70‚Üí    userPreferences: {
    71‚Üí      theme: 'light',
    72‚Üí      language: 'en',
    73‚Üí      aiPersonality: 'friendly'
    74‚Üí    },
    75‚Üí    conversationHistory: [],
    76‚Üí    ...context
    77‚Üí  });
    78‚Üí
    79‚Üí  const messagesEndRef = useRef<HTMLDivElement>(null);
    80‚Üí  const inputRef = useRef<HTMLInputElement>(null);
    81‚Üí
    82‚Üí  // Katalyst Hooks
    83‚Üí  const { executeTask, createThreadPool } = useMultithreadingStore();
    84‚Üí  const { generateRecommendations, isAIEnabled, processWithAI } = useAI();
    85‚Üí  const { threadPools, createWorkerPool } = useMultithreading();
    86‚Üí
    87‚Üí  // Predefined quick actions with Katalyst integration
    88‚Üí  const quickActions = [
    89‚Üí    {
    90‚Üí      icon: Search,
    91‚Üí      label: 'Find Products',
    92‚Üí      action: 'product_search',
    93‚Üí      prompt: 'Help me find products in your catalog'
    94‚Üí    },
    95‚Üí    {
    96‚Üí      icon: ShoppingBag,
    97‚Üí      label: 'Order Status',
    98‚Üí      action: 'order_inquiry',
    99‚Üí      prompt: 'Check the status of my recent orders'
   100‚Üí    },
   101‚Üí    {
   102‚Üí      icon: Code,
   103‚Üí      label: 'Technical Support',
   104‚Üí      action: 'tech_support',
   105‚Üí      prompt: 'I need help with technical integration'
   106‚Üí    },
   107‚Üí    {
   108‚Üí      icon: Settings,
   109‚Üí      label: 'Account Help',
   110‚Üí      action: 'account_support',
   111‚Üí      prompt: 'Help me with my account settings'
   112‚Üí    }
   113‚Üí  ];
   114‚Üí
   115‚Üí  // Initialize chatbot with welcome message
   116‚Üí  useEffect(() => {
   117‚Üí    if (isOpen && messages.length === 0) {
   118‚Üí      const welcomeMessage: ChatMessage = {
   119‚Üí        id: `msg-${Date.now()}`,
   120‚Üí        type: 'assistant',
   121‚Üí        content: `Hello! I'm your Katalyst AI assistant, powered by advanced multithreading and AI technology. I can help you with:
   122‚Üí
   123‚Üí‚Ä¢ **Product Search** - Find exactly what you're looking for
   124‚Üí‚Ä¢ **Technical Support** - Get help with integrations and setup
   125‚Üí‚Ä¢ **Order Management** - Check status, track shipments, handle returns
   126‚Üí‚Ä¢ **Performance Insights** - Real-time system analytics and optimization
   127‚Üí
   128‚ÜíHow can I assist you today?`,
   129‚Üí        timestamp: new Date(),
   130‚Üí        metadata: {
   131‚Üí          model: '@cf/meta/llama-3.1-8b-instruct',
   132‚Üí          confidence: 0.95,
   133‚Üí          suggestions: [
   134‚Üí            'Show me your latest AI development tools',
   135‚Üí            'Help me optimize my application performance',
   136‚Üí            'What multithreading capabilities do you offer?'
   137‚Üí          ]
   138‚Üí        }
   139‚Üí      };
   140‚Üí
   141‚Üí      setMessages([welcomeMessage]);
   142‚Üí    }
   143‚Üí
   144‚Üí    if (initialMessage && isOpen) {
   145‚Üí      handleSendMessage(initialMessage);
   146‚Üí    }
   147‚Üí  }, [isOpen, initialMessage]);
   148‚Üí
   149‚Üí  // Auto-scroll to bottom
   150‚Üí  useEffect(() => {
   151‚Üí    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
   152‚Üí  }, [messages]);
   153‚Üí
   154‚Üí  // Focus input when opened
   155‚Üí  useEffect(() => {
   156‚Üí    if (isOpen && !isMinimized) {
   157‚Üí      setTimeout(() => inputRef.current?.focus(), 100);
   158‚Üí    }
   159‚Üí  }, [isOpen, isMinimized]);
   160‚Üí
   161‚Üí  // AI-powered message processing with multithreading
   162‚Üí  const processAIMessage = useCallback(async (userMessage: string, messageId: string): Promise<string> => {
   163‚Üí    try {
   164‚Üí      setIsTyping(true);
   165‚Üí
   166‚Üí      // Create dedicated AI processing thread pool for complex queries
   167‚Üí      const aiProcessingPool = await createThreadPool({
   168‚Üí        type: 'tokio',
   169‚Üí        workerCount: 2,
   170‚Üí        priority: 'high'
   171‚Üí      });
   172‚Üí
   173‚Üí      // Execute AI processing with context awareness
   174‚Üí      const aiResponse = await executeTask({
   175‚Üí        id: `ai-chat-${messageId}`,
   176‚Üí        operation: 'process_ai_chat_message',
   177‚Üí        data: {
   178‚Üí          userMessage,
   179‚Üí          context: chatContext,
   180‚Üí          conversationHistory: messages.slice(-10), // Last 10 messages for context
   181‚Üí          systemPrompt: `You are Katalyst AI, an advanced assistant for a multithreaded, AI-powered e-commerce platform.
   182‚Üí          
   183‚ÜíKey Capabilities:
   184‚Üí- Real-time multithreaded processing
   185‚Üí- AI-powered product recommendations
   186‚Üí- Performance optimization insights
   187‚Üí- Technical support for developers
   188‚Üí- Cloudflare AI model integration
   189‚Üí
   190‚ÜíPersonality: ${chatContext.userPreferences.aiPersonality}
   191‚ÜíCurrent Page: ${chatContext.currentPage || 'unknown'}
   192‚Üí
   193‚ÜíProvide helpful, accurate responses that showcase Katalyst's advanced capabilities.`,
   194‚Üí          modelConfig: {
   195‚Üí            model: '@cf/meta/llama-3.1-8b-instruct',
   196‚Üí            maxTokens: 1024,
   197‚Üí            temperature: 0.7,
   198‚Üí            topP: 0.9
   199‚Üí          },
   200‚Üí          processingOptions: {
   201‚Üí            useMultithreading: true,
   202‚Üí            enableContextAnalysis: true,
   203‚Üí            includeRecommendations: true,
   204‚Üí            threadPoolId: aiProcessingPool.id
   205‚Üí          }
   206‚Üí        },
   207‚Üí        priority: 'high',
   208‚Üí        status: 'pending'
   209‚Üí      });
   210‚Üí
   211‚Üí      // Process AI response and generate suggestions
   212‚Üí      const response = aiResponse?.content || `I apologize, but I'm having trouble processing your request right now. As your Katalyst AI assistant, I'm designed to help with:
   213‚Üí
   214‚Üí‚Ä¢ Product searches and recommendations
   215‚Üí‚Ä¢ Technical support and troubleshooting  
   216‚Üí‚Ä¢ Order management and tracking
   217‚Üí‚Ä¢ Performance optimization insights
   218‚Üí
   219‚ÜíPlease try rephrasing your question, or select one of the quick actions below to get started.`;
   220‚Üí
   221‚Üí      // Generate contextual suggestions using AI
   222‚Üí      let suggestions: string[] = [];
   223‚Üí      if (isAIEnabled) {
   224‚Üí        try {
   225‚Üí          const suggestionContext = {
   226‚Üí            userMessage,
   227‚Üí            aiResponse: response,
   228‚Üí            conversationHistory: messages.slice(-5)
   229‚Üí          };
   230‚Üí          
   231‚Üí          const aiSuggestions = await generateRecommendations(suggestionContext, {
   232‚Üí            type: 'conversation_suggestions',
   233‚Üí            limit: 3
   234‚Üí          });
   235‚Üí          
   236‚Üí          suggestions = aiSuggestions || [];
   237‚Üí        } catch (error) {
   238‚Üí          console.warn('Failed to generate suggestions:', error);
   239‚Üí        }
   240‚Üí      }
   241‚Üí
   242‚Üí      // Update message with metadata
   243‚Üí      setMessages(prev => prev.map(msg => 
   244‚Üí        msg.id === messageId 
   245‚Üí          ? {
   246‚Üí              ...msg,
   247‚Üí              content: response,
   248‚Üí              status: 'sent',
   249‚Üí              metadata: {
   250‚Üí                ...msg.metadata,
   251‚Üí                processingTime: Date.now() - msg.timestamp.getTime(),
   252‚Üí                threadPoolId: aiProcessingPool.id,
   253‚Üí                model: '@cf/meta/llama-3.1-8b-instruct',
   254‚Üí                confidence: 0.85,
   255‚Üí                suggestions: suggestions.length > 0 ? suggestions : [
   256‚Üí                  'Tell me more about this',
   257‚Üí                  'Show me related products',
   258‚Üí                  'How can I get started?'
   259‚Üí                ]
   260‚Üí              }
   261‚Üí            }
   262‚Üí          : msg
   263‚Üí      ));
   264‚Üí
   265‚Üí      return response;
   266‚Üí
   267‚Üí    } catch (error) {
   268‚Üí      console.error('AI message processing failed:', error);
   269‚Üí      
   270‚Üí      // Update message with error state
   271‚Üí      setMessages(prev => prev.map(msg => 
   272‚Üí        msg.id === messageId 
   273‚Üí          ? { ...msg, status: 'error', content: 'I apologize, but I encountered an error processing your message. Please try again.' }
   274‚Üí          : msg
   275‚Üí      ));
   276‚Üí
   277‚Üí      return 'I apologize, but I encountered an error processing your message. Please try again.';
   278‚Üí    } finally {
   279‚Üí      setIsTyping(false);
   280‚Üí    }
   281‚Üí  }, [executeTask, createThreadPool, chatContext, messages, isAIEnabled, generateRecommendations]);
   282‚Üí
   283‚Üí  // Handle sending messages
   284‚Üí  const handleSendMessage = useCallback(async (message?: string) => {
   285‚Üí    const messageText = message || inputValue.trim();
   286‚Üí    if (!messageText) return;
   287‚Üí
   288‚Üí    // Clear input
   289‚Üí    setInputValue('');
   290‚Üí
   291‚Üí    // Add user message
   292‚Üí    const userMessage: ChatMessage = {
   293‚Üí      id: `user-msg-${Date.now()}`,
   294‚Üí      type: 'user',
   295‚Üí      content: messageText,
   296‚Üí      timestamp: new Date(),
   297‚Üí      status: 'sent'
   298‚Üí    };
   299‚Üí
   300‚Üí    // Add AI response placeholder
   301‚Üí    const aiMessageId = `ai-msg-${Date.now()}`;
   302‚Üí    const aiMessage: ChatMessage = {
   303‚Üí      id: aiMessageId,
   304‚Üí      type: 'assistant',
   305‚Üí      content: '',
   306‚Üí      timestamp: new Date(),
   307‚Üí      status: 'sending'
   308‚Üí    };
   309‚Üí
   310‚Üí    setMessages(prev => [...prev, userMessage, aiMessage]);
   311‚Üí
   312‚Üí    // Process AI response
   313‚Üí    await processAIMessage(messageText, aiMessageId);
   314‚Üí
   315‚Üí    // Update conversation context
   316‚Üí    setChatContext(prev => ({
   317‚Üí      ...prev,
   318‚Üí      conversationHistory: [...prev.conversationHistory, userMessage]
   319‚Üí    }));
   320‚Üí
   321‚Üí  }, [inputValue, processAIMessage]);
   322‚Üí
   323‚Üí  // Handle quick action clicks
   324‚Üí  const handleQuickAction = useCallback((action: typeof quickActions[0]) => {
   325‚Üí    handleSendMessage(action.prompt);
   326‚Üí  }, [handleSendMessage]);
   327‚Üí
   328‚Üí  // Handle suggestion clicks
   329‚Üí  const handleSuggestionClick = useCallback((suggestion: string) => {
   330‚Üí    handleSendMessage(suggestion);
   331‚Üí  }, [handleSendMessage]);
   332‚Üí
   333‚Üí  // Copy message content
   334‚Üí  const copyMessage = useCallback((content: string) => {
   335‚Üí    navigator.clipboard.writeText(content);
   336‚Üí  }, []);
   337‚Üí
   338‚Üí  // Clear conversation
   339‚Üí  const clearConversation = useCallback(() => {
   340‚Üí    setMessages([]);
   341‚Üí    setChatContext(prev => ({ ...prev, conversationHistory: [] }));
   342‚Üí  }, []);
   343‚Üí
   344‚Üí  if (!isOpen) return null;
   345‚Üí
   346‚Üí  return (
   347‚Üí    <motion.div
   348‚Üí      initial={{ opacity: 0, y: 100, scale: 0.9 }}
   349‚Üí      animate={{ 
   350‚Üí        opacity: 1, 
   351‚Üí        y: 0, 
   352‚Üí        scale: 1,
   353‚Üí        height: isMinimized ? 'auto' : '600px'
   354‚Üí      }}
   355‚Üí      exit={{ opacity: 0, y: 100, scale: 0.9 }}
   356‚Üí      transition={{ type: 'spring', stiffness: 300, damping: 30 }}
   357‚Üí      className="fixed bottom-4 right-4 z-50 w-96 max-w-[calc(100vw-2rem)]"
   358‚Üí    >
   359‚Üí      <Card className="shadow-2xl border-primary/20">
   360‚Üí        {/* Chat Header */}
   361‚Üí        <CardHeader className="pb-3 bg-gradient-to-r from-primary to-secondary text-white">
   362‚Üí          <div className="flex items-center justify-between">
   363‚Üí            <div className="flex items-center space-x-3">
   364‚Üí              <div className="relative">
   365‚Üí                <Avatar className="h-8 w-8 bg-white/20">
   366‚Üí                  <AvatarFallback className="bg-transparent text-white">
   367‚Üí                    <Bot className="h-5 w-5" />
   368‚Üí                  </AvatarFallback>
   369‚Üí                </Avatar>
   370‚Üí                <div className="absolute -bottom-1 -right-1">
   371‚Üí                  <div className="w-3 h-3 bg-green-400 rounded-full border-2 border-white animate-pulse" />
   372‚Üí                </div>
   373‚Üí              </div>
   374‚Üí              
   375‚Üí              <div>
   376‚Üí                <CardTitle className="text-sm font-semibold text-white">
   377‚Üí                  Katalyst AI Assistant
   378‚Üí                </CardTitle>
   379‚Üí                <div className="flex items-center space-x-2 text-xs text-white/80">
   380‚Üí                  {isAIEnabled && (
   381‚Üí                    <>
   382‚Üí                      <Brain className="w-3 h-3" />
   383‚Üí                      <span>AI-Powered</span>
   384‚Üí                    </>
   385‚Üí                  )}
   386‚Üí                  <Cpu className="w-3 h-3" />
   387‚Üí                  <span>{threadPools.length} pools</span>
   388‚Üí                </div>
   389‚Üí              </div>
   390‚Üí            </div>
   391‚Üí
   392‚Üí            <div className="flex items-center space-x-1">
   393‚Üí              <Button
   394‚Üí                variant="ghost"
   395‚Üí                size="sm"
   396‚Üí                onClick={() => setIsMinimized(!isMinimized)}
   397‚Üí                className="h-8 w-8 p-0 text-white hover:bg-white/20"
   398‚Üí              >
   399‚Üí                {isMinimized ? <Maximize2 className="h-4 w-4" /> : <Minimize2 className="h-4 w-4" />}
   400‚Üí              </Button>
   401‚Üí              
   402‚Üí              <Button
   403‚Üí                variant="ghost"
   404‚Üí                size="sm"
   405‚Üí                onClick={clearConversation}
   406‚Üí                className="h-8 w-8 p-0 text-white hover:bg-white/20"
   407‚Üí              >
   408‚Üí                <RotateCcw className="h-4 w-4" />
   409‚Üí              </Button>
   410‚Üí              
   411‚Üí              <Button
   412‚Üí                variant="ghost"
   413‚Üí                size="sm"
   414‚Üí                onClick={onClose}
   415‚Üí                className="h-8 w-8 p-0 text-white hover:bg-white/20"
   416‚Üí              >
   417‚Üí                <X className="h-4 w-4" />
   418‚Üí              </Button>
   419‚Üí            </div>
   420‚Üí          </div>
   421‚Üí        </CardHeader>
   422‚Üí
   423‚Üí        {!isMinimized && (
   424‚Üí          <CardContent className="p-0">
   425‚Üí            {/* Messages Area */}
   426‚Üí            <ScrollArea className="h-80 p-4">
   427‚Üí              <div className="space-y-4">
   428‚Üí                {messages.map((message) => (
   429‚Üí                  <motion.div
   430‚Üí                    key={message.id}
   431‚Üí                    initial={{ opacity: 0, y: 10 }}
   432‚Üí                    animate={{ opacity: 1, y: 0 }}
   433‚Üí                    className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
   434‚Üí                  >
   435‚Üí                    <div className={`max-w-[80%] ${message.type === 'user' ? 'order-2' : 'order-1'}`}>
   436‚Üí                      <div className="flex items-start space-x-2">
   437‚Üí                        {message.type === 'assistant' && (
   438‚Üí                          <Avatar className="h-6 w-6 mt-1">
   439‚Üí                            <AvatarFallback className="bg-primary text-primary-foreground">
   440‚Üí                              <Bot className="h-4 w-4" />
   441‚Üí                            </AvatarFallback>
   442‚Üí                          </Avatar>
   443‚Üí                        )}
   444‚Üí                        
   445‚Üí                        <div className={`rounded-lg px-3 py-2 ${
   446‚Üí                          message.type === 'user' 
   447‚Üí                            ? 'bg-primary text-primary-foreground' 
   448‚Üí                            : 'bg-muted'
   449‚Üí                        }`}>
   450‚Üí                          {message.status === 'sending' ? (
   451‚Üí                            <div className="flex items-center space-x-2">
   452‚Üí                              <Loader2 className="h-4 w-4 animate-spin" />
   453‚Üí                              <span className="text-sm">Thinking...</span>
   454‚Üí                            </div>
   455‚Üí                          ) : (
   456‚Üí                            <div className="text-sm whitespace-pre-wrap">{message.content}</div>
   457‚Üí                          )}
   458‚Üí
   459‚Üí                          {/* Message Metadata */}
   460‚Üí                          {message.metadata && message.type === 'assistant' && message.status === 'sent' && (
   461‚Üí                            <div className="mt-2 space-y-2">
   462‚Üí                              {/* Processing Info */}
   463‚Üí                              <div className="flex items-center space-x-2 text-xs text-muted-foreground">
   464‚Üí                                {message.metadata.processingTime && (
   465‚Üí                                  <Badge variant="outline" className="text-xs">
   466‚Üí                                    <Zap className="w-3 h-3 mr-1" />
   467‚Üí                                    {message.metadata.processingTime}ms
   468‚Üí                                  </Badge>
