import { useState, useRef, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

// Katalyst Core Imports
import { useMultithreadingStore } from '@katalyst/core/stores/multithreading-store';
import { useAI } from '@katalyst/hooks/use-ai';
import { useMultithreading } from '@katalyst/hooks/use-multithreading';

// Design System
import { Button } from '@katalyst/design-system/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@katalyst/design-system/ui/card';
import { Input } from '@katalyst/design-system/ui/input';
import { Badge } from '@katalyst/design-system/ui/badge';
import { Avatar, AvatarFallback } from '@katalyst/design-system/ui/avatar';
import { ScrollArea } from '@katalyst/design-system/ui/scroll-area';

// Icons
import {
  MessageCircle, Send, Bot, User, Zap, Brain, Cpu, Loader2,
  X, Minimize2, Maximize2, RotateCcw, Copy, ThumbsUp, ThumbsDown,
  Sparkles, Code, Search, ShoppingBag, Settings, HelpCircle
} from 'lucide-react';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant' | 'system';
  content: string;
  timestamp: Date;
  status?: 'sending' | 'sent' | 'error';
  metadata?: {
    processingTime?: number;
    threadPoolId?: string;
    model?: string;
    confidence?: number;
    suggestions?: string[];
  };
}

interface ChatContext {
  sessionId: string;
  userPreferences: {
    theme: 'light' | 'dark';
    language: string;
    aiPersonality: 'professional' | 'friendly' | 'technical';
  };
  conversationHistory: ChatMessage[];
  currentPage?: string;
  userIntent?: string;
}

interface KatalystAIChatbotProps {
  isOpen: boolean;
  onClose: () => void;
  initialMessage?: string;
  context?: Partial<ChatContext>;
}

export function KatalystAIChatbot({ 
  isOpen, 
  onClose, 
  initialMessage,
  context = {}
}: KatalystAIChatbotProps) {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [chatContext, setChatContext] = useState<ChatContext>({
    sessionId: `chat-${Date.now()}`,
    userPreferences: {
      theme: 'light',
      language: 'en',
      aiPersonality: 'friendly'
    },
    conversationHistory: [],
    ...context
  });

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Katalyst Hooks
  const { executeTask, createThreadPool } = useMultithreadingStore();
  const { generateRecommendations, isAIEnabled, processWithAI } = useAI();
  const { threadPools, createWorkerPool } = useMultithreading();

  // Predefined quick actions with Katalyst integration
  const quickActions = [
    {
      icon: Search,
      label: 'Find Products',
      action: 'product_search',
      prompt: 'Help me find products in your catalog'
    },
    {
      icon: ShoppingBag,
      label: 'Order Status',
      action: 'order_inquiry',
      prompt: 'Check the status of my recent orders'
    },
    {
      icon: Code,
      label: 'Technical Support',
      action: 'tech_support',
      prompt: 'I need help with technical integration'
    },
    {
      icon: Settings,
      label: 'Account Help',
      action: 'account_support',
      prompt: 'Help me with my account settings'
    }
  ];

  // Initialize chatbot with welcome message
  useEffect(() => {
    if (isOpen && messages.length === 0) {
      const welcomeMessage: ChatMessage = {
        id: `msg-${Date.now()}`,
        type: 'assistant',
        content: `Hello! I'm your Katalyst AI assistant, powered by advanced multithreading and AI technology. I can help you with:

‚Ä¢ **Product Search** - Find exactly what you're looking for
‚Ä¢ **Technical Support** - Get help with integrations and setup
‚Ä¢ **Order Management** - Check status, track shipments, handle returns
‚Ä¢ **Performance Insights** - Real-time system analytics and optimization

How can I assist you today?`,
        timestamp: new Date(),
        metadata: {
          model: '@cf/meta/llama-3.1-8b-instruct',
          confidence: 0.95,
          suggestions: [
            'Show me your latest AI development tools',
            'Help me optimize my application performance',
            'What multithreading capabilities do you offer?'
          ]
        }
      };

      setMessages([welcomeMessage]);
    }

    if (initialMessage && isOpen) {
      handleSendMessage(initialMessage);
    }
  }, [isOpen, initialMessage]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Focus input when opened
  useEffect(() => {
    if (isOpen && !isMinimized) {
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [isOpen, isMinimized]);

  // AI-powered message processing with multithreading
  const processAIMessage = useCallback(async (userMessage: string, messageId: string): Promise<string> => {
    try {
      setIsTyping(true);

      // Create dedicated AI processing thread pool for complex queries
      const aiProcessingPool = await createThreadPool({
        type: 'tokio',
        workerCount: 2,
        priority: 'high'
      });

      // Execute AI processing with context awareness
      const aiResponse = await executeTask({
        id: `ai-chat-${messageId}`,
        operation: 'process_ai_chat_message',
        data: {
          userMessage,
          context: chatContext,
          conversationHistory: messages.slice(-10), // Last 10 messages for context
          systemPrompt: `You are Katalyst AI, an advanced assistant for a multithreaded, AI-powered e-commerce platform.

Key Capabilities:
- Real-time multithreaded processing
- AI-powered product recommendations
- Performance optimization insights
- Technical support for developers
- Cloudflare AI model integration

Personality: ${chatContext.userPreferences.aiPersonality}
Current Page: ${chatContext.currentPage || 'unknown'}

Provide helpful, accurate responses that showcase Katalyst's advanced capabilities.`,
          modelConfig: {
            model: '@cf/meta/llama-3.1-8b-instruct',
            maxTokens: 1024,
            temperature: 0.7,
            topP: 0.9
          },
          processingOptions: {
            useMultithreading: true,
            enableContextAnalysis: true,
            includeRecommendations: true,
            threadPoolId: aiProcessingPool.id
          }
        },
        priority: 'high',
        status: 'pending'
      });

      // Process AI response and generate suggestions
      const response = aiResponse?.content || `I apologize, but I'm having trouble processing your request right now. As your Katalyst AI assistant, I'm designed to help with:

‚Ä¢ Product searches and recommendations
‚Ä¢ Technical support and troubleshooting  
‚Ä¢ Order management and tracking
‚Ä¢ Performance optimization insights

Please try rephrasing your question, or select one of the quick actions below to get started.`;

      // Generate contextual suggestions using AI
      let suggestions: string[] = [];
      if (isAIEnabled) {
        try {
          const suggestionContext = {
            userMessage,
            aiResponse: response,
            conversationHistory: messages.slice(-5)
          };

          const aiSuggestions = await generateRecommendations(suggestionContext, {
            type: 'conversation_suggestions',
            limit: 3
          });

          suggestions = aiSuggestions || [];
        } catch (error) {
          console.warn('Failed to generate suggestions:', error);
        }
      }

      // Update message with metadata
      setMessages(prev => prev.map(msg => 
        msg.id === messageId 
          ? {
              ...msg,
              content: response,
              status: 'sent',
              metadata: {
                ...msg.metadata,
                processingTime: Date.now() - msg.timestamp.getTime(),
                threadPoolId: aiProcessingPool.id,
                model: '@cf/meta/llama-3.1-8b-instruct',
                confidence: 0.85,
                suggestions: suggestions.length > 0 ? suggestions : [
                  'Tell me more about this',
                  'Show me related products',
                  'How can I get started?'
                ]
              }
            }
          : msg
      ));

      return response;

    } catch (error) {
      console.error('AI message processing failed:', error);

      // Update message with error state
      setMessages(prev => prev.map(msg => 
        msg.id === messageId 
          ? { ...msg, status: 'error', content: 'I apologize, but I encountered an error processing your message. Please try again.' }
          : msg
      ));

      return 'I apologize, but I encountered an error processing your message. Please try again.';
    } finally {
      setIsTyping(false);
    }
  }, [executeTask, createThreadPool, chatContext, messages, isAIEnabled, generateRecommendations]);

  // Handle sending messages
  const handleSendMessage = useCallback(async (message?: string) => {
    const messageText = message || inputValue.trim();
    if (!messageText) return;

    // Clear input
    setInputValue('');

    // Add user message
    const userMessage: ChatMessage = {
      id: `user-msg-${Date.now()}`,
      type: 'user',
      content: messageText,
      timestamp: new Date(),
      status: 'sent'
    };

    // Add AI response placeholder
    const aiMessageId = `ai-msg-${Date.now()}`;
    const aiMessage: ChatMessage = {
      id: aiMessageId,
      type: 'assistant',
      content: '',
      timestamp: new Date(),
      status: 'sending'
    };

    setMessages(prev => [...prev, userMessage, aiMessage]);

    // Process AI response
    await processAIMessage(messageText, aiMessageId);

    // Update conversation context
    setChatContext(prev => ({
      ...prev,
      conversationHistory: [...prev.conversationHistory, userMessage]
    }));

  }, [inputValue, processAIMessage]);

  // Handle quick action clicks
  const handleQuickAction = useCallback((action: typeof quickActions[0]) => {
    handleSendMessage(action.prompt);
  }, [handleSendMessage]);

  // Handle suggestion clicks
  const handleSuggestionClick = useCallback((suggestion: string) => {
    handleSendMessage(suggestion);
  }, [handleSendMessage]);

  // Copy message content
  const copyMessage = useCallback((content: string) => {
    navigator.clipboard.writeText(content);
  }, []);

  // Clear conversation
  const clearConversation = useCallback(() => {
    setMessages([]);
    setChatContext(prev => ({ ...prev, conversationHistory: [] }));
  }, []);

  if (!isOpen) return null;

  return (
    <motion.div
      initial={{ opacity: 0, y: 100, scale: 0.9 }}
      animate={{ 
        opacity: 1, 
        y: 0, 
        scale: 1,
        height: isMinimized ? 'auto' : '600px'
      }}
      exit={{ opacity: 0, y: 100, scale: 0.9 }}
      transition={{ type: 'spring', stiffness: 300, damping: 30 }}
      className="fixed bottom-4 right-4 z-50 w-96 max-w-[calc(100vw-2rem)]"
    >
      <Card className="shadow-2xl border-primary/20">
        {/* Chat Header */}
        <CardHeader className="pb-3 bg-gradient-to-r from-primary to-secondary text-white">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="relative">
                <Avatar className="h-8 w-8 bg-white/20">
                  <AvatarFallback className="bg-transparent text-white">
                    <Bot className="h-5 w-5" />
                  </AvatarFallback>
                </Avatar>
                <div className="absolute -bottom-1 -right-1">
                  <div className="w-3 h-3 bg-green-400 rounded-full border-2 border-white animate-pulse" />
                </div>
              </div>

              <div>
                <CardTitle className="text-sm font-semibold text-white">
                  Katalyst AI Assistant
                </CardTitle>
                <div className="flex items-center space-x-2 text-xs text-white/80">
                  {isAIEnabled && (
                    <>
                      <Brain className="w-3 h-3" />
                      <span>AI-Powered</span>
                    </>
                  )}
                  <Cpu className="w-3 h-3" />
                  <span>{threadPools.length} pools</span>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-1">
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setIsMinimized(!isMinimized)}
                className="h-8 w-8 p-0 text-white hover:bg-white/20"
              >
                {isMinimized ? <Maximize2 className="h-4 w-4" /> : <Minimize2 className="h-4 w-4" />}
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={clearConversation}
                className="h-8 w-8 p-0 text-white hover:bg-white/20"
              >
                <RotateCcw className="h-4 w-4" />
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={onClose}
                className="h-8 w-8 p-0 text-white hover:bg-white/20"
              >
                <X className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </CardHeader>

        {!isMinimized && (
          <CardContent className="p-0">
            {/* Messages Area */}
            <ScrollArea className="h-80 p-4">
              <div className="space-y-4">
                {messages.map((message) => (
                  <motion.div
                    key={message.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`max-w-[80%] ${message.type === 'user' ? 'order-2' : 'order-1'}`}>
                      <div className="flex items-start space-x-2">
                        {message.type === 'assistant' && (
                          <Avatar className="h-6 w-6 mt-1">
                            <AvatarFallback className="bg-primary text-primary-foreground">
                              <Bot className="h-4 w-4" />
                            </AvatarFallback>
                          </Avatar>
                        )}

                        <div className={`rounded-lg px-3 py-2 ${
                          message.type === 'user' 
                            ? 'bg-primary text-primary-foreground' 
                            : 'bg-muted'
                        }`}>
                          {message.status === 'sending' ? (
                            <div className="flex items-center space-x-2">
                              <Loader2 className="h-4 w-4 animate-spin" />
                              <span className="text-sm">Thinking...</span>
                            </div>
                          ) : (
                            <div className="text-sm whitespace-pre-wrap">{message.content}</div>
                          )}

                          {/* Message Metadata */}
                          {message.metadata && message.type === 'assistant' && message.status === 'sent' && (
                            <div className="mt-2 space-y-2">
                              {/* Processing Info */}
                              <div className="flex items-center space-x-2 text-xs text-muted-foreground">
                                {message.metadata.processingTime && (
                                  <Badge variant="outline" className="text-xs">
                                    <Zap className="w-3 h-3 mr-1" />
                                    {message.metadata.processingTime}ms
                                  </Badge>
                                )}
                                {message.metadata.model && (
                                  <Badge variant="outline" className="text-xs">
                                    <Brain className="w-3 h-3 mr-1" />
                                    AI
                                  </Badge>
                                )}
                              </div>

                              {/* Suggestions */}
                              {message.metadata.suggestions && message.metadata.suggestions.length > 0 && (
                                <div className="space-y-1">
                                  <div className="text-xs text-muted-foreground">Quick follow-ups:</div>
                                  <div className="flex flex-wrap gap-1">
                                    {message.metadata.suggestions.map((suggestion, index) => (
                                      <Button
                                        key={index}
                                        variant="outline"
                                        size="sm"
                                        onClick={() => handleSuggestionClick(suggestion)}
                                        className="h-6 text-xs"
                                      >
                                        {suggestion}
                                      </Button>
                                    ))}
                                  </div>
                                </div>
                              )}
                            </div>
                          )}
                        </div>

                        {/* Message Actions */}
                        {message.type === 'assistant' && message.status === 'sent' && (
                          <div className="flex flex-col space-y-1">
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => copyMessage(message.content)}
                              className="h-6 w-6 p-0"
                            >
                              <Copy className="h-3 w-3" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              className="h-6 w-6 p-0"
                            >
                              <ThumbsUp className="h-3 w-3" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              className="h-6 w-6 p-0"
                            >
                              <ThumbsDown className="h-3 w-3" />
                            </Button>
                          </div>
                        )}
                      </div>
                    </div>
                  </motion.div>
                ))}

                {/* Typing Indicator */}
                {isTyping && (
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="flex items-start space-x-2"
                  >
                    <Avatar className="h-6 w-6">
                      <AvatarFallback className="bg-primary text-primary-foreground">
                        <Bot className="h-4 w-4" />
                      </AvatarFallback>
                    </Avatar>
                    <div className="bg-muted rounded-lg px-3 py-2">
                      <div className="flex space-x-1">
                        <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
                        <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
                        <div className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
                      </div>
                    </div>
                  </motion.div>
                )}

                <div ref={messagesEndRef} />
              </div>
            </ScrollArea>

            {/* Quick Actions */}
            {messages.length <= 1 && (
              <div className="p-4 border-t">
                <div className="text-xs text-muted-foreground mb-2">Quick actions:</div>
                <div className="grid grid-cols-2 gap-2">
                  {quickActions.map((action) => (
                    <Button
                      key={action.action}
                      variant="outline"
                      size="sm"
                      onClick={() => handleQuickAction(action)}
                      className="flex items-center space-x-2 h-8 text-xs"
                    >
                      <action.icon className="h-3 w-3" />
                      <span>{action.label}</span>
                    </Button>
                  ))}
                </div>
              </div>
            )}

            {/* Input Area */}
            <div className="p-4 border-t">
              <div className="flex space-x-2">
                <Input
                  ref={inputRef}
                  placeholder="Ask me anything about Katalyst..."
                  value={inputValue}
                  onChange={(e) => setInputValue(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage()}
                  className="flex-1"
                />
                <Button 
                  onClick={() => handleSendMessage()}
                  disabled={!inputValue.trim() || isTyping}
                  size="sm"
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>

              {/* Status Indicator */}
              <div className="flex items-center justify-between mt-2 text-xs text-muted-foreground">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full" />
                  <span>Katalyst AI Online</span>
                  {isAIEnabled && (
                    <Badge variant="outline" className="text-xs">
                      <Sparkles className="w-3 h-3 mr-1" />
                      Enhanced
                    </Badge>
                  )}
                </div>
                <div>{messages.length} messages</div>
              </div>
            </div>
          </CardContent>
        )}
      </Card>
    </motion.div>
  );
}

